import React, { useContext, useState } from "react";
import { NavLink, useNavigate } from "react-router-dom";
import { AuthContext } from "../context/AuthContext";

// Access Heroicons from the global window object (loaded via CDN)
const {
  HomeIcon,
  UserIcon,
  ArrowRightOnRectangleIcon,
  UserPlusIcon,
  ArrowLeftOnRectangleIcon,
} = window.HeroiconsReactOutline || {};

function Navbar() {
  const { user, logout } = useContext(AuthContext);
  const navigate = useNavigate();
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);

  const handleLogout = () => {
    logout();
    navigate("/");
    setIsMobileMenuOpen(false);
  };

  const toggleMobileMenu = () => {
    setIsMobileMenuOpen((prev) => !prev);
  };

  return (
    <nav
      className="bg-gradient-to-r from-blue-600 to-blue-700 p-4 shadow-lg sticky top-0 z-50"
      role="navigation"
    >
      <div className="container mx-auto flex justify-between items-center">
        {/* Logo */}
        <NavLink
          to="/"
          className="text-white text-2xl font-bold transform hover:scale-105 transition-all duration-200"
          aria-label="NexusEd Home"
        >
          NexusEd
        </NavLink>

        {/* Hamburger Icon for Mobile */}
        <button
          className="md:hidden text-white focus:outline-none focus:ring-2 focus:ring-blue-200 rounded-md"
          onClick={toggleMobileMenu}
          aria-expanded={isMobileMenuOpen}
          aria-controls="mobile-menu"
          aria-label="Toggle navigation menu"
        >
          <svg
            className="w-6 h-6"
            fill="none"
            stroke="currentColor"
            viewBox="0 0 24 24"
            xmlns="http://www.w3.org/2000/svg"
          >
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth="2"
              d={
                isMobileMenuOpen
                  ? "M6 18L18 6M6 6l12 12"
                  : "M4 6h16M4 12h16M4 18h16"
              }
            />
          </svg>
        </button>

        {/* Desktop Menu with Icons */}
        <div className="hidden md:flex md:space-x-6">
          <NavLink
            to="/"
            className={({ isActive }) =>
              `relative group text-white p-2 rounded-md transition-all duration-200 ${
                isActive
                  ? "bg-blue-500 text-white"
                  : "hover:text-blue-200 after:absolute after:bottom-0 after:left-0 after:w-0 after:h-0.5 after:bg-blue-200 after:transition-all after:duration-300 hover:after:w-full"
              }`
            }
            aria-label="Home"
            title="Home"
          >
            <HomeIcon className="w-6 h-6" />
            {/* Tooltip */}
            <span className="absolute invisible group-hover:visible group-focus:visible opacity-0 group-hover:opacity-100 group-focus:opacity-100 transition-opacity duration-200 bg-gray-800 text-white text-xs rounded py-1 px-2 bottom-[-2rem] left-1/2 transform -translate-x-1/2">
              Home
            </span>
          </NavLink>
          {user ? (
            <>
              <NavLink
                to="/profile"
                className={({ isActive }) =>
                  `relative group text-white p-2 rounded-md transition-all duration-200 ${
                    isActive
                      ? "bg-blue-500 text-white"
                      : "hover:text-blue-200 after:absolute after:bottom-0 after:left-0 after:w-0 after:h-0.5 after:bg-blue-200 after:transition-all after:duration-300 hover:after:w-full"
                  }`
                }
                aria-label="Profile"
                title="Profile"
              >
                <UserIcon className="w-6 h-6" />
                <span className="absolute invisible group-hover:visible group-focus:visible opacity-0 group-hover:opacity-100 group-focus:opacity-100 transition-opacity duration-200 bg-gray-800 text-white text-xs rounded py-1 px-2 bottom-[-2rem] left-1/2 transform -translate-x-1/2">
                  Profile
                </span>
              </NavLink>
              <button
                type="button"
                onClick={handleLogout}
                className="relative group text-white p-2 rounded-md hover:text-blue-200 transition-all duration-200 focus:outline-none focus:ring-2 focus:ring-blue-200 focus:ring-offset-2 after:absolute after:bottom-0 after:left-0 after:w-0 after:h-0.5 after:bg-blue-200 after:transition-all after:duration-300 hover:after:w-full"
                aria-label="Logout"
                title="Logout"
              >
                <ArrowLeftOnRectangleIcon className="w-6 h-6" />
                <span className="absolute invisible group-hover:visible group-focus:visible opacity-0 group-hover:opacity-100 group-focus:opacity-100 transition-opacity duration-200 bg-gray-800 text-white text-xs rounded py-1 px-2 bottom-[-2rem] left-1/2 transform -translate-x-1/2">
                  Logout
                </span>
              </button>
            </>
          ) : (
            <>
              <NavLink
                to="/login"
                className={({ isActive }) =>
                  `relative group text-white p-2 rounded-md transition-all duration-200 ${
                    isActive
                      ? "bg-blue-500 text-white"
                      : "hover:text-blue-200 after:absolute after:bottom-0 after:left-0 after:w-0 after:h-0.5 after:bg-blue-200 after:transition-all after:duration-300 hover:after:w-full"
                  }`
                }
                aria-label="Login"
                title="Login"
              >
                <ArrowRightOnRectangleIcon className="w-6 h-6" />
                <span className="absolute invisible group-hover:visible group-focus:visible opacity-0 group-hover:opacity-100 group-focus:opacity-100 transition-opacity duration-200 bg-gray-800 text-white text-xs rounded py-1 px-2 bottom-[-2rem] left-1/2 transform -translate-x-1/2">
                  Login
                </span>
              </NavLink>
              <NavLink
                to="/register"
                className={({ isActive }) =>
                  `relative group text-white p-2 rounded-md transition-all duration-200 ${
                    isActive
                      ? "bg-blue-500 text-white"
                      : "hover:text-blue-200 after:absolute after:bottom-0 after:left-0 after:w-0 after:h-0.5 after:bg-blue-200 after:transition-all after:duration-300 hover:after:w-full"
                  }`
                }
                aria-label="Register"
                title="Register"
              >
                <UserPlusIcon className="w-6 h-6" />
                <span className="absolute invisible group-hover:visible group-focus:visible opacity-0 group-hover:opacity-100 group-focus:opacity-100 transition-opacity duration-200 bg-gray-800 text-white text-xs rounded py-1 px-2 bottom-[-2rem] left-1/2 transform -translate-x-1/2">
                  Register
                </span>
              </NavLink>
            </>
          )}
        </div>
      </div>

      {/* Mobile Menu with Text and Icons */}
      {isMobileMenuOpen && (
        <div id="mobile-menu" className="md:hidden mt-4 space-y-3">
          <NavLink
            to="/"
            className={({ isActive }) =>
              `flex items-center space-x-3 text-white px-4 py-2 rounded-md transition-all duration-200 ${
                isActive
                  ? "bg-blue-500 text-white"
                  : "hover:bg-blue-500 hover:text-white"
              }`
            }
            onClick={() => setIsMobileMenuOpen(false)}
            aria-label="Home"
          >
            <HomeIcon className="w-5 h-5" />
            <span>Home</span>
          </NavLink>
          {user ? (
            <>
              <NavLink
                to="/profile"
                className={({ isActive }) =>
                  `flex items-center space-x-3 text-white px-4 py-2 rounded-md transition-all duration-200 ${
                    isActive
                      ? "bg-blue-500 text-white"
                      : "hover:bg-blue-500 hover:text-white"
                  }`
                }
                onClick={() => setIsMobileMenuOpen(false)}
                aria-label="Profile"
              >
                <UserIcon className="w-5 h-5" />
                <span>Profile</span>
              </NavLink>
              <button
                type="button"
                onClick={handleLogout}
                className="flex items-center space-x-3 w-full text-left text-white px-4 py-2 rounded-md bg-blue-500 hover:bg-blue-600 transition-all duration-200 focus:outline-none focus:ring-2 focus:ring-blue-200 focus:ring-offset-2"
                aria-label="Logout"
              >
                <ArrowLeftOnRectangleIcon className="w-5 h-5" />
                <span>Logout</span>
              </button>
            </>
          ) : (
            <>
              <NavLink
                to="/login"
                className={({ isActive }) =>
                  `flex items-center space-x-3 text-white px-4 py-2 rounded-md transition-all duration-200 ${
                    isActive
                      ? "bg-blue-500 text-white"
                      : "hover:bg-blue-500 hover:text-white"
                  }`
                }
                onClick={() => setIsMobileMenuOpen(false)}
                aria-label="Login"
              >
                <ArrowRightOnRectangleIcon className="w-5 h-5" />
                <span>Login</span>
              </NavLink>
              <NavLink
                to="/register"
                className={({ isActive }) =>
                  `flex items-center space-x-3 text-white px-4 py-2 rounded-md transition-all duration-200 ${
                    isActive
                      ? "bg-blue-500 text-white"
                      : "hover:bg-blue-500 hover:text-white"
                  }`
                }
                onClick={() => setIsMobileMenuOpen(false)}
                aria-label="Register"
              >
                <UserPlusIcon className="w-5 h-5" />
                <span>Register</span>
              </NavLink>
            </>
          )}
        </div>
      )}
    </nav>
  );
}

export default Navbar;
