import React, { useState, useContext, useRef, useEffect } from "react";
import {
  Card,
  Form,
  Button,
  InputGroup,
  Row,
  Col,
  Alert,
  ProgressBar,
} from "react-bootstrap";
import { Link, useNavigate } from "react-router-dom";
import { AuthContext } from "../context/AuthContext";

function Register() {
  const { register } = useContext(AuthContext);
  const navigate = useNavigate();

  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [showPassword, setShowPassword] = useState(false);
  const [error, setError] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const [passwordStrength, setPasswordStrength] = useState({
    score: 0,
    feedback: "",
    color: "danger",
    criteria: {
      length: false,
      uppercase: false,
      lowercase: false,
      number: false,
      special: false,
    },
  });
  const [debugInfo, setDebugInfo] = useState({ logs: [] });

  const usernameRef = useRef(null);
  const emailRef = useRef(null);
  const passwordRef = useRef(null);
  const confirmPasswordRef = useRef(null);

  // Clear errors after 5 seconds
  useEffect(() => {
    if (error) {
      const timer = setTimeout(() => {
        setError("");
      }, 5000);
      return () => clearTimeout(timer);
    }
  }, [error]);

  // Password strength calculation
  useEffect(() => {
    if (password) {
      const strength = calculatePasswordStrength(password);
      setPasswordStrength(strength);
    } else {
      setPasswordStrength({
        score: 0,
        feedback: "",
        color: "danger",
        criteria: {
          length: false,
          uppercase: false,
          lowercase: false,
          number: false,
          special: false,
        },
      });
    }
  }, [password]);

  const calculatePasswordStrength = (pwd) => {
    const criteria = {
      length: pwd.length >= 8,
      uppercase: /[A-Z]/.test(pwd),
      lowercase: /[a-z]/.test(pwd),
      number: /\d/.test(pwd),
      special: /[!@#$%^&*()_+\-=\[\]{};':"\\|,.<>\/?]/.test(pwd),
    };

    const score = Object.values(criteria).filter(Boolean).length;

    let feedback, color;

    if (score === 0) {
      feedback = "Enter a password";
      color = "secondary";
    } else if (score <= 2) {
      feedback = "Very weak password";
      color = "danger";
    } else if (score === 3) {
      feedback = "Weak password";
      color = "warning";
    } else if (score === 4) {
      feedback = "Good password";
      color = "info";
    } else {
      feedback = "Strong password";
      color = "success";
    }

    return { score: (score / 5) * 100, feedback, color, criteria };
  };

  // Focus management for navigation
  const handleKeyDown = (e, currentField) => {
    if (e.key === "Enter") {
      e.preventDefault();

      switch (currentField) {
        case "username":
          if (email === "") {
            emailRef.current?.focus();
          } else if (password === "") {
            passwordRef.current?.focus();
          } else if (confirmPassword === "") {
            confirmPasswordRef.current?.focus();
          } else {
            handleSubmit();
          }
          break;
        case "email":
          if (password === "") {
            passwordRef.current?.focus();
          } else if (confirmPassword === "") {
            confirmPasswordRef.current?.focus();
          } else {
            handleSubmit();
          }
          break;
        case "password":
          if (confirmPassword === "") {
            confirmPasswordRef.current?.focus();
          } else {
            handleSubmit();
          }
          break;
        case "confirmPassword":
          handleSubmit();
          break;
        default:
          break;
      }
    }
  };

  const addDebugLog = (message) => {
    console.log(`📝 Register Debug: ${message}`);
    setDebugInfo((prev) => ({
      logs: [
        ...prev.logs.slice(-4),
        `${new Date().toLocaleTimeString()}: ${message}`,
      ],
    }));
  };

  const validateForm = () => {
    if (!username.trim() || !email.trim() || !password || !confirmPassword) {
      setError("All fields are required.");
      return false;
    }

    if (password !== confirmPassword) {
      setError("Passwords do not match.");
      return false;
    }

    if (password.length < 8) {
      setError("Password must be at least 8 characters long.");
      return false;
    }

    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(email)) {
      setError("Please enter a valid email address.");
      return false;
    }

    if (username.length < 3) {
      setError("Username must be at least 3 characters long.");
      return false;
    }

    if (!/^[a-zA-Z0-9_]+$/.test(username)) {
      setError("Username can only contain letters, numbers, and underscores.");
      return false;
    }

    return true;
  };

  const handleSubmit = async () => {
    addDebugLog("Registration form submission started");

    if (!validateForm()) {
      addDebugLog("Form validation failed");
      return;
    }

    try {
      setIsLoading(true);
      setError("");
      addDebugLog(
        `Attempting registration for: ${username} (${email.toLowerCase()})`
      );

      // Convert email to lowercase for consistency
      const lowerEmail = email.toLowerCase().trim();

      const result = await register(username.trim(), lowerEmail, password);

      if (result && result.success) {
        addDebugLog("Registration successful, redirecting...");
        // Add a small delay to show success state
        setTimeout(() => {
          navigate("/");
        }, 500);
      } else {
        addDebugLog(
          "Registration failed with result: " + JSON.stringify(result)
        );
        setError(result?.message || "Registration failed. Please try again.");
        setIsLoading(false);
      }
    } catch (err) {
      addDebugLog("Registration error caught: " + err.message);
      console.error("Registration error:", err);
      setError(
        err.response?.data?.message ||
          err.message ||
          "Registration failed. Please try again."
      );
      setIsLoading(false);
    }
  };

  return (
    <Row className="justify-content-center">
      <Col md={6} lg={5}>
        <Card className="shadow animate-fade-in-up">
          <Card.Body className="p-4">
            <div className="text-center mb-4">
              <i className="fas fa-user-plus fa-3x text-primary mb-3"></i>
              <h2 className="fw-bold">Create an Account</h2>
              <p className="text-muted">Join the NexusEd student community</p>
            </div>

            {error && (
              <Alert variant="danger" className="mb-4 fade-message">
                <i className="fas fa-exclamation-circle me-2"></i>
                {error}
              </Alert>
            )}

            <Form>
              <Form.Group className="mb-3">
                <Form.Label>Username</Form.Label>
                <InputGroup>
                  <InputGroup.Text>
                    <i className="fas fa-user"></i>
                  </InputGroup.Text>
                  <Form.Control
                    type="text"
                    ref={usernameRef}
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                    onKeyDown={(e) => handleKeyDown(e, "username")}
                    placeholder="Choose a username"
                    autoComplete="username"
                    disabled={isLoading}
                    required
                  />
                </InputGroup>
                <Form.Text className="text-muted">
                  3+ characters, letters, numbers, and underscores only
                </Form.Text>
              </Form.Group>

              <Form.Group className="mb-3">
                <Form.Label>Email</Form.Label>
                <InputGroup>
                  <InputGroup.Text>
                    <i className="fas fa-envelope"></i>
                  </InputGroup.Text>
                  <Form.Control
                    type="email"
                    ref={emailRef}
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    onKeyDown={(e) => handleKeyDown(e, "email")}
                    placeholder="Enter your email"
                    autoComplete="email"
                    disabled={isLoading}
                    required
                  />
                </InputGroup>
                <Form.Text className="text-muted">
                  Will be converted to lowercase automatically
                </Form.Text>
              </Form.Group>

              <Form.Group className="mb-3">
                <Form.Label>Password</Form.Label>
                <InputGroup>
                  <InputGroup.Text>
                    <i className="fas fa-lock"></i>
                  </InputGroup.Text>
                  <Form.Control
                    type={showPassword ? "text" : "password"}
                    ref={passwordRef}
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    onKeyDown={(e) => handleKeyDown(e, "password")}
                    placeholder="Create a password"
                    autoComplete="new-password"
                    disabled={isLoading}
                    required
                  />
                  <Button
                    variant="outline-secondary"
                    onClick={() => setShowPassword(!showPassword)}
                    disabled={isLoading}
                    aria-label={
                      showPassword ? "Hide password" : "Show password"
                    }
                  >
                    <i
                      className={`fa-solid ${
                        showPassword ? "fa-eye-slash" : "fa-eye"
                      }`}
                    ></i>
                  </Button>
                </InputGroup>

                {/* Password Strength Meter */}
                {password && (
                  <div className="mt-2">
                    <div className="d-flex justify-content-between align-items-center mb-1">
                      <small className="text-muted">Password Strength:</small>
                      <small className={`text-${passwordStrength.color}`}>
                        {passwordStrength.feedback}
                      </small>
                    </div>
                    <ProgressBar
                      variant={passwordStrength.color}
                      now={passwordStrength.score}
                      style={{ height: "6px" }}
                    />

                    {/* Password Criteria */}
                    <div className="mt-2">
                      <small className="text-muted d-block mb-1">
                        Requirements:
                      </small>
                      <div className="row">
                        <div className="col-6">
                          <small
                            className={
                              passwordStrength.criteria.length
                                ? "text-success"
                                : "text-muted"
                            }
                          >
                            <i
                              className={`fas ${
                                passwordStrength.criteria.length
                                  ? "fa-check"
                                  : "fa-times"
                              } me-1`}
                            ></i>
                            8+ characters
                          </small>
                        </div>
                        <div className="col-6">
                          <small
                            className={
                              passwordStrength.criteria.uppercase
                                ? "text-success"
                                : "text-muted"
                            }
                          >
                            <i
                              className={`fas ${
                                passwordStrength.criteria.uppercase
                                  ? "fa-check"
                                  : "fa-times"
                              } me-1`}
                            ></i>
                            Uppercase
                          </small>
                        </div>
                        <div className="col-6">
                          <small
                            className={
                              passwordStrength.criteria.lowercase
                                ? "text-success"
                                : "text-muted"
                            }
                          >
                            <i
                              className={`fas ${
                                passwordStrength.criteria.lowercase
                                  ? "fa-check"
                                  : "fa-times"
                              } me-1`}
                            ></i>
                            Lowercase
                          </small>
                        </div>
                        <div className="col-6">
                          <small
                            className={
                              passwordStrength.criteria.number
                                ? "text-success"
                                : "text-muted"
                            }
                          >
                            <i
                              className={`fas ${
                                passwordStrength.criteria.number
                                  ? "fa-check"
                                  : "fa-times"
                              } me-1`}
                            ></i>
                            Number
                          </small>
                        </div>
                        <div className="col-12">
                          <small
                            className={
                              passwordStrength.criteria.special
                                ? "text-success"
                                : "text-muted"
                            }
                          >
                            <i
                              className={`fas ${
                                passwordStrength.criteria.special
                                  ? "fa-check"
                                  : "fa-times"
                              } me-1`}
                            ></i>
                            Special character (!@#$%^&*...)
                          </small>
                        </div>
                      </div>
                    </div>
                  </div>
                )}
              </Form.Group>

              <Form.Group className="mb-4">
                <Form.Label>Confirm Password</Form.Label>
                <InputGroup>
                  <InputGroup.Text>
                    <i className="fas fa-lock"></i>
                  </InputGroup.Text>
                  <Form.Control
                    type={showPassword ? "text" : "password"}
                    ref={confirmPasswordRef}
                    value={confirmPassword}
                    onChange={(e) => setConfirmPassword(e.target.value)}
                    onKeyDown={(e) => handleKeyDown(e, "confirmPassword")}
                    placeholder="Confirm your password"
                    autoComplete="new-password"
                    disabled={isLoading}
                    required
                  />
                </InputGroup>
                {confirmPassword && password !== confirmPassword && (
                  <small className="text-danger">
                    <i className="fas fa-times me-1"></i>
                    Passwords do not match
                  </small>
                )}
                {confirmPassword && password === confirmPassword && (
                  <small className="text-success">
                    <i className="fas fa-check me-1"></i>
                    Passwords match
                  </small>
                )}
              </Form.Group>

              <div className="d-grid">
                <Button
                  variant="primary"
                  onClick={handleSubmit}
                  className="btn-hover-shadow"
                  disabled={isLoading}
                >
                  {isLoading ? (
                    <>
                      <span
                        className="spinner-border spinner-border-sm me-2"
                        role="status"
                        aria-hidden="true"
                      ></span>
                      Registering...
                    </>
                  ) : (
                    <>
                      <i className="fas fa-user-plus me-2"></i>
                      Register
                    </>
                  )}
                </Button>
              </div>
            </Form>

            <div className="text-center mt-4">
              <p>
                Already have an account?{" "}
                <Link
                  to="/login"
                  className="text-primary fw-semibold transition-all hover-underline-effect"
                >
                  Login here
                </Link>
              </p>
            </div>

            {/* Debug Information (only show in development) */}
            {process.env.NODE_ENV === "development" &&
              debugInfo.logs.length > 0 && (
                <div className="mt-3 p-2 bg-light rounded">
                  <small className="text-muted">
                    <strong>Debug Info:</strong>
                    {debugInfo.logs.map((log, index) => (
                      <div key={index} style={{ fontSize: "11px" }}>
                        {log}
                      </div>
                    ))}
                  </small>
                </div>
              )}
          </Card.Body>
        </Card>
      </Col>
    </Row>
  );
}

export default Register;
