import React, { useState } from "react";

function PostCard({
  title,
  content,
  author,
  id,
  setPosts,
  currentUser,
  comments,
}) {
  const [commentContent, setCommentContent] = useState("");
  const [error, setError] = useState("");

  const handleDelete = async () => {
    try {
      const response = await fetch(
        `${process.env.REACT_APP_API_URL}/api/posts/${id}`,
        {
          method: "DELETE",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${localStorage.getItem("token")}`,
          },
        }
      );
      if (!response.ok) {
        throw new Error("Failed to delete post");
      }
      setPosts((prevPosts) => prevPosts.filter((post) => post._id !== id));
    } catch (err) {
      console.error("Error deleting post:", err);
      setError(err.message || "Failed to delete post");
    }
  };

  const handleCommentSubmit = async (e) => {
    e.preventDefault();
    setError("");

    if (!commentContent.trim()) {
      setError("Comment cannot be empty");
      return;
    }

    try {
      const response = await fetch(
        `${process.env.REACT_APP_API_URL}/api/posts/${id}/comments`,
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${localStorage.getItem("token")}`,
          },
          body: JSON.stringify({ content: commentContent }),
        }
      );

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || "Failed to add comment");
      }

      const updatedPost = await response.json();
      setPosts((prevPosts) =>
        prevPosts.map((post) => (post._id === id ? updatedPost : post))
      );
      setCommentContent("");
    } catch (err) {
      console.error("Error adding comment:", err);
      setError(err.message || "Failed to add comment");
    }
  };

  return (
    <div className="bg-white p-6 rounded-lg shadow-md mb-4">
      <h2 className="text-xl font-semibold text-gray-800">{title}</h2>
      <p className="text-gray-600 mt-2">{content}</p>
      <p className="text-gray-500 text-sm mt-2">Posted by {author}</p>
      {currentUser && currentUser.id === id && (
        <button
          onClick={handleDelete}
          className="mt-2 text-red-600 hover:text-red-800 text-sm"
        >
          Delete
        </button>
      )}
      {error && (
        <div className="bg-red-100 text-red-700 p-2 mt-2 rounded-md">
          {error}
        </div>
      )}
      <div className="mt-4">
        <h4 className="text-lg font-semibold text-gray-800">Comments</h4>
        {comments && comments.length > 0 ? (
          comments.map((comment) => (
            <div key={comment._id} className="bg-gray-100 p-2 mt-2 rounded-md">
              <p className="text-gray-600">{comment.content}</p>
              <p className="text-gray-500 text-sm">
                By {comment.author.username} on{" "}
                {new Date(comment.createdAt).toLocaleDateString()}
              </p>
            </div>
          ))
        ) : (
          <p className="text-gray-600 text-sm">No comments yet.</p>
        )}
        {currentUser && (
          <form onSubmit={handleCommentSubmit} className="mt-4">
            <textarea
              value={commentContent}
              onChange={(e) => setCommentContent(e.target.value)}
              placeholder="Add a comment"
              className="w-full p-2 mb-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-blue-600"
              rows="2"
            />
            <button
              type="submit"
              className="bg-blue-600 text-white px-4 py-2 rounded-md hover:bg-blue-700"
            >
              Comment
            </button>
          </form>
        )}
      </div>
    </div>
  );
}

export default PostCard;
