import React, { useState, useEffect, useContext, useRef } from "react";
import PostForm from "./PostForm";
import PostCard from "./PostCard";
import { AuthContext } from "../context/AuthContext";

function Home() {
  const { user } = useContext(AuthContext);
  const [posts, setPosts] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState("");
  const timeoutRef = useRef(null);

  const setTemporaryError = (message) => {
    if (timeoutRef.current) {
      clearTimeout(timeoutRef.current);
    }
    setError(message);
    timeoutRef.current = setTimeout(() => {
      setError("");
      timeoutRef.current = null;
    }, 3000);
  };

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        setIsLoading(true);
        const response = await fetch(
          `${process.env.REACT_APP_API_URL}/api/posts`
        );
        if (!response.ok) {
          throw new Error(`Failed to fetch posts: ${response.status}`);
        }
        const data = await response.json();
        setPosts(data);
      } catch (err) {
        setTemporaryError("Failed to load posts. Please try again.");
      } finally {
        setIsLoading(false);
      }
    };
    fetchPosts();

    return () => {
      if (timeoutRef.current) {
        clearTimeout(timeoutRef.current);
      }
    };
  }, []);

  return (
    <section
      className="min-h-screen bg-gradient-to-br from-gray-50 to-blue-50 py-10"
      aria-label="Home Feed"
    >
      <style>
        {`
          @keyframes fadeInUp {
            0% { opacity: 0; transform: translateY(20px); }
            100% { opacity: 1; transform: translateY(0); }
          }
          @keyframes pulse {
            0% { opacity: 0.6; }
            50% { opacity: 1; }
            100% { opacity: 0.6; }
          }
          .animate-fade-in-up {
            animation: fadeInUp 0.5s ease-out forwards;
          }
          .animate-pulse-text {
            animation: pulse 1.5s infinite;
          }
        `}
      </style>
      <div className="container px-4 animate-fade-in-up">
        {user && (
          <div className="mb-8 bg-gradient-to-br from-blue-50 to-indigo-50 p-6 rounded-xl shadow-xl border border-blue-100">
            <PostForm setPosts={setPosts} />
          </div>
        )}
        {error && (
          <div className="bg-red-100 text-red-700 p-4 mb-8 rounded-lg border border-gradient-to-r from-red-200 to-red-300 shadow-md">
            {error}
          </div>
        )}
        {isLoading ? (
          <div className="bg-white p-6 rounded-lg shadow-lg flex flex-col items-center justify-center">
            <div className="inline-block animate-spin rounded-full h-10 w-10 border-t-4 border-b-4 border-gradient-to-r from-blue-600 to-indigo-600"></div>
            <p className="text-gray-600 mt-3 text-lg animate-pulse-text">
              Loading posts...
            </p>
          </div>
        ) : posts.length > 0 ? (
          <div className="bg-white p-6 rounded-xl shadow-lg space-y-8">
            {posts.map((post, index) => (
              <div
                key={post._id}
                className="animate-fade-in-up"
                style={{ animationDelay: `${index * 0.1}s` }}
              >
                <PostCard
                  id={post._id}
                  title={post.title}
                  content={post.content}
                  author={post.author?.username || "Unknown"}
                  comments={post.comments}
                  likes={post.likes}
                  post={post}
                  setPosts={setPosts}
                  currentUser={user}
                />
              </div>
            ))}
          </div>
        ) : (
          <p className="text-center text-lg bg-clip-text text-transparent bg-gradient-to-r from-blue-600 to-indigo-600 transition-transform duration-200 hover:scale-105">
            No posts available.
          </p>
        )}
      </div>
    </section>
  );
}

export default Home;
