import React, { useState, useEffect, useContext, useRef } from "react";
import PostForm from "./PostForm";
import PostCard from "./PostCard";
import { AuthContext } from "../context/AuthContext";

function Home() {
  const { user } = useContext(AuthContext);
  const [posts, setPosts] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState("");
  const timeoutRef = useRef(null);

  const setTemporaryError = (message) => {
    if (timeoutRef.current) {
      clearTimeout(timeoutRef.current);
    }
    setError(message);
    timeoutRef.current = setTimeout(() => {
      setError("");
      timeoutRef.current = null;
    }, 3000);
  };

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        setIsLoading(true);
        const response = await fetch(
          `${process.env.REACT_APP_API_URL}/api/posts`
        );
        if (!response.ok) {
          throw new Error(`Failed to fetch posts: ${response.status}`);
        }
        const data = await response.json();
        setPosts(data);
      } catch (err) {
        setTemporaryError("Failed to load posts. Please try again.");
      } finally {
        setIsLoading(false);
      }
    };
    fetchPosts();

    return () => {
      if (timeoutRef.current) {
        clearTimeout(timeoutRef.current);
      }
    };
  }, []);

  return (
    <section aria-label="Home Feed">
      {user && <PostForm setPosts={setPosts} />}
      {error && (
        <div className="bg-red-100 text-red-700 p-3 mb-6 rounded-md message">
          {error}
        </div>
      )}
      {isLoading ? (
        <p className="text-gray-600 text-center">Loading posts...</p>
      ) : posts.length > 0 ? (
        <div className="space-y-6">
          {posts.map((post) => (
            <PostCard
              key={post._id}
              id={post._id}
              title={post.title}
              content={post.content}
              author={post.author?.username || "Unknown"}
              comments={post.comments}
              likes={post.likes}
              post={post}
              setPosts={setPosts}
              currentUser={user}
            />
          ))}
        </div>
      ) : (
        <p className="text-gray-600 text-center">No posts available.</p>
      )}
    </section>
  );
}

export default Home;
