import React, { useState, useEffect, useContext, useRef } from "react";
import { Calendar, dateFnsLocalizer } from "react-big-calendar";
import { format, parse, startOfWeek, getDay } from "date-fns";
import "react-big-calendar/lib/css/react-big-calendar.css";
import { AuthContext } from "../context/AuthContext";
import { useNavigate } from "react-router-dom";

// Setup date-fns localizer for react-big-calendar
const locales = {
  "en-US": require("date-fns/locale/en-US"),
};
const localizer = dateFnsLocalizer({
  format,
  parse,
  startOfWeek,
  getDay,
  locales,
});

function CalendarPage() {
  const { user, isLoading: authLoading } = useContext(AuthContext);
  const navigate = useNavigate();
  const [events, setEvents] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState("");
  const [showModal, setShowModal] = useState(false);
  const [newEvent, setNewEvent] = useState({
    title: "",
    description: "",
    start: new Date(),
    end: new Date(),
    groupId: "",
  });
  const [groups, setGroups] = useState([]);
  const [selectedEvent, setSelectedEvent] = useState(null);

  // Refs for focusing inputs and buttons
  const titleInputRef = useRef(null);
  const descriptionInputRef = useRef(null);
  const startInputRef = useRef(null);
  const endInputRef = useRef(null);
  const groupSelectRef = useRef(null);
  const createButtonRef = useRef(null);
  const cancelButtonRef = useRef(null);

  // Fetch user's groups
  useEffect(() => {
    const fetchGroups = async () => {
      try {
        const response = await fetch(
          `${process.env.REACT_APP_API_URL}/api/study-groups`,
          {
            headers: {
              Authorization: `Bearer ${localStorage.getItem("token")}`,
            },
          }
        );
        if (!response.ok) {
          throw new Error("Failed to fetch groups");
        }
        const data = await response.json();
        const userGroups = data.filter((group) =>
          group.members.some((member) => member._id === user.id)
        );
        setGroups(userGroups);
        if (userGroups.length > 0) {
          setNewEvent((prev) => ({ ...prev, groupId: userGroups[0]._id }));
        }
      } catch (err) {
        setError(err.message || "Failed to load groups");
      }
    };

    if (user && !authLoading) {
      fetchGroups();
    }
  }, [user, authLoading]);

  // Fetch events
  useEffect(() => {
    const fetchEvents = async () => {
      try {
        const response = await fetch(
          `${process.env.REACT_APP_API_URL}/api/events`,
          {
            headers: {
              Authorization: `Bearer ${localStorage.getItem("token")}`,
            },
          }
        );
        if (!response.ok) {
          throw new Error("Failed to fetch events");
        }
        const data = await response.json();
        console.log("Fetched events:", data);
        const formattedEvents = data.map((event) => ({
          ...event,
          start: new Date(event.start),
          end: new Date(event.end),
          title: `${event.title} (${event.group?.name || "Unknown Group"})`,
          group: event.group,
          creator: event.creator,
        }));
        setEvents(formattedEvents);
      } catch (err) {
        setError(err.message || "Failed to load events");
      } finally {
        setIsLoading(false);
      }
    };

    if (authLoading) {
      setIsLoading(true);
    } else if (user) {
      fetchEvents();
    } else {
      setIsLoading(false);
      setError("Please log in to view events.");
    }
  }, [user, authLoading]);

  const handleCreateEvent = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch(
        `${process.env.REACT_APP_API_URL}/api/events`,
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${localStorage.getItem("token")}`,
          },
          body: JSON.stringify({
            title: newEvent.title,
            description: newEvent.description,
            start: newEvent.start,
            end: newEvent.end,
            groupId: newEvent.groupId,
          }),
        }
      );
      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || "Failed to create event");
      }
      const createdEvent = await response.json();
      setEvents([
        ...events,
        {
          ...createdEvent,
          start: new Date(createdEvent.start),
          end: new Date(createdEvent.end),
          title: `${createdEvent.title} (${
            groups.find((g) => g._id === createdEvent.group.toString())?.name ||
            "Unknown Group"
          })`,
          group: groups.find((g) => g._id === createdEvent.group.toString()),
          creator: { _id: user.id, username: user.username },
        },
      ]);
      setShowModal(false);
      setNewEvent({
        title: "",
        description: "",
        start: new Date(),
        end: new Date(),
        groupId: groups.length > 0 ? groups[0]._id : "",
      });
    } catch (err) {
      setError(err.message || "Failed to create event");
    }
  };

  const handleDeleteEvent = async () => {
    if (!selectedEvent) return;
    try {
      const response = await fetch(
        `${process.env.REACT_APP_API_URL}/api/events/${selectedEvent._id}`,
        {
          method: "DELETE",
          headers: {
            Authorization: `Bearer ${localStorage.getItem("token")}`,
          },
        }
      );
      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || "Failed to delete event");
      }
      setEvents(events.filter((event) => event._id !== selectedEvent._id));
      setSelectedEvent(null);
    } catch (err) {
      setError(err.message || "Failed to delete event");
    }
  };

  const handleSelectSlot = ({ start, end }) => {
    setNewEvent((prev) => ({ ...prev, start, end }));
    setShowModal(true);
  };

  const handleSelectEvent = (event) => {
    console.log("Selected event:", event);
    setSelectedEvent(event);
  };

  // Handle Enter key to move focus to the next field
  const handleTitleKeyDown = (e) => {
    if (e.key === "Enter") {
      e.preventDefault();
      descriptionInputRef.current.focus();
    }
  };

  const handleDescriptionKeyDown = (e) => {
    if (e.key === "Enter" && !e.shiftKey) {
      e.preventDefault();
      startInputRef.current.focus();
    }
    // Shift + Enter will add a new line (default behavior of textarea)
  };

  const handleStartKeyDown = (e) => {
    if (e.key === "Enter") {
      e.preventDefault();
      endInputRef.current.focus();
    }
  };

  const handleEndKeyDown = (e) => {
    if (e.key === "Enter") {
      e.preventDefault();
      groupSelectRef.current.focus();
    }
  };

  const handleGroupKeyDown = (e) => {
    if (e.key === "Enter") {
      e.preventDefault();
      createButtonRef.current.focus();
    }
  };

  // Handle arrow key navigation between buttons
  const handleCreateButtonKeyDown = (e) => {
    if (e.key === "ArrowRight") {
      e.preventDefault();
      cancelButtonRef.current.focus();
    }
  };

  const handleCancelButtonKeyDown = (e) => {
    if (e.key === "ArrowLeft") {
      e.preventDefault();
      createButtonRef.current.focus();
    }
  };

  if (authLoading || isLoading) {
    return (
      <div className="bg-gradient-to-br from-blue-50 to-indigo-50 dark:from-gray-800 dark:to-gray-900 p-6 rounded-xl shadow-xl border border-blue-100 dark:border-gray-600 hover:shadow-2xl dark:hover:shadow-[0_0_15px_rgba(209,213,219,0.3)] hover:-translate-y-2 transition-all duration-300 animate-fade-in-up flex flex-col items-center justify-center">
        <div className="inline-block animate-spin rounded-full h-10 w-10 border-t-4 border-b-4 border-gradient-to-r from-blue-600 to-indigo-600 dark:from-gray-500 dark:to-gray-400"></div>
        <p className="text-gray-600 dark:text-gray-400 mt-3 text-lg animate-pulse">
          Loading calendar...
        </p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="bg-red-100 dark:bg-red-900/70 text-red-700 dark:text-red-300 p-4 mt-10 rounded-lg border border-gradient-to-r from-red-200 to-red-300 dark:from-red-800 dark:to-red-700 shadow-md hover:shadow-[0_0_10px_rgba(239,68,68,0.5)] dark:hover:shadow-[0_0_10px_rgba(209,213,219,0.5)] transition-all duration-300 animate-fade-in-up">
        {error}
      </div>
    );
  }

  return (
    <section className="min-h-screen bg-gradient-to-br from-gray-50 to-blue-50 dark:from-gray-800 dark:to-gray-900 py-10">
      <div className="container mx-auto px-4">
        <div className="max-w-4xl mx-auto">
          <div className="bg-gradient-to-br from-blue-50 to-indigo-50 dark:from-gray-800 dark:to-gray-900 p-6 rounded-xl shadow-xl border border-blue-100 dark:border-gray-600 hover:shadow-2xl dark:hover:shadow-[0_0_15px_rgba(209,213,219,0.3)] hover:-translate-y-2 transition-all duration-300 animate-fade-in-up">
            <h1 className="relative text-2xl font-bold bg-clip-text text-transparent bg-gradient-to-r from-blue-600 to-indigo-600 dark:from-amber-200 dark:to-amber-100 mb-6 hover-underline">
              Study Calendar
            </h1>
            <button
              onClick={() => setShowModal(true)}
              className="mb-4 bg-gradient-to-r from-blue-600 to-indigo-600 dark:from-cyan-600 dark:to-teal-600 text-white dark:text-gray-200 px-5 py-3 rounded-lg hover:shadow-[0_0_10px_rgba(59,130,246,0.5)] dark:hover:shadow-[0_0_10px_rgba(21,94,117,0.5)] dark:hover:dark-glow hover:scale-105 transition-all duration-300 focus:outline-none focus:ring-2 focus:ring-blue-500 dark:focus:ring-cyan-500 focus:ring-offset-2 dark:focus:ring-offset-gray-800 flex items-center space-x-1"
              aria-label="Create New Event"
            >
              <i className="fa-solid fa-plus"></i>
              <span>Create Event</span>
            </button>
            <div className="calendar-container">
              <Calendar
                localizer={localizer}
                events={events}
                startAccessor="start"
                endAccessor="end"
                style={{ height: 500 }}
                selectable
                onSelectSlot={handleSelectSlot}
                onSelectEvent={handleSelectEvent}
                className="bg-white dark:bg-gray-700 rounded-lg shadow-inner text-gray-800 dark:text-gray-200"
              />
            </div>
          </div>

          {/* Event Details Modal */}
          {selectedEvent && (
            <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
              <div className="bg-white dark:bg-gray-800 p-6 rounded-lg shadow-xl max-w-md w-full">
                <h2 className="text-xl font-bold text-gray-800 dark:text-amber-200 mb-4">
                  {selectedEvent.title}
                </h2>
                <p className="text-gray-600 dark:text-gray-300 mb-2">
                  <strong>Start:</strong> {format(selectedEvent.start, "PPP p")}
                </p>
                <p className="text-gray-600 dark:text-gray-300 mb-2">
                  <strong>End:</strong> {format(selectedEvent.end, "PPP p")}
                </p>
                <p className="text-gray-600 dark:text-gray-300 mb-4">
                  <strong>Description:</strong>{" "}
                  {selectedEvent.description || "No description"}
                </p>
                <div className="flex space-x-3">
                  <button
                    onClick={() =>
                      navigate(
                        `/groups/${
                          typeof selectedEvent.group === "string"
                            ? selectedEvent.group
                            : selectedEvent.group?._id
                        }`
                      )
                    }
                    className="bg-gradient-to-r from-blue-500 to-blue-600 dark:from-purple-600 dark:to-indigo-600 text-white dark:text-gray-200 px-4 py-2 rounded-md hover:shadow-[0_0_10px_rgba(59,130,246,0.5)] dark:hover:shadow-[0_0_10px_rgba(88,28,135,0.5)] dark:hover:dark-glow hover:scale-105 transition-all duration-300 focus:outline-none focus:ring-2 focus:ring-blue-500 dark:focus:ring-purple-500 focus:ring-offset-2 dark:focus:ring-offset-gray-800 text-sm flex items-center space-x-1"
                    aria-label="View Group"
                  >
                    <i className="fa-solid fa-users"></i>
                    <span>View Group</span>
                  </button>
                  {selectedEvent.creator?._id &&
                    user?.id &&
                    selectedEvent.creator._id === user.id && (
                      <button
                        onClick={handleDeleteEvent}
                        className="bg-gradient-to-r from-red-500 to-red-600 dark:from-red-600 dark:to-red-700 text-white dark:text-gray-200 px-4 py-2 rounded-md hover:shadow-[0_0_10px_rgba(239,68,68,0.5)] dark:hover:shadow-[0_0_10px_rgba(220,38,38,0.5)] dark:hover:dark-glow hover:scale-105 transition-all duration-300 focus:outline-none focus:ring-2 focus:ring-red-500 dark:focus:ring-red-600 focus:ring-offset-2 dark:focus:ring-offset-gray-800 text-sm"
                        aria-label="Delete Event"
                      >
                        Delete
                      </button>
                    )}
                  <button
                    onClick={() => setSelectedEvent(null)}
                    className="bg-gradient-to-r from-gray-500 to-gray-600 dark:from-gray-600 dark:to-gray-500 text-white dark:text-gray-200 px-4 py-2 rounded-md hover:shadow-[0_0_10px_rgba(107,114,128,0.5)] dark:hover:shadow-[0_0_10px_rgba(209,213,219,0.5)] dark:hover:dark-glow hover:scale-105 transition-all duration-300 focus:outline-none focus:ring-2 focus:ring-gray-500 dark:focus:ring-gray-500 focus:ring-offset-2 dark:focus:ring-offset-gray-800 text-sm"
                    aria-label="Close"
                  >
                    Close
                  </button>
                </div>
              </div>
            </div>
          )}

          {/* Create Event Modal */}
          {showModal && (
            <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
              <div className="bg-white dark:bg-gray-800 p-6 rounded-lg shadow-xl max-w-md w-full">
                <h2 className="text-xl font-bold text-gray-800 dark:text-amber-200 mb-4">
                  Create New Event
                </h2>
                <div className="mb-4">
                  <label
                    htmlFor="event-title"
                    className="block text-gray-700 dark:text-gray-200 mb-2 font-medium"
                  >
                    Title
                  </label>
                  <input
                    type="text"
                    id="event-title"
                    ref={titleInputRef}
                    value={newEvent.title}
                    onChange={(e) =>
                      setNewEvent({ ...newEvent, title: e.target.value })
                    }
                    onKeyDown={handleTitleKeyDown}
                    className="w-full p-3 bg-gray-50 dark:bg-gray-700 border border-gray-200 dark:border-gray-600 rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-gradient-to-r from-blue-500 to-indigo-500 dark:from-gray-500 dark:to-gray-400 focus:scale-[1.01] transition-all duration-300 text-gray-800 dark:text-gray-200 placeholder-gray-500 dark:placeholder-gray-400"
                    placeholder="Event title"
                    aria-label="Event Title"
                  />
                </div>
                <div className="mb-4">
                  <label
                    htmlFor="event-description"
                    className="block text-gray-700 dark:text-gray-200 mb-2 font-medium"
                  >
                    Description
                  </label>
                  <textarea
                    id="event-description"
                    ref={descriptionInputRef}
                    value={newEvent.description}
                    onChange={(e) =>
                      setNewEvent({ ...newEvent, description: e.target.value })
                    }
                    onKeyDown={handleDescriptionKeyDown}
                    className="w-full p-3 bg-gray-50 dark:bg-gray-700 border border-gray-200 dark:border-gray-600 rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-gradient-to-r from-blue-500 to-indigo-500 dark:from-gray-500 dark:to-gray-400 focus:scale-[1.01] transition-all duration-300 text-gray-800 dark:text-gray-200 placeholder-gray-500 dark:placeholder-gray-400"
                    rows="3"
                    placeholder="Event description (Shift + Enter for new line)"
                    aria-label="Event Description"
                  />
                </div>
                <div className="mb-4">
                  <label
                    htmlFor="event-start"
                    className="block text-gray-700 dark:text-gray-200 mb-2 font-medium"
                  >
                    Start Time
                  </label>
                  <input
                    type="datetime-local"
                    id="event-start"
                    ref={startInputRef}
                    value={format(newEvent.start, "yyyy-MM-dd'T'HH:mm")}
                    onChange={(e) =>
                      setNewEvent({
                        ...newEvent,
                        start: new Date(e.target.value),
                      })
                    }
                    onKeyDown={handleStartKeyDown}
                    className="w-full p-3 bg-gray-50 dark:bg-gray-700 border border-gray-200 dark:border-gray-600 rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-gradient-to-r from-blue-500 to-indigo-500 dark:from-gray-500 dark:to-gray-400 focus:scale-[1.01] transition-all duration-300 text-gray-800 dark:text-gray-200"
                    aria-label="Event Start Time"
                  />
                </div>
                <div className="mb-4">
                  <label
                    htmlFor="event-end"
                    className="block text-gray-700 dark:text-gray-200 mb-2 font-medium"
                  >
                    End Time
                  </label>
                  <input
                    type="datetime-local"
                    id="event-end"
                    ref={endInputRef}
                    value={format(newEvent.end, "yyyy-MM-dd'T'HH:mm")}
                    onChange={(e) =>
                      setNewEvent({
                        ...newEvent,
                        end: new Date(e.target.value),
                      })
                    }
                    onKeyDown={handleEndKeyDown}
                    className="w-full p-3 bg-gray-50 dark:bg-gray-700 border border-gray-200 dark:border-gray-600 rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-gradient-to-r from-blue-500 to-indigo-500 dark:from-gray-500 dark:to-gray-400 focus:scale-[1.01] transition-all duration-300 text-gray-800 dark:text-gray-200"
                    aria-label="Event End Time"
                  />
                </div>
                <div className="mb-4">
                  <label
                    htmlFor="event-group"
                    className="block text-gray-700 dark:text-gray-200 mb-2 font-medium"
                  >
                    Group
                  </label>
                  <select
                    id="event-group"
                    ref={groupSelectRef}
                    value={newEvent.groupId}
                    onChange={(e) =>
                      setNewEvent({ ...newEvent, groupId: e.target.value })
                    }
                    onKeyDown={handleGroupKeyDown}
                    className="w-full p-3 bg-gray-50 dark:bg-gray-700 border border-gray-200 dark:border-gray-600 rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-gradient-to-r from-blue-500 to-indigo-500 dark:from-gray-500 dark:to-gray-400 focus:scale-[1.01] transition-all duration-300 text-gray-800 dark:text-gray-200"
                    aria-label="Select Group"
                  >
                    {groups.map((group) => (
                      <option key={group._id} value={group._id}>
                        {group.name}
                      </option>
                    ))}
                  </select>
                </div>
                <div className="flex space-x-3">
                  <button
                    ref={createButtonRef}
                    onClick={handleCreateEvent}
                    onKeyDown={handleCreateButtonKeyDown}
                    className="bg-gradient-to-r from-blue-600 to-indigo-600 dark:from-cyan-600 dark:to-teal-600 text-white dark:text-gray-200 px-5 py-3 rounded-lg hover:shadow-[0_0_10px_rgba(59,130,246,0.5)] dark:hover:shadow-[0_0_10px_rgba(21,94,117,0.5)] dark:hover:dark-glow hover:scale-105 transition-all duration-300 focus:outline-none focus:ring-2 focus:ring-blue-500 dark:focus:ring-cyan-500 focus:ring-offset-2"
                    aria-label="Create Event"
                  >
                    Create
                  </button>
                  <button
                    ref={cancelButtonRef}
                    onClick={() => setShowModal(false)}
                    onKeyDown={handleCancelButtonKeyDown}
                    className="bg-gradient-to-r from-gray-500 to-gray-600 dark:from-gray-600 dark:to-gray-500 text-white dark:text-gray-200 px-5 py-3 rounded-lg hover:shadow-[0_0_5px_rgba(107,114,128,0.3)] dark:hover:shadow-[0_0_5px_rgba(209,213,219,0.3)] dark:hover:dark-glow hover:scale-105 transition-all duration-300 focus:outline-none focus:ring-2 focus:ring-gray-500 dark:focus:ring-gray-500 focus:ring-offset-2"
                    aria-label="Cancel"
                  >
                    Cancel
                  </button>
                </div>
              </div>
            </div>
          )}
        </div>
      </div>
    </section>
  );
}

export default CalendarPage;
