import React, { useState, useEffect, useContext, useRef } from "react";
import PostCard from "./PostCard";
import { AuthContext } from "../context/AuthContext";
import axios from "axios";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faExclamationCircle,
  faCheckCircle,
  faPlus,
} from "@fortawesome/free-solid-svg-icons";

function Profile() {
  const { user, setUser } = useContext(AuthContext);
  const [posts, setPosts] = useState([]);
  const [isPostsLoading, setIsPostsLoading] = useState(false);
  const [bio, setBio] = useState("");
  const [username, setUsername] = useState("");
  const [profilePicture, setProfilePicture] = useState(null);
  const [isEditing, setIsEditing] = useState(false);
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const [sortOption, setSortOption] = useState("newest");
  const [showCreatePost, setShowCreatePost] = useState(false);
  const [newPostTitle, setNewPostTitle] = useState("");
  const [newPostContent, setNewPostContent] = useState("");
  const timeoutRef = useRef(null);
  const fileInputRef = useRef(null);
  const BIO_MAX_LENGTH = 150;
  const USERNAME_MIN_LENGTH = 3;
  const USERNAME_MAX_LENGTH = 20;

  const setTemporaryMessage = (type, message) => {
    if (timeoutRef.current) {
      clearTimeout(timeoutRef.current);
    }
    if (type === "error") {
      setError(message);
      setSuccess("");
    } else {
      setSuccess(message);
      setError("");
    }
    timeoutRef.current = setTimeout(() => {
      setError("");
      setSuccess("");
      timeoutRef.current = null;
    }, 3000);
  };

  useEffect(() => {
    return () => {
      if (timeoutRef.current) {
        clearTimeout(timeoutRef.current);
      }
    };
  }, []);

  useEffect(() => {
    if (user) {
      setBio(user.bio || "");
      setUsername(user.username || "");
      const fetchUserPosts = async () => {
        setIsPostsLoading(true);
        try {
          const response = await fetch(
            `${process.env.REACT_APP_API_URL}/api/posts?author=${user.id}`
          );
          if (!response.ok) {
            throw new Error(`Failed to fetch user posts: ${response.status}`);
          }
          const data = await response.json();
          setPosts(data);
        } catch (err) {
          setTemporaryMessage("error", "Failed to fetch posts");
        } finally {
          setIsPostsLoading(false);
        }
      };
      fetchUserPosts();
    }
  }, [user?.id]); // Depend on user.id to prevent re-fetches if other user properties change

  const handleProfilePictureChange = async (e) => {
    const file = e.target.files[0];
    if (!file) return;

    if (!file.type.startsWith("image/")) {
      setTemporaryMessage("error", "Please upload an image file");
      return;
    }

    if (file.size > 5 * 1024 * 1024) {
      // 5MB limit
      setTemporaryMessage("error", "Image size must be less than 5MB");
      return;
    }

    const formData = new FormData();
    formData.append("profilePicture", file);

    try {
      setIsLoading(true);
      const token = localStorage.getItem("token");
      const response = await axios.post(
        `${process.env.REACT_APP_API_URL}/api/users/upload-profile-picture`,
        formData,
        {
          headers: {
            "Content-Type": "multipart/form-data",
            Authorization: `Bearer ${token}`,
          },
        }
      );

      const updatedUser = {
        ...user,
        profilePicture: response.data.profilePicture,
      };
      setUser(updatedUser);
      setTemporaryMessage("success", "Profile picture updated successfully");
    } catch (err) {
      const errorMessage =
        err.response?.data?.message ||
        err.message ||
        "Failed to upload profile picture";
      setTemporaryMessage("error", errorMessage);
    } finally {
      setIsLoading(false);
      setProfilePicture(null);
      if (fileInputRef.current) {
        fileInputRef.current.value = null;
      }
    }
  };

  const handleBioUpdate = async (e) => {
    e.preventDefault();
    setError("");
    setSuccess("");
    setIsLoading(true);

    if (!bio.trim()) {
      setTemporaryMessage("error", "Bio cannot be empty");
      setIsLoading(false);
      return;
    }

    if (bio.length > BIO_MAX_LENGTH) {
      setTemporaryMessage(
        "error",
        `Bio cannot exceed ${BIO_MAX_LENGTH} characters`
      );
      setIsLoading(false);
      return;
    }

    if (
      username.length < USERNAME_MIN_LENGTH ||
      username.length > USERNAME_MAX_LENGTH
    ) {
      setTemporaryMessage(
        "error",
        `Username must be between ${USERNAME_MIN_LENGTH} and ${USERNAME_MAX_LENGTH} characters`
      );
      setIsLoading(false);
      return;
    }

    if (!/^[a-zA-Z0-9_]+$/.test(username)) {
      setTemporaryMessage(
        "error",
        "Username can only contain letters, numbers, and underscores"
      );
      setIsLoading(false);
      return;
    }

    try {
      const token = localStorage.getItem("token");
      if (!token) {
        throw new Error("No authentication token found. Please log in again.");
      }
      if (typeof setUser !== "function") {
        throw new Error(
          "setUser is not a function. Ensure AuthProvider wraps the app."
        );
      }

      const response = await axios.put(
        `${process.env.REACT_APP_API_URL}/api/users/profile`,
        { bio, username },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      const updatedUser = {
        id: response.data.id,
        username: response.data.username,
        email: response.data.email,
        bio: response.data.bio,
        profilePicture: user.profilePicture || response.data.profilePicture,
      };
      setUser(updatedUser);
      setIsEditing(false);
      setTemporaryMessage("success", "Profile updated successfully");
    } catch (err) {
      const errorMessage =
        err.response?.data?.message ||
        err.message ||
        "Failed to update profile";
      setTemporaryMessage("error", errorMessage);
    } finally {
      setIsLoading(false);
    }
  };

  const handleCreatePost = async (e) => {
    e.preventDefault();
    setError("");
    setSuccess("");
    setIsLoading(true);

    if (!newPostTitle.trim() || !newPostContent.trim()) {
      setTemporaryMessage("error", "Title and content are required");
      setIsLoading(false);
      return;
    }

    try {
      const token = localStorage.getItem("token");
      const response = await axios.post(
        `${process.env.REACT_APP_API_URL}/api/posts`,
        { title: newPostTitle, content: newPostContent },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      setPosts((prevPosts) => [response.data, ...prevPosts]);
      setNewPostTitle("");
      setNewPostContent("");
      setShowCreatePost(false);
      setTemporaryMessage("success", "Post created successfully");
    } catch (err) {
      const errorMessage =
        err.response?.data?.message || err.message || "Failed to create post";
      setTemporaryMessage("error", errorMessage);
    } finally {
      setIsLoading(false);
    }
  };

  const sortPosts = (posts) => {
    const sortedPosts = [...posts];
    switch (sortOption) {
      case "newest":
        return sortedPosts.sort(
          (a, b) => new Date(b.createdAt) - new Date(a.createdAt)
        );
      case "oldest":
        return sortedPosts.sort(
          (a, b) => new Date(a.createdAt) - new Date(b.createdAt)
        );
      case "mostLikes":
        return sortedPosts.sort(
          (a, b) => (b.likes?.length || 0) - (a.likes?.length || 0)
        );
      case "leastLikes":
        return sortedPosts.sort(
          (a, b) => (a.likes?.length || 0) - (b.likes?.length || 0)
        );
      default:
        return sortedPosts;
    }
  };

  const totalLikes = posts.reduce(
    (sum, post) => sum + (post.likes?.length || 0),
    0
  );

  if (!user) {
    return (
      <div className="text-gray-600 text-center py-10">
        Please log in to view your profile.
      </div>
    );
  }

  return (
    <div className="max-w-4xl mx-auto">
      <div className="bg-white p-6 rounded-lg shadow-md mb-8 transition-all duration-300">
        <div className="flex flex-col sm:flex-row items-center sm:items-start space-y-4 sm:space-y-0 sm:space-x-6 mb-6">
          <div className="relative">
            {user.profilePicture ? (
              <img
                src={user.profilePicture}
                alt={`${user.username}'s profile picture`}
                className="w-16 h-16 rounded-full object-cover border-2 border-blue-600"
                aria-label={`${user.username}'s profile picture`}
              />
            ) : (
              <div
                className="w-16 h-16 bg-blue-600 text-white flex items-center justify-center rounded-full text-2xl font-semibold border-2 border-blue-600"
                aria-label={`${user.username}'s avatar`}
              >
                {user.username.charAt(0).toUpperCase()}
              </div>
            )}
            <button
              onClick={() => fileInputRef.current?.click()}
              className="absolute bottom-0 right-0 bg-blue-600 text-white p-2 rounded-full hover:bg-blue-700 transition duration-200 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2"
              aria-label="Upload profile picture"
              disabled={isLoading}
            >
              <FontAwesomeIcon icon={faPlus} />
            </button>
            <input
              type="file"
              ref={fileInputRef}
              onChange={handleProfilePictureChange}
              accept="image/*"
              className="hidden"
              aria-label="Profile picture upload"
            />
          </div>
          <div className="text-center sm:text-left">
            {isEditing ? (
              <>
                <input
                  type="text"
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                  placeholder="Update your username"
                  className="w-full p-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent transition duration-150 mb-2 text-xl font-semibold text-gray-800"
                  maxLength={USERNAME_MAX_LENGTH}
                  aria-label="Username"
                />
                <p className="text-gray-500 text-sm mb-2">
                  {username.length}/{USERNAME_MAX_LENGTH} characters
                </p>
              </>
            ) : (
              <h2 className="text-2xl font-semibold text-gray-800">
                {user.username}
              </h2>
            )}
            <p className="text-gray-500 text-sm">{user.email}</p>
            <div className="flex flex-wrap justify-center sm:justify-start space-x-4 mt-2 text-sm text-gray-600">
              <p>Posts: {posts.length}</p>
              <p>Total Likes: {totalLikes}</p>
              {user.createdAt && (
                <p>Joined: {new Date(user.createdAt).toLocaleDateString()}</p>
              )}
            </div>
          </div>
        </div>
        {isEditing ? (
          <form onSubmit={handleBioUpdate} className="animate-fade-in">
            <textarea
              value={bio}
              onChange={(e) => setBio(e.target.value)}
              placeholder="Update your bio"
              className="w-full p-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent transition duration-150 mb-2"
              rows="4"
              maxLength={BIO_MAX_LENGTH}
              aria-label="Bio"
            />
            <p className="text-gray-500 text-sm mb-2">
              {bio.length}/{BIO_MAX_LENGTH} characters
            </p>
            {error && (
              <div className="bg-red-100 text-red-700 p-3 mb-4 rounded-md message flex items-center space-x-2">
                <FontAwesomeIcon icon={faExclamationCircle} />
                <span>{error}</span>
              </div>
            )}
            <div className="flex space-x-3">
              <button
                type="submit"
                className="bg-blue-600 text-white px-4 py-2 rounded-md hover:bg-blue-700 transition duration-200 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2 disabled:opacity-50"
                disabled={isLoading}
                aria-label="Save Profile"
              >
                {isLoading ? "Saving..." : "Save"}
              </button>
              <button
                type="button"
                onClick={() => {
                  setIsEditing(false);
                  setBio(user.bio || "");
                  setUsername(user.username || "");
                  setError("");
                  setSuccess("");
                }}
                className="bg-gray-300 text-gray-800 px-4 py-2 rounded-md hover:bg-gray-400 transition duration-200 focus:outline-none focus:ring-2 focus:ring-gray-400 focus:ring-offset-2 disabled:opacity-50"
                disabled={isLoading}
                aria-label="Cancel"
              >
                Cancel
              </button>
            </div>
          </form>
        ) : (
          <>
            <p className="text-gray-600 mb-4">{user.bio || "No bio set"}</p>
            <button
              onClick={() => setIsEditing(true)}
              className="bg-blue-600 text-white px-4 py-2 rounded-md hover:bg-blue-700 transition duration-200 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2"
              aria-label="Edit Profile"
            >
              Edit Profile
            </button>
            {success && (
              <div className="bg-green-100 text-green-700 p-3 mt-4 rounded-md message flex items-center space-x-2">
                <FontAwesomeIcon icon={faCheckCircle} />
                <span>{success}</span>
              </div>
            )}
          </>
        )}
      </div>

      <div className="mb-6">
        <button
          onClick={() => setShowCreatePost(!showCreatePost)}
          className="bg-blue-600 text-white px-4 py-2 rounded-md hover:bg-blue-700 transition duration-200 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2 mb-4"
          aria-label={
            showCreatePost ? "Hide Create Post Form" : "Create New Post"
          }
        >
          {showCreatePost ? "Cancel" : "Create New Post"}
        </button>
        {showCreatePost && (
          <form
            onSubmit={handleCreatePost}
            className="bg-white p-6 rounded-lg shadow-md mb-6 animate-fade-in"
          >
            <input
              type="text"
              value={newPostTitle}
              onChange={(e) => setNewPostTitle(e.target.value)}
              placeholder="Post Title"
              className="w-full p-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent transition duration-150 mb-4"
              aria-label="Post Title"
            />
            <textarea
              value={newPostContent}
              onChange={(e) => setNewPostContent(e.target.value)}
              placeholder="Post Content"
              className="w-full p-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent transition duration-150 mb-4"
              rows="4"
              aria-label="Post Content"
            />
            <button
              type="submit"
              className="bg-blue-600 text-white px-4 py-2 rounded-md hover:bg-blue-700 transition duration-200 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2 disabled:opacity-50"
              disabled={isLoading}
              aria-label="Create Post"
            >
              {isLoading ? "Creating..." : "Create Post"}
            </button>
          </form>
        )}
      </div>

      <div className="flex justify-between items-center mb-6">
        <h3 className="text-xl font-semibold text-gray-800">My Posts</h3>
        <div>
          <label htmlFor="sort-posts" className="text-gray-600 mr-2">
            Sort by:
          </label>
          <select
            id="sort-posts"
            value={sortOption}
            onChange={(e) => setSortOption(e.target.value)}
            className="border border-gray-300 rounded-md p-1 focus:outline-none focus:ring-2 focus:ring-blue-500"
            aria-label="Sort posts"
          >
            <option value="newest">Newest</option>
            <option value="oldest">Oldest</option>
            <option value="mostLikes">Most Likes</option>
            <option value="leastLikes">Least Likes</option>
          </select>
        </div>
      </div>

      <div className="space-y-6">
        {isPostsLoading ? (
          <div className="text-center text-gray-600 py-10">
            <div className="inline-block animate-spin rounded-full h-8 w-8 border-t-2 border-b-2 border-blue-600"></div>
            <p className="mt-2">Loading posts...</p>
          </div>
        ) : sortPosts(posts).length > 0 ? (
          sortPosts(posts).map((post) => (
            <PostCard
              key={post._id}
              id={post._id}
              title={post.title}
              content={post.content}
              author={post.author?.username || "Unknown"}
              comments={post.comments}
              likes={post.likes}
              post={post}
              setPosts={setPosts}
              currentUser={user}
            />
          ))
        ) : (
          <p className="text-gray-600 text-center">No posts yet.</p>
        )}
      </div>
    </div>
  );
}

export default Profile;
