import React, { useState, useEffect } from "react";
import { Routes, Route } from "react-router-dom";
import Navbar from "./components/Navbar";
import Home from "./components/Home";
import Profile from "./components/Profile";
import Login from "./components/Login";
import Register from "./components/Register";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faMoon, faSun } from "@fortawesome/free-solid-svg-icons";

function App() {
  const [isDarkMode, setIsDarkMode] = useState(() => {
    const savedTheme = localStorage.getItem("theme");
    return savedTheme ? savedTheme === "dark" : false;
  });

  useEffect(() => {
    if (isDarkMode) {
      document.documentElement.classList.add("dark");
      localStorage.setItem("theme", "dark");
    } else {
      document.documentElement.classList.remove("dark");
      localStorage.setItem("theme", "light");
    }
  }, [isDarkMode]);

  const toggleDarkMode = () => {
    setIsDarkMode((prev) => !prev);
  };

  return (
    <div className="flex flex-col min-h-screen">
      <style>
        {`
          :root {
            --background-light: linear-gradient(to bottom right, #f9fafb, #dbeafe);
            --background-dark: linear-gradient(to bottom right, #1f2937, #4b5e82);
            --card-bg-light: linear-gradient(to bottom right, #eff6ff, #e0e7ff);
            --card-bg-dark: linear-gradient(to bottom right, #374151, #4b5e82);
            --navbar-bg-light: linear-gradient(to right, #1d4ed8, #4f46e5);
            --navbar-bg-dark: linear-gradient(to right, #1e3a8a, #3730a3);
            --text-light: #1f2937;
            --text-dark: #e5e7eb;
            --text-secondary-light: #4b5563;
            --text-secondary-dark: #9ca3af;
            --border-light: #bfdbfe;
            --border-dark: #4b5e82;
            --error-bg-light: #fee2e2;
            --error-bg-dark: #7f1d1d;
            --error-text-light: #b91c1c;
            --error-text-dark: #fecaca;
            --error-border-light: linear-gradient(to right, #fecaca, #f87171);
            --error-border-dark: linear-gradient(to right, #b91c1c, #dc2626);
            --success-bg-light: #dcfce7;
            --success-bg-dark: #14532d;
            --success-text-light: #15803d;
            --success-text-dark: #86efac;
            --button-bg-light: linear-gradient(to right, #2563eb, #4f46e5);
            --button-bg-dark: linear-gradient(to right, #3b82f6, #6366f1);
            --button-hover-light: #1d4ed8;
            --button-hover-dark: #60a5fa;
            --shadow-light: 0 0 10px rgba(59, 130, 246, 0.5);
            --shadow-dark: 0 0 10px rgba(96, 165, 250, 0.3);
          }
          .dark {
            background: var(--background-dark);
            color: var(--text-dark);
          }
          @keyframes fadeInUp {
            0% { opacity: 0; transform: translateY(20px); }
            100% { opacity: 1; transform: translateY(0); }
          }
          .animate-fade-in-up {
            animation: fadeInUp 0.5s ease-out forwards;
          }
          .hover-scale {
            transition: transform 0.2s ease-in-out;
          }
          .hover-scale:hover {
            transform: scale(1.05);
          }
          .social-icon {
            transition: all 0.3s ease-in-out;
          }
          .social-icon:hover {
            transform: scale(1.2);
            filter: brightness(1.2);
          }
          .theme-toggle {
            position: fixed;
            bottom: 20px;
            right: 20px;
            z-index: 1000;
            background: var(--button-bg-light);
            color: #ffffff;
            border-radius: 50%;
            width: 48px;
            height: 48px;
            display: flex;
            align-items: center;
            justify-content: center;
            box-shadow: var(--shadow-light);
            transition: all 0.3s ease-in-out;
          }
          .dark .theme-toggle {
            background: var(--button-bg-dark);
            box-shadow: var(--shadow-dark);
          }
          .theme-toggle:hover {
            transform: scale(1.1);
            box-shadow: 0 0 15px rgba(59, 130, 246, 0.7);
          }
        `}
      </style>
      <Navbar />
      <main className="container mx-auto px-4 py-8 flex-grow">
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/profile" element={<Profile />} />
          <Route path="/login" element={<Login />} />
          <Route path="/register" element={<Register />} />
        </Routes>
      </main>
      <footer className="bg-gradient-to-r from-blue-600 to-indigo-600 dark:bg-gradient-to-r dark:from-blue-800 dark:to-indigo-800 text-white p-6 shadow-lg border-t border-blue-200 dark:border-blue-600">
        <div className="container mx-auto flex flex-col md:flex-row items-center justify-between animate-fade-in-up">
          <p className="bg-clip-text bg-gradient-to-r from-blue-200 to-indigo-200 dark:from-blue-400 dark:to-indigo-400">
            Â© 2025 NexusEd. All rights reserved.
          </p>
          <div className="md:-ml-40">
            <p className="text-sm mt-2 md:mt-0 bg-clip-text text-transparent bg-gradient-to-r from-blue-100 to-indigo-100 dark:from-blue-300 dark:to-indigo-300">
              Powered by NexusEd
            </p>
          </div>
          <div className="flex space-x-4 mt-4 md:mt-0">
            <a href="#" className="social-icon" aria-label="Facebook">
              <svg
                className="w-6 h-6 fill-current text-white"
                viewBox="0 0 24 24"
              >
                <path d="M12 2.04c-5.5 0-9.96 4.46-9.96 9.96 0 4.99 3.66 9.12 8.44 9.88v-6.98h-2.54v-2.9h2.54v-2.2c0-2.5 1.5-3.88 3.78-3.88 1.1 0 2.24.2 2.24.2v2.46h-1.26c-1.24 0-1.62.77-1.62 1.56v1.86h2.84l-.46 2.9h-2.38v6.98c4.78-.76 8.44-4.89 8.44-9.88 0-5.5-4.46-9.96-9.96-9.96z" />
              </svg>
            </a>
            <a href="#" className="social-icon" aria-label="Twitter">
              <svg
                className="w-6 h-6 fill-current text-white"
                viewBox="0 0 24 24"
              >
                <path d="M23.95 4.57a9.97 9.97 0 0 1-2.83 1.3 4.93 4.93 0 0 0-8.38 4.5 13.98 13.98 0 0 1-10.16-5.15 4.93 4.93 0 0 0 1.52 6.58 4.9 4.9 0 0 1-2.23-.62v.06a4.93 4.93 0 0 0 3.95 4.84 4.9 4.9 0 0 1-2.22.08 4.93 4.93 0 0 0 4.6 3.42 9.87 9.87 0 0 1-6.1 2.1c-.4 0-.8-.02-1.18-.07a13.94 13.94 0 0 0 7.55 2.2c9.05 0 14-7.5 14-14v-.64a9.96 9.96 0 0 0 2.48-2.54 9.87 9.87 0 0 1-2.8.77z" />
              </svg>
            </a>
            <a href="#" className="social-icon" aria-label="Instagram">
              <svg
                className="w-6 h-6 fill-current text-white"
