import React, { useState, useEffect, useContext, useRef } from "react";
import PostCard from "./PostCard";
import { AuthContext } from "../context/AuthContext";
import axios from "axios";

function Profile() {
  const { user, setUser } = useContext(AuthContext);
  const [posts, setPosts] = useState([]);
  const [bio, setBio] = useState("");
  const [isEditing, setIsEditing] = useState(false);
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const timeoutRef = useRef(null);

  const setTemporaryMessage = (type, message) => {
    if (timeoutRef.current) {
      clearTimeout(timeoutRef.current);
    }
    if (type === "error") {
      setError(message);
      setSuccess("");
    } else {
      setSuccess(message);
      setError("");
    }
    timeoutRef.current = setTimeout(() => {
      setError("");
      setSuccess("");
      timeoutRef.current = null;
    }, 3000);
  };

  useEffect(() => {
    return () => {
      if (timeoutRef.current) {
        clearTimeout(timeoutRef.current);
      }
    };
  }, []);

  useEffect(() => {
    if (user) {
      setBio(user.bio || "");
      const fetchUserPosts = async () => {
        try {
          const response = await fetch(
            `${process.env.REACT_APP_API_URL}/api/posts?author=${user.id}`
          );
          if (!response.ok) {
            throw new Error(`Failed to fetch user posts: ${response.status}`);
          }
          const data = await response.json();
          setPosts(data);
        } catch (err) {
          setTemporaryMessage("error", "Failed to fetch posts");
        }
      };
      fetchUserPosts();
    }
  }, [user]);

  const handleBioUpdate = async (e) => {
    e.preventDefault();
    setError("");
    setSuccess("");
    setIsLoading(true);

    if (!bio.trim()) {
      setTemporaryMessage("error", "Bio cannot be empty");
      setIsLoading(false);
      return;
    }

    try {
      const token = localStorage.getItem("token");
      if (!token) {
        throw new Error("No authentication token found. Please log in again.");
      }
      if (typeof setUser !== "function") {
        throw new Error(
          "setUser is not a function. Ensure AuthProvider wraps the app."
        );
      }

      const response = await axios.put(
        `${process.env.REACT_APP_API_URL}/api/users/profile`,
        { bio },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      const updatedUser = {
        id: response.data.id,
        username: response.data.username,
        email: response.data.email,
        bio: response.data.bio,
      };
      setUser(updatedUser);
      setIsEditing(false);
      setTemporaryMessage("success", "Bio updated successfully");
    } catch (err) {
      const errorMessage =
        err.response?.data?.message || err.message || "Failed to update bio";
      setTemporaryMessage("error", errorMessage);
    } finally {
      setIsLoading(false);
    }
  };

  if (!user) {
    return (
      <div className="text-gray-600 text-center py-10">
        Please log in to view your profile.
      </div>
    );
  }

  return (
    <div>
      <div className="bg-white p-6 rounded-lg shadow-md mb-8">
        <div className="flex items-center space-x-4 mb-6">
          <div className="w-12 h-12 bg-blue-600 text-white flex items-center justify-center rounded-full text-xl font-semibold">
            {user.username.charAt(0).toUpperCase()}
          </div>
          <div>
            <h2 className="text-2xl font-semibold text-gray-800">
              {user.username}
            </h2>
            <p className="text-gray-500 text-sm">{user.email}</p>
          </div>
        </div>
        {isEditing ? (
          <form onSubmit={handleBioUpdate}>
            <textarea
              value={bio}
              onChange={(e) => setBio(e.target.value)}
              placeholder="Update your bio"
              className="w-full p-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent transition duration-150 mb-4"
              rows="4"
              aria-label="Bio"
            />
            {error && (
              <div className="bg-red-100 text-red-700 p-3 mb-4 rounded-md message">
                {error}
              </div>
            )}
            <div className="flex space-x-3">
              <button
                type="submit"
                className="bg-blue-600 text-white px-4 py-2 rounded-md hover:bg-blue-700 transition duration-200 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2 disabled:opacity-50"
                disabled={isLoading}
                aria-label="Save Bio"
              >
                {isLoading ? "Saving..." : "Save"}
              </button>
              <button
                type="button"
                onClick={() => {
                  setIsEditing(false);
                  setBio(user.bio || "");
                  setError("");
                  setSuccess("");
                }}
                className="bg-gray-300 text-gray-800 px-4 py-2 rounded-md hover:bg-gray-400 transition duration-200 focus:outline-none focus:ring-2 focus:ring-gray-400 focus:ring-offset-2 disabled:opacity-50"
                disabled={isLoading}
                aria-label="Cancel"
              >
                Cancel
              </button>
            </div>
          </form>
        ) : (
          <>
            <p className="text-gray-600 mb-4">{user.bio || "No bio set"}</p>
            <button
              onClick={() => setIsEditing(true)}
              className="bg-blue-600 text-white px-4 py-2 rounded-md hover:bg-blue-700 transition duration-200 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2"
              aria-label="Edit Bio"
            >
              Edit Bio
            </button>
            {success && (
              <div className="bg-green-100 text-green-700 p-3 mt-4 rounded-md message">
                {success}
              </div>
            )}
          </>
        )}
      </div>
      <h3 className="text-xl font-semibold text-gray-800 mb-6">My Posts</h3>
      <div className="space-y-6">
        {posts.length > 0 ? (
          posts.map((post) => (
            <PostCard
              key={post._id}
              id={post._id}
              title={post.title}
              content={post.content}
              author={post.author?.username || "Unknown"}
              comments={post.comments}
              likes={post.likes}
              post={post}
              setPosts={setPosts}
              currentUser={user}
            />
          ))
        ) : (
          <p className="text-gray-600 text-center">No posts yet.</p>
        )}
      </div>
    </div>
  );
}

export default Profile;
