import React, { useContext, useState } from "react";
import { NavLink, useNavigate } from "react-router-dom";
import { AuthContext } from "../context/AuthContext";

function Navbar() {
  const { user, logout } = useContext(AuthContext);
  const navigate = useNavigate();
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const [isFading, setIsFading] = useState(false);

  const handleLogout = () => {
    setIsFading(true);
    setTimeout(() => {
      logout();
      navigate("/");
      setIsMobileMenuOpen(false);
      setIsFading(false);
    }, 500); // Match the duration of the fadeOut animation
  };

  const toggleMobileMenu = () => {
    setIsMobileMenuOpen((prev) => !prev);
  };

  return (
    <nav
      className={`bg-gradient-to-r from-blue-700 to-indigo-600 p-4 shadow-lg shadow-inner sticky top-0 z-50 ${
        isFading ? "fade-out" : ""
      }`}
      role="navigation"
    >
      <style>
        {`
          @keyframes pulse {
            0% { transform: scale(1); }
            50% { transform: scale(1.03); }
            100% { transform: scale(1); }
          }
          .nav-active {
            animation: pulse 1.5s infinite;
          }
          @keyframes fadeOut {
            0% { opacity: 1; }
            100% { opacity: 0; }
          }
          .fade-out {
            animation: fadeOut 0.5s ease-in-out forwards;
          }
        `}
      </style>
      <div className="container mx-auto flex justify-between items-center">
        {/* Logo */}
        <NavLink
          to="/"
          className="text-white text-2xl font-bold transform"
          aria-label="NexusEd Home"
        >
          NexusEd
        </NavLink>

        {/* Hamburger Icon for Mobile */}
        <button
          className="md:hidden text-white focus:outline-none focus:ring-2 focus:ring-blue-200 rounded-md"
          onClick={toggleMobileMenu}
          aria-expanded={isMobileMenuOpen}
          aria-controls="mobile-menu"
          aria-label="Toggle navigation menu"
        >
          <svg
            className="w-6 h-6"
            fill="none"
            stroke="currentColor"
            viewBox="0 0 24 24"
            xmlns="http://www.w3.org/2000/svg"
          >
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth="2"
              d={
                isMobileMenuOpen
                  ? "M6 18L18 6M6 6l12 12"
                  : "M4 6h16M4 12h16M4 18h16"
              }
            />
          </svg>
        </button>

        {/* Desktop Menu */}
        <div className="hidden md:flex md:space-x-6">
          <NavLink
            to="/"
            className={({ isActive }) =>
              `relative text-white px-3 py-1 rounded-md transition-all duration-300 ease-in-out ${
                isActive
                  ? "bg-blue-300 text-white shadow-[0_0_10px_rgba(59,130,246,0.5)] nav-active"
                  : "hover:text-blue-100 hover:shadow-[0_0_5px_rgba(59,130,246,0.3)] after:absolute after:bottom-0 after:left-0 after:w-0 after:h-1 after:bg-blue-300 after:transition-all after:duration-300 hover:after:w-full"
              }`
            }
            aria-label="Home"
          >
            Home
          </NavLink>
          {user ? (
            <>
              <NavLink
                to="/profile"
                className={({ isActive }) =>
                  `relative text-white px-3 py-1 rounded-md transition-all duration-300 ease-in-out ${
                    isActive
                      ? "bg-blue-300 text-white shadow-[0_0_10px_rgba(59,130,246,0.5)] nav-active"
                      : "hover:text-blue-100 hover:shadow-[0_0_5px_rgba(59,130,246,0.3)] after:absolute after:bottom-0 after:left-0 after:w-0 after:h-1 after:bg-blue-300 after:transition-all after:duration-300 hover:after:w-full"
                  }`
                }
                aria-label="Profile"
              >
                Profile
              </NavLink>
              <button
                type="button"
                onClick={handleLogout}
                className="bg-red-600 text-white px-3 py-1 rounded-md hover:bg-red-500 hover:shadow-[0_0_10px_rgba(239,68,68,0.5)] transition-all duration-300 ease-in-out focus:outline-none focus:ring-2 focus:ring-red-500 focus:ring-offset-2"
                aria-label="Logout"
              >
                Logout
              </button>
            </>
          ) : (
            <>
              <NavLink
                to="/login"
                className={({ isActive }) =>
                  `relative text-white px-3 py-1 rounded-md transition-all duration-300 ease-in-out ${
                    isActive
                      ? "bg-blue-300 text-white shadow-[0_0_10px_rgba(59,130,246,0.5)] nav-active"
                      : "hover:text-blue-100 hover:shadow-[0_0_5px_rgba(59,130,246,0.3)] after:absolute after:bottom-0 after:left-0 after:w-0 after:h-1 after:bg-blue-300 after:transition-all after:duration-300 hover:after:w-full"
                  }`
                }
                aria-label="Login"
              >
                Login
              </NavLink>
              <NavLink
                to="/register"
                className={({ isActive }) =>
                  `relative text-white px-3 py-1 rounded-md transition-all duration-300 ease-in-out ${
                    isActive
                      ? "bg-blue-300 text-white shadow-[0_0_10px_rgba(59,130,246,0.5)] nav-active"
                      : "hover:text-blue-100 hover:shadow-[0_0_5px_rgba(59,130,246,0.3)] after:absolute after:bottom-0 after:left-0 after:w-0 after:h-1 after:bg-blue-300 after:transition-all after:duration-300 hover:after:w-full"
                  }`
                }
                aria-label="Register"
              >
                Register
              </NavLink>
            </>
          )}
        </div>
      </div>

      {/* Mobile Menu */}
      {isMobileMenuOpen && (
        <div id="mobile-menu" className="md:hidden mt-4 space-y-3">
          <NavLink
            to="/"
            className={({ isActive }) =>
              `block text-white px-4 py-2 rounded-md transition-all duration-300 ease-in-out ${
                isActive
                  ? "bg-blue-300 text-white shadow-[0_0_10px_rgba(59,130,246,0.5)] nav-active"
                  : "hover:bg-blue-600 hover:text-blue-100 hover:shadow-[0_0_5px_rgba(59,130,246,0.3)] relative after:absolute after:bottom-0 after:left-0 after:w-0 after:h-1 after:bg-blue-300 after:transition-all after:duration-300 hover:after:w-full"
              }`
            }
            onClick={() => setIsMobileMenuOpen(false)}
            aria-label="Home"
          >
            Home
          </NavLink>
          {user ? (
            <>
              <NavLink
                to="/profile"
                className={({ isActive }) =>
                  `block text-white px-4 py-2 rounded-md transition-all duration-300 ease-in-out ${
                    isActive
                      ? "bg-blue-300 text-white shadow-[0_0_10px_rgba(59,130,246,0.5)] nav-active"
                      : "hover:bg-blue-600 hover:text-blue-100 hover:shadow-[0_0_5px_rgba(59,130,246,0.3)] relative after:absolute after:bottom-0 after:left-0 after:w-0 after:h-1 after:bg-blue-300 after:transition-all after:duration-300 hover:after:w-full"
                  }`
                }
                onClick={() => setIsMobileMenuOpen(false)}
                aria-label="Profile"
              >
                Profile
              </NavLink>
              <button
                type="button"
                onClick={handleLogout}
                className="block w-full text-left text-white px-4 py-2 rounded-md bg-red-600 hover:bg-red-500 hover:shadow-[0_0_10px_rgba(239,68,68,0.5)] transition-all duration-300 ease-in-out focus:outline-none focus:ring-2 focus:ring-red-200 focus:ring-offset-2"
                aria-label="Logout"
              >
                Logout
              </button>
            </>
          ) : (
            <>
              <NavLink
                to="/login"
                className={({ isActive }) =>
                  `block text-white px-4 py-2 rounded-md transition-all duration-300 ease-in-out ${
                    isActive
                      ? "bg-blue-300 text-white shadow-[0_0_10px_rgba(59,130,246,0.5)] nav-active"
                      : "hover:bg-blue-600 hover:text-blue-100 hover:shadow-[0_0_5px_rgba(59,130,246,0.3)] relative after:absolute after:bottom-0 after:left-0 after:w-0 after:h-1 after:bg-blue-300 after:transition-all after:duration-300 hover:after:w-full"
                  }`
                }
                onClick={() => setIsMobileMenuOpen(false)}
                aria-label="Login"
              >
                Login
              </NavLink>
              <NavLink
                to="/register"
                className={({ isActive }) =>
                  `block text-white px-4 py-2 rounded-md transition-all duration-300 ease-in-out ${
                    isActive
                      ? "bg-blue-300 text-white shadow-[0_0_10px_rgba(59,130,246,0.5)] nav-active"
                      : "hover:bg-blue-600 hover:text-blue-100 hover:shadow-[0_0_5px_rgba(59,130,246,0.3)] relative after:absolute after:bottom-0 after:left-0 after:w-0 after:h-1 after:bg-blue-300 after:transition-all after:duration-300 hover:after:w-full"
                  }`
                }
                onClick={() => setIsMobileMenuOpen(false)}
                aria-label="Register"
              >
                Register
              </NavLink>
            </>
          )}
        </div>
      )}
    </nav>
  );
}

export default Navbar;
