import React, { useState, useEffect, useContext, useRef } from "react";
import PostCard from "./PostCard";
import { AuthContext } from "../context/AuthContext";
import axios from "axios";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faExclamationCircle,
  faCheckCircle,
  faPlus,
} from "@fortawesome/free-solid-svg-icons";

function Profile() {
  const { user, setUser, isLoading: authLoading } = useContext(AuthContext);
  const [posts, setPosts] = useState([]);
  const [isPostsLoading, setIsPostsLoading] = useState(false);
  const [bio, setBio] = useState("");
  const [username, setUsername] = useState("");
  const [profilePicture, setProfilePicture] = useState(null);
  const [isEditing, setIsEditing] = useState(false);
  const [error, setError] = useState("");
  const [uploadError, setUploadError] = useState("");
  const [success, setSuccess] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const [sortOption, setSortOption] = useState("newest");
  const [imageLoadError, setImageLoadError] = useState(false);
  const timeoutRef = useRef(null);
  const uploadTimeoutRef = useRef(null);
  const fileInputRef = useRef(null);
  const BIO_MAX_LENGTH = 150;
  const USERNAME_MIN_LENGTH = 3;
  const USERNAME_MAX_LENGTH = 20;
  const DEFAULT_BIO =
    "A student passionate about learning and sharing knowledge.";

  const setTemporaryMessage = (type, message) => {
    if (timeoutRef.current) {
      clearTimeout(timeoutRef.current);
    }
    if (type === "error") {
      setError(message);
      setSuccess("");
    } else {
      setSuccess(message);
      setError("");
    }
    timeoutRef.current = setTimeout(() => {
      setError("");
      setSuccess("");
      timeoutRef.current = null;
    }, 3000);
  };

  const setTemporaryUploadError = (message) => {
    if (uploadTimeoutRef.current) {
      clearTimeout(uploadTimeoutRef.current);
    }
    setUploadError(message);
    uploadTimeoutRef.current = setTimeout(() => {
      setUploadError("");
      uploadTimeoutRef.current = null;
    }, 3000);
  };

  useEffect(() => {
    return () => {
      if (timeoutRef.current) {
        clearTimeout(timeoutRef.current);
      }
      if (uploadTimeoutRef.current) {
        clearTimeout(uploadTimeoutRef.current);
      }
    };
  }, []);

  useEffect(() => {
    if (user) {
      setBio(user.bio || DEFAULT_BIO);
      setUsername(user.username || "");
      setImageLoadError(false);

      const fetchUserProfile = async () => {
        try {
          const token = localStorage.getItem("token");
          const response = await axios.get(
            `${process.env.REACT_APP_API_URL}/api/users/profile`,
            { headers: { Authorization: `Bearer ${token}` } }
          );
          const updatedUser = {
            ...user,
            bio: response.data.bio,
            username: response.data.username,
            email: response.data.email,
            profilePicture: response.data.profilePicture,
          };
          setUser(updatedUser);
          setBio(response.data.bio || DEFAULT_BIO);
          setUsername(response.data.username || "");
          console.log("Profile picture URL:", response.data.profilePicture);
        } catch (err) {
          console.error("Failed to fetch user profile:", err.message);
          setTemporaryMessage("error", "Failed to fetch user profile");
        }
      };

      const fetchUserPosts = async () => {
        setIsPostsLoading(true);
        try {
          const response = await fetch(
            `${process.env.REACT_APP_API_URL}/api/posts?author=${user.id}`
          );
          if (!response.ok) {
            throw new Error(`Failed to fetch user posts: ${response.status}`);
          }
          const data = await response.json();
          setPosts(data);
        } catch (err) {
          setTemporaryMessage("error", "Failed to fetch posts");
        } finally {
          setIsPostsLoading(false);
        }
      };

      fetchUserProfile();
      fetchUserPosts();
    }
  }, [user?.id, setUser]);

  const handleProfilePictureChange = async (e) => {
    const file = e.target.files[0];
    if (!file) return;

    if (!file.type.startsWith("image/")) {
      setTemporaryUploadError("Please upload an image file");
      return;
    }

    if (file.size > 5 * 1024 * 1024) {
      setTemporaryUploadError("Image size must be less than 5MB");
      return;
    }

    const formData = new FormData();
    formData.append("profilePicture", file);

    try {
      setIsLoading(true);
      const token = localStorage.getItem("token");
      const response = await axios.post(
        `${process.env.REACT_APP_API_URL}/api/users/upload-profile-picture`,
        formData,
        {
          headers: {
            "Content-Type": "multipart/form-data",
            Authorization: `Bearer ${token}`,
          },
        }
      );

      const profileResponse = await axios.get(
        `${process.env.REACT_APP_API_URL}/api/users/profile`,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      const updatedUser = {
        ...user,
        bio: profileResponse.data.bio,
        username: profileResponse.data.username,
        email: profileResponse.data.email,
        profilePicture: profileResponse.data.profilePicture,
      };
      setUser(updatedUser);
      setImageLoadError(false);
      setTemporaryMessage("success", "Profile picture updated successfully");
    } catch (err) {
      const errorMessage =
        err.response?.data?.message ||
        err.message ||
        "Failed to upload profile picture";
      setTemporaryMessage("error", errorMessage);
    } finally {
      setIsLoading(false);
      setProfilePicture(null);
      if (fileInputRef.current) {
        fileInputRef.current.value = null;
      }
    }
  };

  const handleBioUpdate = async (e) => {
    e.preventDefault();
    setError("");
    setSuccess("");
    setIsLoading(true);

    if (!bio.trim()) {
      setTemporaryMessage("error", "Bio cannot be empty");
      setIsLoading(false);
      return;
    }

    if (bio.length > BIO_MAX_LENGTH) {
      setTemporaryMessage(
        "error",
        `Bio cannot exceed ${BIO_MAX_LENGTH} characters`
      );
      setIsLoading(false);
      return;
    }

    if (
      username.length < USERNAME_MIN_LENGTH ||
      username.length > USERNAME_MAX_LENGTH
    ) {
      setTemporaryMessage(
        "error",
        `Username must be between ${USERNAME_MIN_LENGTH} and ${USERNAME_MAX_LENGTH} characters`
      );
      setIsLoading(false);
      return;
    }

    if (!/^[a-zA-Z0-9_]+$/.test(username)) {
      setTemporaryMessage(
        "error",
        "Username can only contain letters, numbers, and underscores"
      );
      setIsLoading(false);
      return;
    }

    try {
      const token = localStorage.getItem("token");
      if (!token) {
        throw new Error("No authentication token found. Please log in again.");
      }
      if (typeof setUser !== "function") {
        throw new Error(
          "setUser is not a function. Ensure AuthProvider wraps the app."
        );
      }

      const response = await axios.put(
        `${process.env.REACT_APP_API_URL}/api/users/profile`,
        { bio, username },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      const updatedUser = {
        id: response.data.id,
        username: response.data.username,
        email: response.data.email,
        bio: response.data.bio,
        profilePicture: user.profilePicture || response.data.profilePicture,
      };
      setUser(updatedUser);
      setIsEditing(false);
      setTemporaryMessage("success", "Profile updated successfully");
    } catch (err) {
      const errorMessage =
        err.response?.data?.message ||
        err.message ||
        "Failed to update profile";
      setTemporaryMessage("error", errorMessage);
    } finally {
      setIsLoading(false);
    }
  };

  const sortPosts = (posts) => {
    const sortedPosts = [...posts];
    switch (sortOption) {
      case "newest":
        return sortedPosts.sort(
          (a, b) => new Date(b.createdAt) - new Date(a.createdAt)
        );
      case "oldest":
        return sortedPosts.sort(
          (a, b) => new Date(a.createdAt) - new Date(b.createdAt)
        );
      case "mostLikes":
        return sortedPosts.sort(
          (a, b) => (b.likes?.length || 0) - (a.likes?.length || 0)
        );
      case "leastLikes":
        return sortedPosts.sort(
          (a, b) => (a.likes?.length || 0) - (b.likes?.length || 0)
        );
      default:
        return sortedPosts;
    }
  };

  const totalLikes = posts.reduce(
    (sum, post) => sum + (post.likes?.length || 0),
    0
  );

  if (authLoading) {
    return (
      <div className="text-gray-600 py-10 animate-pulse">
        Loading profile...
      </div>
    );
  }

  if (!user) {
    return (
      <div className="text-gray-600 py-10 animate-pulse">
        Please log in to view your profile.
      </div>
    );
  }

  const profilePictureUrl = user.profilePicture
    ? `${process.env.REACT_APP_API_URL}${user.profilePicture}`
    : null;

  return (
    <div className="min-h-screen bg-gray-50">
      <style>
        {`
          @keyframes fadeInUp {
            0% { opacity: 0; transform: translateY(20px); }
            100% { opacity: 1; transform: translateY(0); }
          }
          .animate-fade-in-up {
            animation: fadeInUp 0.5s ease-out forwards;
          }
          .hover-underline::after {
            content: '';
            position: absolute;
            width: 0;
            height: 2px;
            bottom: 0;
            left: 0;
            background: linear-gradient(to right, #2563eb, #4f46e5);
            transition: width 0.3s ease-in-out;
          }
          .hover-underline:hover::after {
            width: 100%;
          }
        `}
      </style>
      <div className="bg-gradient-to-br from-blue-50 to-indigo-50 p-8 rounded-xl shadow-xl border border-blue-100 mb-8 animate-fade-in-up">
        <div className="flex flex-col sm:flex-row items-start space-y-6 sm:space-y-0 sm:space-x-8 mb-6">
          <div className="relative">
            {profilePictureUrl && !imageLoadError ? (
              <img
                src={profilePictureUrl}
                alt={`${user.username}'s profile picture`}
                className="w-50 h-28 rounded-full object-cover border-4 border-gradient-to-r from-blue-600 to-indigo-600 shadow-[0_0_10px_rgba(59,130,246,0.3)] transition-transform duration-300 hover:scale-105"
                aria-label={`${user.username}'s profile picture`}
                onError={() => setImageLoadError(true)}
              />
            ) : (
              <div
                className="w-28 h-28 bg-gradient-to-r from-blue-600 to-indigo-600 text-white flex items-center justify-center rounded-full text-4xl font-bold border-4 border-gradient-to-r from-blue-600 to-indigo-600 shadow-[0_0_10px_rgba(59,130,246,0.3)] transition-transform duration-300 hover:scale-105"
                aria-label={`${user.username}'s avatar`}
              >
                {user.username.charAt(0).toUpperCase()}
              </div>
            )}
            <button
              onClick={() => fileInputRef.current?.click()}
              className="absolute bottom-0 right-0 bg-gradient-to-r from-blue-600 to-indigo-600 text-white p-3 rounded-full hover:bg-blue-700 hover:scale-110 hover:shadow-[0_0_10px_rgba(59,130,246,0.5)] transition-all duration-200 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2"
              aria-label="Upload profile picture"
              disabled={isLoading}
            >
              <FontAwesomeIcon icon={faPlus} size="lg" />
            </button>
            <input
              type="file"
              ref={fileInputRef}
              onChange={handleProfilePictureChange}
              accept="image/*"
              className="hidden"
              aria-label="Profile picture upload"
            />
            {uploadError && (
              <div className="absolute top-full mt-3 bg-red-100 text-red-700 p-2 rounded-md flex items-center space-x-2 text-sm w-48 shadow-md">
                <FontAwesomeIcon icon={faExclamationCircle} />
                <span>{uploadError}</span>
              </div>
            )}
          </div>
          <div className="w-full">
            {isEditing ? (
              <>
                <input
                  type="text"
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                  placeholder="Update your username"
                  className="w-full p-3 bg-gradient-to-r from-gray-50 to-blue-50 border border-gray-200 rounded-lg focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-gradient-to-r from-blue-500 to-indigo-500 transition duration-200 mb-2 text-2xl font-bold text-gray-800 shadow-sm"
                  maxLength={USERNAME_MAX_LENGTH}
                  aria-label="Username"
                />
                <p className="text-gray-500 text-sm mb-2">
                  {username.length}/{USERNAME_MAX_LENGTH} characters
                </p>
              </>
            ) : (
              <h2 className="relative text-3xl font-bold bg-clip-text text-transparent bg-gradient-to-r from-blue-600 to-indigo-600 hover-underline transition-all duration-300">
                {user.username}
              </h2>
            )}
            <p className="text-sm bg-clip-text text-transparent bg-gradient-to-r from-gray-500 to-gray-700 mt-1">
              {user.email}
            </p>
            <div className="flex flex-wrap space-x-4 mt-3 text-sm text-gray-600">
              <p className="font-semibold transition-transform duration-200 hover:scale-105">
                Posts: {posts.length}
              </p>
              <p className="font-semibold transition-transform duration-200 hover:scale-105">
                Total Likes: {totalLikes}
              </p>
              {user.createdAt && (
                <p className="font-semibold transition-transform duration-200 hover:scale-105">
                  Joined: {new Date(user.createdAt).toLocaleDateString()}
                </p>
              )}
            </div>
          </div>
        </div>
        {isEditing ? (
          <form onSubmit={handleBioUpdate} className="animate-fade-in">
            <textarea
              value={bio}
              onChange={(e) => setBio(e.target.value)}
              placeholder="Update your bio"
              className="w-full p-3 bg-gradient-to-r from-gray-50 to-blue-50 border border-gray-200 rounded-lg focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-gradient-to-r from-blue-500 to-indigo-500 transition duration-200 mb-2 text-gray-700 shadow-sm"
              rows="4"
              maxLength={BIO_MAX_LENGTH}
              aria-label="Bio"
            />
            <p className="text-gray-500 text-sm mb-2">
              {bio.length}/{BIO_MAX_LENGTH} characters
            </p>
            {error && (
              <div className="bg-red-100 text-red-700 p-3 mb-4 rounded-md flex items-center space-x-2 shadow-md">
                <FontAwesomeIcon icon={faExclamationCircle} />
                <span>{error}</span>
              </div>
            )}
            <div className="flex space-x-3">
              <button
                type="submit"
                className="bg-gradient-to-r from-blue-600 to-indigo-600 text-white px-5 py-2 rounded-lg hover:bg-blue-700 hover:shadow-[0_0_10px_rgba(59,130,246,0.5)] hover:scale-105 transition-all duration-200 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2 disabled:opacity-50"
                disabled={isLoading}
                aria-label="Save Profile"
              >
                {isLoading ? "Saving..." : "Save"}
              </button>
              <button
                type="button"
                onClick={() => {
                  setIsEditing(false);
                  setBio(user.bio || DEFAULT_BIO);
                  setUsername(user.username || "");
                  setError("");
                  setSuccess("");
                }}
                className="bg-gradient-to-r from-gray-200 to-gray-300 text-gray-800 px-5 py-2 rounded-lg hover:bg-gray-400 hover:shadow-[0_0_5px_rgba(107,114,128,0.5)] hover:scale-105 transition-all duration-200 focus:outline-none focus:ring-2 focus:ring-gray-400 focus:ring-offset-2 disabled:opacity-50"
                aria-label="Cancel"
              >
                Cancel
              </button>
            </div>
          </form>
        ) : (
          <>
            <p className="text-gray-700 mb-4 italic">
              {user.bio || DEFAULT_BIO}
            </p>
            <button
              onClick={() => setIsEditing(true)}
              className="bg-gradient-to-r from-blue-600 to-indigo-600 text-white px-5 py-2 rounded-lg hover:bg-blue-700 hover:shadow-[0_0_10px_rgba(59,130,246,0.5)] hover:scale-105 transition-all duration-200 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2"
              aria-label="Edit Profile"
            >
              Edit Profile
            </button>
            {success && (
              <div className="bg-green-100 text-green-700 p-3 mt-4 rounded-md flex items-center space-x-2 shadow-md">
                <FontAwesomeIcon icon={faCheckCircle} />
                <span>{success}</span>
              </div>
            )}
          </>
        )}
      </div>

      <div className="flex justify-between items-center mb-6 animate-fade-in-up">
        <h3 className="relative text-2xl font-bold bg-clip-text text-transparent bg-gradient-to-r from-blue-600 to-indigo-600 hover-underline">
          My Posts
        </h3>
        <div>
          <label htmlFor="sort-posts" className="text-gray-600 mr-2">
            Sort by:
          </label>
          <select
            id="sort-posts"
            value={sortOption}
            onChange={(e) => setSortOption(e.target.value)}
            className="border-2 border-gradient-to-r from-blue-300 to-indigo-300 rounded-lg p-2 bg-white shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-500 hover:shadow-[0_0_5px_rgba(59,130,246,0.3)] transition-all duration-200"
            aria-label="Sort posts"
          >
            <option value="newest">Newest</option>
            <option value="oldest">Oldest</option>
            <option value="mostLikes">Most Likes</option>
            <option value="leastLikes">Least Likes</option>
          </select>
        </div>
      </div>

      <div className="space-y-6 animate-fade-in-up">
        {isPostsLoading ? (
          <div className="text-gray-600 py-10">
            <div className="inline-block animate-spin rounded-full h-8 w-8 border-t-2 border-b-2 border-blue-600"></div>
            <p className="mt-2">Loading posts...</p>
          </div>
        ) : sortPosts(posts).length > 0 ? (
          sortPosts(posts).map((post) => (
            <PostCard
              key={post._id}
              id={post._id}
              title={post.title}
              content={post.content}
              author={post.author?.username || "Unknown"}
              comments={post.comments}
              likes={post.likes}
              post={post}
              setPosts={setPosts}
              currentUser={user}
            />
          ))
        ) : (
          <p className="text-gray-600">No posts yet.</p>
        )}
      </div>
    </div>
  );
}

export default Profile;
