import React, { useState, useEffect, useContext, useRef } from "react";
import PostCard from "./PostCard";
import { AuthContext } from "../context/AuthContext";
import axios from "axios";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faExclamationCircle,
  faCheckCircle,
  faPlus,
} from "@fortawesome/free-solid-svg-icons";

function Profile() {
  const { user, setUser, isLoading: authLoading } = useContext(AuthContext);
  const [posts, setPosts] = useState([]);
  const [isPostsLoading, setIsPostsLoading] = useState(false);
  const [bio, setBio] = useState("");
  const [username, setUsername] = useState("");
  const [profilePicture, setProfilePicture] = useState(null);
  const [isEditing, setIsEditing] = useState(false);
  const [error, setError] = useState("");
  const [uploadError, setUploadError] = useState("");
  const [success, setSuccess] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const [sortOption, setSortOption] = useState("newest");
  const [imageLoadError, setImageLoadError] = useState(false);
  const timeoutRef = useRef(null);
  const uploadTimeoutRef = useRef(null);
  const fileInputRef = useRef(null);
  const BIO_MAX_LENGTH = 150;
  const USERNAME_MIN_LENGTH = 3;
  const USERNAME_MAX_LENGTH = 20;
  const DEFAULT_BIO =
    "A student passionate about learning and sharing knowledge.";

  const setTemporaryMessage = (type, message) => {
    if (timeoutRef.current) {
      clearTimeout(timeoutRef.current);
    }
    if (type === "error") {
      setError(message);
      setSuccess("");
    } else {
      setSuccess(message);
      setError("");
    }
    timeoutRef.current = setTimeout(() => {
      setError("");
      setSuccess("");
      timeoutRef.current = null;
    }, 3000);
  };

  const setTemporaryUploadError = (message) => {
    if (uploadTimeoutRef.current) {
      clearTimeout(uploadTimeoutRef.current);
    }
    setUploadError(message);
    uploadTimeoutRef.current = setTimeout(() => {
      setUploadError("");
      uploadTimeoutRef.current = null;
    }, 3000);
  };

  useEffect(() => {
    return () => {
      if (timeoutRef.current) {
        clearTimeout(timeoutRef.current);
      }
      if (uploadTimeoutRef.current) {
        clearTimeout(uploadTimeoutRef.current);
      }
    };
  }, []);

  useEffect(() => {
    if (user) {
      setBio(user.bio || DEFAULT_BIO);
      setUsername(user.username || "");
      setImageLoadError(false);

      const fetchUserProfile = async () => {
        try {
          const token = localStorage.getItem("token");
          const response = await axios.get(
            `${process.env.REACT_APP_API_URL}/api/users/profile`,
            {
              headers: { Authorization: `Bearer ${token}` },
            }
          );
          const updatedUser = {
            ...user,
            bio: response.data.bio,
            username: response.data.username,
            email: response.data.email,
            profilePicture: response.data.profilePicture,
            createdAt: response.data.createdAt,
          };
          setUser(updatedUser);
          setBio(response.data.bio || DEFAULT_BIO);
          setUsername(response.data.username || "");
          console.log("Profile picture URL:", response.data.profilePicture);
        } catch (err) {
          console.error("Failed to fetch user profile:", err.message);
          setTemporaryMessage("error", "Failed to fetch user profile");
        }
      };

      const fetchUserPosts = async () => {
        setIsPostsLoading(true);
        try {
          const response = await fetch(
            `${process.env.REACT_APP_API_URL}/api/posts?author=${user.id}`
          );
          if (!response.ok) {
            throw new Error(`Failed to fetch user posts: ${response.status}`);
          }
          const data = await response.json();
          setPosts(data);
        } catch (err) {
          setTemporaryMessage("error", "Failed to fetch posts");
        } finally {
          setIsPostsLoading(false);
        }
      };

      fetchUserProfile();
      fetchUserPosts();
    }
  }, [user?.id, setUser]);

  const handleProfilePictureChange = async (e) => {
    const file = e.target.files[0];
    if (!file) return;

    if (!file.type.startsWith("image/")) {
      setTemporaryUploadError("Please upload an image file");
      return;
    }

    if (file.size > 5 * 1024 * 1024) {
      setTemporaryUploadError("Image size must be less than 5MB");
      return;
    }

    const formData = new FormData();
    formData.append("profilePicture", file);

    try {
      setIsLoading(true);
      const token = localStorage.getItem("token");
      const response = await axios.post(
        `${process.env.REACT_APP_API_URL}/api/users/upload-profile-picture`,
        formData,
        {
          headers: {
            "Content-Type": "multipart/form-data",
            Authorization: `Bearer ${token}`,
          },
        }
      );

      const profileResponse = await axios.get(
        `${process.env.REACT_APP_API_URL}/api/users/profile`,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      const updatedUser = {
        ...user,
        bio: profileResponse.data.bio,
        username: profileResponse.data.username,
        email: profileResponse.data.email,
        profilePicture: profileResponse.data.profilePicture,
        createdAt: profileResponse.data.createdAt,
      };
      setUser(updatedUser);
      setImageLoadError(false);
      setTemporaryMessage("success", "Profile picture updated successfully");
    } catch (err) {
      const errorMessage =
        err.response?.data?.message ||
        err.message ||
        "Failed to upload profile picture";
      setTemporaryMessage("error", errorMessage);
    } finally {
      setIsLoading(false);
      setProfilePicture(null);
      if (fileInputRef.current) {
        fileInputRef.current.value = null;
      }
    }
  };

  const handleBioUpdate = async (e) => {
    e.preventDefault();
    setError("");
    setSuccess("");
    setIsLoading(true);

    if (!bio.trim()) {
      setTemporaryMessage("error", "Bio cannot be empty");
      setIsLoading(false);
      return;
    }

    if (bio.length > BIO_MAX_LENGTH) {
      setTemporaryMessage(
        "error",
        `Bio cannot exceed ${BIO_MAX_LENGTH} characters`
      );
      setIsLoading(false);
      return;
    }

    if (
      username.length < USERNAME_MIN_LENGTH ||
      username.length > USERNAME_MAX_LENGTH
    ) {
      setTemporaryMessage(
        "error",
        `Username must be between ${USERNAME_MIN_LENGTH} and ${USERNAME_MAX_LENGTH} characters`
      );
      setIsLoading(false);
      return;
    }

    if (!/^[a-zA-Z0-9_]+$/.test(username)) {
      setTemporaryMessage(
        "error",
        "Username can only contain letters, numbers, and underscores"
      );
      setIsLoading(false);
      return;
    }

    try {
      const token = localStorage.getItem("token");
      if (!token) {
        throw new Error("No authentication token found. Please log in again.");
      }
      if (typeof setUser !== "function") {
        throw new Error(
          "setUser is not a function. Ensure AuthProvider wraps the app."
        );
      }

      const response = await axios.put(
        `${process.env.REACT_APP_API_URL}/api/users/profile`,
        { bio, username },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      const updatedUser = {
        id: response.data.id,
        username: response.data.username,
        email: response.data.email,
        bio: response.data.bio,
        profilePicture: user.profilePicture || response.data.profilePicture,
        createdAt: response.data.createdAt,
      };
      setUser(updatedUser);
      setIsEditing(false);
      setTemporaryMessage("success", "Profile updated successfully");
    } catch (err) {
      const errorMessage =
        err.response?.data?.message ||
        err.message ||
        "Failed to update profile";
      setTemporaryMessage("error", errorMessage);
    } finally {
      setIsLoading(false);
    }
  };

  const handleSortChange = (e) => {
    const option = e.target.value;
    setSortOption(option);
    setPosts((prevPosts) => {
      const sortedPosts = [...prevPosts];
      if (option === "newest") {
        sortedPosts.sort(
          (a, b) => new Date(b.createdAt) - new Date(a.createdAt)
        );
      } else if (option === "oldest") {
        sortedPosts.sort(
          (a, b) => new Date(a.createdAt) - new Date(b.createdAt)
        );
      } else if (option === "most-liked") {
        sortedPosts.sort(
          (a, b) => (b.likes?.length || 0) - (a.likes?.length || 0)
        );
      }
      return sortedPosts;
    });
  };

  const handleImageError = () => {
    setImageLoadError(true);
  };

  const formatDate = (date) => {
    return new Intl.DateTimeFormat("en-US", {
      month: "numeric",
      day: "numeric",
      year: "numeric",
      timeZone: "Asia/Kolkata",
    }).format(new Date(date));
  };

  if (authLoading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="inline-block animate-spin rounded-full h-10 w-10 border-t-4 border-b-4 border-gradient-to-r from-blue-600 to-indigo-600 dark:from-blue-400 dark:to-indigo-400"></div>
      </div>
    );
  }

  if (!user) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <p className="text-lg transition-transform duration-200 hover:scale-105">
          Please log in to view your profile.
        </p>
      </div>
    );
  }

  return (
    <div className="min-h-screen py-10 px-4">
      <style>
        {`
          @keyframes fadeInUp {
            0% { opacity: 0; transform: translateY(20px); }
            100% { opacity: 1; transform: translateY(0); }
          }
          @keyframes pulse {
            0% { opacity: 0.6; }
            50% { opacity: 1; }
            100% { opacity: 0.6; }
          }
          .animate-fade-in-up {
            animation: fadeInUp 0.5s ease-out forwards;
          }
          .animate-pulse-text {
            animation: pulse 1.5s infinite;
          }
          .hover-underline::after {
            content: '';
            position: absolute;
            width: 0;
            height: 2px;
            bottom: 0;
            left: 0;
            background: var(--button-bg-light);
            transition: width 0.3s ease-in-out;
          }
          .dark .hover-underline::after {
            background: var(--button-bg-dark);
          }
          .hover-underline:hover::after {
            width: 100%;
          }
        `}
      </style>
      <div className="max-w-4xl mx-auto">
        <div className="p-6 rounded-xl shadow-xl border mb-8 animate-fade-in-up">
          <div className="flex flex-col md:flex-row items-center md:items-start space-y-6 md:space-y-0 md:space-x-6">
            <div className="relative">
              {imageLoadError || !user.profilePicture ? (
                <div className="w-32 h-32 rounded-full bg-gray-200 dark:bg-gray-600 flex items-center justify-center text-gray-500 dark:text-gray-400 text-5xl font-bold">
                  {user.username?.charAt(0).toUpperCase() || "?"}
                </div>
              ) : (
                <img
                  src={user.profilePicture}
                  alt="Profile"
                  className="w-32 h-32 rounded-full object-cover border-4 border-gray-200 dark:border-gray-600 shadow-md"
                  onError={handleImageError}
                />
              )}
              <input
                type="file"
                accept="image/*"
                onChange={handleProfilePictureChange}
                className="hidden"
                id="profile-picture-upload"
                ref={fileInputRef}
                disabled={isLoading}
                aria-label="Upload Profile Picture"
              />
              <label
                htmlFor="profile-picture-upload"
                className="absolute bottom-0 right-0 bg-gradient-to-r from-blue-600 to-indigo-600 dark:from-blue-500 dark:to-indigo-500 text-white rounded-full p-3 cursor-pointer hover:shadow-[0_0_10px_rgba(59,130,246,0.5)] dark:hover:shadow-[0_0_10px_rgba(96,165,250,0.3)] transition-all duration-200 disabled:opacity-50"
                aria-label="Upload new profile picture"
              >
                <FontAwesomeIcon icon={faPlus} />
              </label>
            </div>
            <div className="flex-1 text-center md:text-left">
              {isEditing ? (
                <form onSubmit={handleBioUpdate}>
                  <div className="mb-4">
                    <label
                      htmlFor="username"
                      className="block mb-2 font-medium"
                    >
                      Username
                    </label>
                    <input
                      type="text"
                      id="username"
                      value={username}
                      onChange={(e) => setUsername(e.target.value)}
                      placeholder="Enter username"
                      className="w-full p-3 bg-gray-50 dark:bg-gray-700 border border-gray-200 dark:border-gray-600 rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-gradient-to-r from-blue-500 to-indigo-500 focus:scale-[1.01] transition-all duration-200"
                      disabled={isLoading}
                      aria-label="Username"
                    />
                  </div>
                  <div className="mb-4">
                    <label htmlFor="bio" className="block mb-2 font-medium">
                      Bio
                    </label>
                    <textarea
                      id="bio"
                      value={bio}
                      onChange={(e) => setBio(e.target.value)}
                      placeholder="Tell us about yourself"
                      className="w-full p-3 bg-gray-50 dark:bg-gray-700 border border-gray-200 dark:border-gray-600 rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-gradient-to-r from-blue-500 to-indigo-500 focus:scale-[1.01] transition-all duration-200"
                      rows="3"
                      disabled={isLoading}
                      aria-label="Bio"
                    />
                    <p className="text-sm mt-1">
                      {bio.length}/{BIO_MAX_LENGTH}
                    </p>
                  </div>
                  <div className="flex space-x-3">
                    <button
                      type="submit"
                      className="bg-gradient-to-r from-blue-600 to-indigo-600 dark:from-blue-500 dark:to-indigo-500 text-white px-5 py-2 rounded-lg hover:shadow-[0_0_10px_rgba(59,130,246,0.5)] dark:hover:shadow-[0_0_10px_rgba(96,165,250,0.3)] transition-all duration-200 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2 disabled:opacity-50"
                      disabled={isLoading}
                      aria-label="Save Profile"
                    >
                      {isLoading ? "Saving..." : "Save"}
                    </button>
                    <button
                      type="button"
                      onClick={() => setIsEditing(false)}
                      className="bg-gradient-to-r from-gray-500 to-gray-600 dark:from-gray-600 dark:to-gray-700 text-white px-5 py-2 rounded-lg hover:shadow-[0_0_10px_rgba(107,114,128,0.5)] transition-all duration-200 focus:outline-none focus:ring-2 focus:ring-gray-500 focus:ring-offset-2"
                      aria-label="Cancel Editing"
                    >
                      Cancel
                    </button>
                  </div>
                </form>
              ) : (
                <>
                  <h2 className="relative text-2xl font-bold mb-2 hover-underline">
                    {user.username}
                  </h2>
                  <p className="mb-3 transition-all duration-200">
                    {user.bio || DEFAULT_BIO}
                  </p>
                  <p className="text-sm mb-4 transition-all duration-200">
                    Joined on {formatDate(user.createdAt)}
                  </p>
                  <button
                    onClick={() => setIsEditing(true)}
                    className="bg-gradient-to-r from-blue-600 to-indigo-600 dark:from-blue-500 dark:to-indigo-500 text-white px-5 py-2 rounded-lg hover:shadow-[0_0_10px_rgba(59,130,246,0.5)] dark:hover:shadow-[0_0_10px_rgba(96,165,250,0.3)] transition-all duration-200 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2"
                    aria-label="Edit Profile"
                  >
                    Edit Profile
                  </button>
                </>
              )}
            </div>
          </div>
          {uploadError && (
            <div className="p-4 mt-4 rounded-lg border shadow-md animate-fade-in-up">
              <FontAwesomeIcon icon={faExclamationCircle} className="mr-2" />
              {uploadError}
            </div>
          )}
          {error && (
            <div className="p-4 mt-4 rounded-lg border shadow-md animate-fade-in-up">
              <FontAwesomeIcon icon={faExclamationCircle} className="mr-2" />
              {error}
            </div>
          )}
          {success && (
            <div className="p-4 mt-4 rounded-lg border shadow-md animate-fade-in-up">
              <FontAwesomeIcon icon={faCheckCircle} className="mr-2" />
              {success}
            </div>
          )}
        </div>

        <div className="mb-8">
          <div className="flex justify-between items-center mb-6">
            <h3 className="relative text-xl font-bold hover-underline">
              My Posts
            </h3>
            <div>
              <label htmlFor="sort-posts" className="mr-2 font-medium">
                Sort by:
              </label>
              <select
                id="sort-posts"
                value={sortOption}
                onChange={handleSortChange}
                className="p-2 bg-gray-50 dark:bg-gray-700 border border-gray-200 dark:border-gray-600 rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-gradient-to-r from-blue-500 to-indigo-500 transition-all duration-200"
                aria-label="Sort posts"
              >
                <option value="newest">Newest</option>
                <option value="oldest">Oldest</option>
                <option value="most-liked">Most Liked</option>
              </select>
            </div>
          </div>
          {isPostsLoading ? (
            <div className="bg-white dark:bg-gray-800 p-6 rounded-lg shadow-lg flex flex-col items-center justify-center">
              <div className="inline-block animate-spin rounded-full h-10 w-10 border-t-4 border-b-4 border-gradient-to-r from-blue-600 to-indigo-600 dark:from-blue-400 dark:to-indigo-400"></div>
              <p className="mt-3 text-lg animate-pulse-text">
                Loading posts...
              </p>
            </div>
          ) : posts.length > 0 ? (
            <div className="bg-white dark:bg-gray-800 p-6 rounded-xl shadow-lg space-y-8">
              {posts.map((post, index) => (
                <div
                  key={post._id}
                  className="animate-fade-in-up"
                  style={{ animationDelay: `${index * 0.1}s` }}
                >
                  <PostCard
                    id={post._id}
                    title={post.title}
                    content={post.content}
                    author={post.author?.username || "Unknown"}
                    comments={post.comments}
                    likes={post.likes}
                    post={post}
                    setPosts={setPosts}
                    currentUser={user}
                  />
                </div>
              ))}
            </div>
          ) : (
            <p className="text-center text-lg transition-transform duration-200 hover:scale-105">
              No posts yet.
            </p>
          )}
        </div>
      </div>
    </div>
  );
}

export default Profile;
