import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import GroupChat from "../components/GroupChat";
import { AuthContext } from "../context/AuthContext";

function GroupDetail() {
  const { groupId } = useParams();
  const navigate = useNavigate();
  const { user } = React.useContext(AuthContext);
  const [group, setGroup] = useState(null);
  const [error, setError] = useState("");
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const fetchGroup = async () => {
      try {
        const response = await fetch(
          `${process.env.REACT_APP_API_URL}/api/study-groups`
        );
        if (!response.ok) {
          throw new Error("Failed to fetch group");
        }
        const groups = await response.json();
        const selectedGroup = groups.find((g) => g._id === groupId);
        if (!selectedGroup) {
          throw new Error("Group not found");
        }
        setGroup(selectedGroup);
      } catch (err) {
        setError(err.message || "Failed to load group");
      } finally {
        setIsLoading(false);
      }
    };
    fetchGroup();
  }, [groupId]);

  const handleJoinGroup = async () => {
    try {
      const token = localStorage.getItem("token");
      const response = await fetch(
        `${process.env.REACT_APP_API_URL}/api/study-groups/${groupId}/join`,
        {
          method: "POST",
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || "Failed to join group");
      }
      const updatedGroup = await response.json();
      setGroup(updatedGroup);
    } catch (err) {
      setError(err.message || "Failed to join group");
    }
  };

  const handleLeaveGroup = async () => {
    try {
      const token = localStorage.getItem("token");
      const response = await fetch(
        `${process.env.REACT_APP_API_URL}/api/study-groups/${groupId}/leave`,
        {
          method: "POST",
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || "Failed to leave group");
      }
      const updatedGroup = await response.json();
      setGroup(updatedGroup);
    } catch (err) {
      setError(err.message || "Failed to leave group");
    }
  };

  const handleDeleteGroup = async () => {
    try {
      const token = localStorage.getItem("token");
      const response = await fetch(
        `${process.env.REACT_APP_API_URL}/api/study-groups/${groupId}`,
        {
          method: "DELETE",
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || "Failed to delete group");
      }
      navigate("/groups");
    } catch (err) {
      setError(err.message || "Failed to delete group");
    }
  };

  if (isLoading) {
    return <div className="text-center mt-10">Loading...</div>;
  }

  if (error) {
    return (
      <div className="bg-red-100 dark:bg-red-900/70 text-red-700 dark:text-red-300 p-4 mt-10 rounded-lg border border-gradient-to-r from-red-200 to-red-300 dark:from-red-800 dark:to-red-700 shadow-md">
        {error}
      </div>
    );
  }

  const isMember = group.members.some((member) => member._id === user?.id);
  const isCreator = group.creator._id === user?.id;

  return (
    <div className="max-w-4xl mx-auto mt-10">
      <div className="bg-gradient-to-br from-blue-50 to-indigo-50 dark:from-indigo-800 dark:to-teal-800 p-6 rounded-xl shadow-xl border border-blue-100 dark:border-teal-600 hover:shadow-2xl dark:hover:shadow-[0_0_15px_rgba(45,212,191,0.3)] hover:-translate-y-2 transition-all duration-300">
        <div className="flex items-center space-x-4 mb-4">
          <div className="flex items-center justify-center w-12 h-12 rounded-full bg-gradient-to-r from-blue-400 to-indigo-400 dark:from-teal-500 dark:to-teal-400 text-white text-2xl">
            <i className={`fa-solid ${group.groupImage}`}></i>
          </div>
          <h1 className="relative text-2xl font-bold bg-clip-text text-transparent bg-gradient-to-r from-blue-600 to-indigo-600 dark:from-amber-400 dark:to-amber-300 hover-underline">
            {group.name}
          </h1>
        </div>
        <p className="text-gray-600 dark:text-gray-200 mb-3">
          {group.description}
        </p>
        <p className="text-sm mb-4 bg-clip-text text-transparent bg-gradient-to-r from-gray-500 to-gray-700 dark:from-teal-400 dark:to-teal-300">
          Category: {group.category}
        </p>
        <p className="text-sm mb-4 bg-clip-text text-transparent bg-gradient-to-r from-gray-500 to-gray-700 dark:from-teal-400 dark:to-teal-300">
          Created by: {group.creator.username}
        </p>
        <p className="text-sm mb-4 bg-clip-text text-transparent bg-gradient-to-r from-gray-500 to-gray-700 dark:from-teal-400 dark:to-teal-300">
          Members: {group.members.map((member) => member.username).join(", ")}
        </p>
        <div className="flex space-x-3 mb-6">
          {user && !isMember && (
            <button
              onClick={handleJoinGroup}
              className="bg-gradient-to-r from-blue-600 to-indigo-600 dark:from-teal-600 dark:to-teal-400 text-white dark:text-gray-100 px-5 py-3 rounded-lg hover:shadow-[0_0_10px_rgba(59,130,246,0.5)] dark:hover:shadow-[0_0_10px_rgba(45,212,191,0.5)] dark:hover:dark-glow hover:scale-105 transition-all duration-300"
            >
              Join Group
            </button>
          )}
          {isMember && !isCreator && (
            <button
              onClick={handleLeaveGroup}
              className="bg-gradient-to-r from-red-600 to-red-800 dark:from-red-700 dark:to-red-600 text-white dark:text-gray-100 px-5 py-3 rounded-lg hover:shadow-[0_0_10px_rgba(239,68,68,0.5)] dark:hover:shadow-[0_0_10px_rgba(45,212,191,0.5)] dark:hover:dark-glow hover:scale-105 transition-all duration-300"
            >
              Leave Group
            </button>
          )}
          {isCreator && (
            <>
              <button
                onClick={() => navigate(`/groups/${groupId}/edit`)}
                className="bg-gradient-to-r from-blue-600 to-indigo-600 dark:from-teal-600 dark:to-teal-400 text-white dark:text-gray-100 px-5 py-3 rounded-lg hover:shadow-[0_0_10px_rgba(59,130,246,0.5)] dark:hover:shadow-[0_0_10px_rgba(45,212,191,0.5)] dark:hover:dark-glow hover:scale-105 transition-all duration-300"
              >
                Edit Group
              </button>
              <button
                onClick={handleDeleteGroup}
                className="bg-gradient-to-r from-red-600 to-red-800 dark:from-red-700 dark:to-red-600 text-white dark:text-gray-100 px-5 py-3 rounded-lg hover:shadow-[0_0_10px_rgba(239,68,68,0.5)] dark:hover:shadow-[0_0_10px_rgba(45,212,191,0.5)] dark:hover:dark-glow hover:scale-105 transition-all duration-300"
              >
                Delete Group
              </button>
            </>
          )}
        </div>
        {isMember && (
          <GroupChat
            groupId={groupId}
            messages={group.messages}
            setGroup={setGroup}
            currentUser={user}
          />
        )}
      </div>
    </div>
  );
}

export default GroupDetail;
