import React, { useState, useContext, useRef, useEffect } from "react";
import {
  Card,
  Form,
  Button,
  InputGroup,
  Row,
  Col,
  Alert,
} from "react-bootstrap";
import { Link, useNavigate } from "react-router-dom";
import { AuthContext } from "../context/AuthContext";

function Login() {
  const { login } = useContext(AuthContext);
  const navigate = useNavigate();

  const [emailOrUsername, setEmailOrUsername] = useState("");
  const [password, setPassword] = useState("");
  const [showPassword, setShowPassword] = useState(false);
  const [error, setError] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const [debugInfo, setDebugInfo] = useState({ logs: [] });

  const emailRef = useRef(null);
  const passwordRef = useRef(null);

  // Clear errors after 5 seconds
  useEffect(() => {
    if (error) {
      const timer = setTimeout(() => {
        setError("");
      }, 5000);
      return () => clearTimeout(timer);
    }
  }, [error]);

  // Focus management for navigation
  const handleKeyDown = (e, currentField) => {
    if (e.key === "Enter") {
      e.preventDefault();

      if (currentField === "email" && password === "") {
        // If email field and password is empty, move to password field
        passwordRef.current?.focus();
      } else if (currentField === "email" && password !== "") {
        // If email field and password is filled, submit
        handleSubmit();
      } else if (currentField === "password") {
        // If password field, submit
        handleSubmit();
      }
    }
  };

  const addDebugLog = (message) => {
    console.log(`ðŸ” Login Debug: ${message}`);
    setDebugInfo((prev) => ({
      logs: [
        ...prev.logs.slice(-4),
        `${new Date().toLocaleTimeString()}: ${message}`,
      ],
    }));
  };

  const validateForm = () => {
    if (!emailOrUsername.trim() || !password.trim()) {
      setError("Please enter both email/username and password.");
      return false;
    }
    return true;
  };

  const handleSubmit = async () => {
    addDebugLog("Form submission started");

    if (!validateForm()) {
      addDebugLog("Form validation failed");
      return;
    }

    try {
      setIsLoading(true);
      setError("");
      addDebugLog(`Attempting login for: ${emailOrUsername.toLowerCase()}`);

      // Convert to lowercase for case-insensitive login
      const identifier = emailOrUsername.toLowerCase().trim();

      const result = await login(identifier, password);

      if (result && result.success) {
        addDebugLog("Login successful, redirecting...");
        // Add a small delay to show success state
        setTimeout(() => {
          navigate("/");
        }, 500);
      } else {
        addDebugLog("Login failed with result: " + JSON.stringify(result));
        setError(
          result?.message || "Login failed. Please check your credentials."
        );
        setIsLoading(false);
      }
    } catch (err) {
      addDebugLog("Login error caught: " + err.message);
      console.error("Login error:", err);
      setError(
        err.response?.data?.message ||
          err.message ||
          "Login failed. Please check your credentials."
      );
      setIsLoading(false);
    }
  };

  return (
    <Row className="justify-content-center">
      <Col md={6} lg={5}>
        <Card className="shadow animate-fade-in-up">
          <Card.Body className="p-4">
            <div className="text-center mb-4">
              <i className="fas fa-user-circle fa-3x text-primary mb-3"></i>
              <h2 className="fw-bold">Login to NexusEd</h2>
              <p className="text-muted">Access your student community</p>
            </div>

            {error && (
              <Alert variant="danger" className="mb-4 fade-message">
                <i className="fas fa-exclamation-circle me-2"></i>
                {error}
              </Alert>
            )}

            <Form>
              <Form.Group className="mb-3">
                <Form.Label>Email or Username</Form.Label>
                <InputGroup>
                  <InputGroup.Text>
                    <i className="fas fa-envelope"></i>
                  </InputGroup.Text>
                  <Form.Control
                    type="text"
                    ref={emailRef}
                    value={emailOrUsername}
                    onChange={(e) => setEmailOrUsername(e.target.value)}
                    onKeyDown={(e) => handleKeyDown(e, "email")}
                    placeholder="Enter your email or username"
                    autoComplete="username"
                    disabled={isLoading}
                    required
                  />
                </InputGroup>
                <Form.Text className="text-muted">
                  Case insensitive - you can use any case
                </Form.Text>
              </Form.Group>

              <Form.Group className="mb-4">
                <Form.Label>Password</Form.Label>
                <InputGroup>
                  <InputGroup.Text>
                    <i className="fas fa-lock"></i>
                  </InputGroup.Text>
                  <Form.Control
                    type={showPassword ? "text" : "password"}
                    ref={passwordRef}
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    onKeyDown={(e) => handleKeyDown(e, "password")}
                    placeholder="Enter your password"
                    autoComplete="current-password"
                    disabled={isLoading}
                    required
                  />
                  <Button
                    variant="outline-secondary"
                    onClick={() => setShowPassword(!showPassword)}
                    disabled={isLoading}
                    aria-label={
                      showPassword ? "Hide password" : "Show password"
                    }
                  >
                    <i
                      className={`fa-solid ${
                        showPassword ? "fa-eye-slash" : "fa-eye"
                      }`}
                    ></i>
                  </Button>
                </InputGroup>
              </Form.Group>

              <div className="d-grid">
                <Button
                  variant="primary"
                  onClick={handleSubmit}
                  className="btn-hover-shadow"
                  disabled={isLoading}
                >
                  {isLoading ? (
                    <>
                      <span
                        className="spinner-border spinner-border-sm me-2"
                        role="status"
                        aria-hidden="true"
                      ></span>
                      Logging in...
                    </>
                  ) : (
                    <>
                      <i className="fas fa-sign-in-alt me-2"></i>
                      Login
                    </>
                  )}
                </Button>
              </div>
            </Form>

            <div className="text-center mt-4">
              <p>
                Don't have an account?{" "}
                <Link
                  to="/register"
                  className="text-primary fw-semibold transition-all hover-underline-effect"
                >
                  Register here
                </Link>
              </p>
            </div>

            {/* Debug Information (only show in development) */}
            {process.env.NODE_ENV === "development" &&
              debugInfo.logs.length > 0 && (
                <div className="mt-3 p-2 bg-light rounded">
                  <small className="text-muted">
                    <strong>Debug Info:</strong>
                    {debugInfo.logs.map((log, index) => (
                      <div key={index} style={{ fontSize: "11px" }}>
                        {log}
                      </div>
                    ))}
                  </small>
                </div>
              )}
          </Card.Body>
        </Card>
      </Col>
    </Row>
  );
}

export default Login;
