import React, { useContext } from "react";
import { NavLink, useNavigate } from "react-router-dom";
import { AuthContext } from "../context/AuthContext";

function Navbar() {
  const { user, logout } = useContext(AuthContext);
  const navigate = useNavigate();

  const handleLogout = () => {
    logout();
    navigate("/");
  };

  return (
    <nav className="bg-blue-600 p-4 shadow-lg">
      <div className="container mx-auto flex justify-between items-center">
        <NavLink
          to="/"
          className="text-white text-2xl font-bold"
          aria-label="NexusEd Home"
        >
          NexusEd
        </NavLink>
        <div className="space-x-6">
          <NavLink
            to="/"
            className={({ isActive }) =>
              `transition duration-200 ${
                isActive
                  ? "text-blue-200 underline"
                  : "text-white hover:text-blue-200 hover:shadow-md"
              }`
            }
            aria-label="Home"
          >
            Home
          </NavLink>
          {user ? (
            <>
              <NavLink
                to="/profile"
                className={({ isActive }) =>
                  `transition duration-200 ${
                    isActive
                      ? "text-blue-200 underline"
                      : "text-white hover:text-blue-200 hover:shadow-md"
                  }`
                }
                aria-label="Profile"
              >
                Profile
              </NavLink>
              <button
                onClick={handleLogout}
                className="text-white hover:text-blue-200 hover:shadow-md bg-transparent border-none cursor-pointer transition duration-200"
                aria-label="Logout"
              >
                Logout
              </button>
            </>
          ) : (
            <>
              <NavLink
                to="/login"
                className={({ isActive }) =>
                  `transition duration-200 ${
                    isActive
                      ? "text-blue-200 underline"
                      : "text-white hover:text-blue-200 hover:shadow-md"
                  }`
                }
                aria-label="Login"
              >
                Login
              </NavLink>
              <NavLink
                to="/register"
                className={({ isActive }) =>
                  `transition duration-200 ${
                    isActive
                      ? "text-blue-200 underline"
                      : "text-white hover:text-blue-200 hover:shadow-md"
                  }`
                }
                aria-label="Register"
              >
                Register
              </NavLink>
            </>
          )}
        </div>
      </div>
    </nav>
  );
}

export default Navbar;
