import React, { useState, useEffect, useContext, useRef } from "react";
import { AuthContext } from "../context/AuthContext";
import axios from "axios";

function StudyGroups() {
  const { user, isLoading: authLoading } = useContext(AuthContext);
  const [groups, setGroups] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");
  const [newGroup, setNewGroup] = useState({
    name: "",
    description: "",
    category: "",
  });
  const [editGroup, setEditGroup] = useState(null); // State for editing a group
  const [selectedCategory, setSelectedCategory] = useState("All");
  const [visibleMembers, setVisibleMembers] = useState({});
  const [confirmation, setConfirmation] = useState(null); // State for join/leave confirmation
  const timeoutRef = useRef(null);
  const confirmationTimeoutRef = useRef(null);
  const groupNameRef = useRef(null);
  const categoryRef = useRef(null);
  const descriptionRef = useRef(null);
  const editGroupNameRef = useRef(null);
  const editCategoryRef = useRef(null);
  const editDescriptionRef = useRef(null);

  // Refs for buttons
  const createGroupButtonRef = useRef(null);
  const joinGroupButtonRefs = useRef({});
  const leaveGroupButtonRefs = useRef({});
  const deleteGroupButtonRefs = useRef({});
  const editGroupButtonRefs = useRef({});

  // Predefined categories
  const categories = [
    "Mathematics",
    "Programming",
    "Literature",
    "Science",
    "History",
  ];

  const setTemporaryMessage = (type, message) => {
    if (timeoutRef.current) {
      clearTimeout(timeoutRef.current);
    }
    if (type === "error") {
      setError(message);
      setSuccess("");
    } else {
      setSuccess(message);
      setError("");
    }
    timeoutRef.current = setTimeout(() => {
      setError("");
      setSuccess("");
      timeoutRef.current = null;
    }, 3000);
  };

  const clearConfirmation = () => {
    if (confirmationTimeoutRef.current) {
      clearTimeout(confirmationTimeoutRef.current);
    }
    setConfirmation(null);
    confirmationTimeoutRef.current = null;
  };

  useEffect(() => {
    const fetchGroups = async () => {
      try {
        setIsLoading(true);
        const response = await fetch(
          `${process.env.REACT_APP_API_URL}/api/study-groups`
        );
        if (!response.ok) {
          throw new Error(`Failed to fetch study groups: ${response.status}`);
        }
        const data = await response.json();
        setGroups(data);
      } catch (err) {
        console.error("Error fetching study groups:", err.message);
        setTemporaryMessage("error", "Failed to load study groups.");
      } finally {
        setIsLoading(false);
      }
    };
    fetchGroups();

    return () => {
      if (timeoutRef.current) {
        clearTimeout(timeoutRef.current);
      }
      if (confirmationTimeoutRef.current) {
        clearTimeout(confirmationTimeoutRef.current);
      }
    };
  }, []);

  const handleCreateGroup = async (e) => {
    e.preventDefault();

    if (createGroupButtonRef.current) {
      createGroupButtonRef.current.blur();
    }

    if (!user) {
      setTemporaryMessage("error", "Please log in to create a study group.");
      return;
    }
    if (
      !newGroup.name.trim() ||
      !newGroup.description.trim() ||
      !newGroup.category
    ) {
      setTemporaryMessage(
        "error",
        "Name, description, and category are required."
      );
      return;
    }

    try {
      const token = localStorage.getItem("token");
      const response = await axios.post(
        `${process.env.REACT_APP_API_URL}/api/study-groups`,
        {
          name: newGroup.name,
          description: newGroup.description,
          category: newGroup.category,
        },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setGroups([...groups, response.data]);
      setNewGroup({ name: "", description: "", category: "" });
      setTemporaryMessage("success", "Study group created successfully!");
    } catch (err) {
      setTemporaryMessage(
        "error",
        err.response?.data?.message || "Failed to create study group."
      );
    }
  };

  const handleEditGroup = (group) => {
    setEditGroup({
      id: group._id,
      name: group.name,
      description: group.description,
      category: group.category,
    });
  };

  const handleUpdateGroup = async (e) => {
    e.preventDefault();

    if (!user) {
      setTemporaryMessage("error", "Please log in to update a study group.");
      return;
    }
    if (
      !editGroup.name.trim() ||
      !editGroup.description.trim() ||
      !editGroup.category
    ) {
      setTemporaryMessage(
        "error",
        "Name, description, and category are required."
      );
      return;
    }

    try {
      const token = localStorage.getItem("token");
      const response = await axios.put(
        `${process.env.REACT_APP_API_URL}/api/study-groups/${editGroup.id}`,
        {
          name: editGroup.name,
          description: editGroup.description,
          category: editGroup.category,
        },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setGroups(
        groups.map((group) =>
          group._id === editGroup.id ? response.data : group
        )
      );
      setEditGroup(null);
      setTemporaryMessage("success", "Study group updated successfully!");
    } catch (err) {
      setTemporaryMessage(
        "error",
        err.response?.data?.message || "Failed to update study group."
      );
    }
  };

  const handleJoinGroup = async (groupId) => {
    if (joinGroupButtonRefs.current[groupId]) {
      joinGroupButtonRefs.current[groupId].blur();
    }

    if (!user) {
      setTemporaryMessage("error", "Please log in to join a study group.");
      return;
    }

    try {
      const token = localStorage.getItem("token");
      const response = await axios.post(
        `${process.env.REACT_APP_API_URL}/api/study-groups/${groupId}/join`,
        {},
        { headers: { Authorization: `Bearer ${token}` } }
      );

      const previousGroups = [...groups];
      setGroups(
        groups.map((group) => (group._id === groupId ? response.data : group))
      );

      clearConfirmation();
      setConfirmation({
        type: "join",
        groupId,
        message: "You have joined the study group.",
        undo: () => {
          handleLeaveGroup(groupId, true);
          setGroups(previousGroups);
        },
      });
      confirmationTimeoutRef.current = setTimeout(clearConfirmation, 5000);
    } catch (err) {
      setTemporaryMessage(
        "error",
        err.response?.data?.message || "Failed to join study group."
      );
    }
  };

  const handleLeaveGroup = async (groupId, fromUndo = false) => {
    if (leaveGroupButtonRefs.current[groupId] && !fromUndo) {
      leaveGroupButtonRefs.current[groupId].blur();
    }

    if (!user) {
      setTemporaryMessage("error", "Please log in to leave a study group.");
      return;
    }

    try {
      const token = localStorage.getItem("token");
      const response = await axios.post(
        `${process.env.REACT_APP_API_URL}/api/study-groups/${groupId}/leave`,
        {},
        { headers: { Authorization: `Bearer ${token}` } }
      );

      if (!fromUndo) {
        const previousGroups = [...groups];
        setGroups(
          groups.map((group) => (group._id === groupId ? response.data : group))
        );

        clearConfirmation();
        setConfirmation({
          type: "leave",
          groupId,
          message: "You have left the study group.",
          undo: () => {
            handleJoinGroup(groupId, true);
            setGroups(previousGroups);
          },
        });
        confirmationTimeoutRef.current = setTimeout(clearConfirmation, 5000);
      } else {
        setGroups(
          groups.map((group) => (group._id === groupId ? response.data : group))
        );
      }
    } catch (err) {
      setTemporaryMessage(
        "error",
        err.response?.data?.message || "Failed to leave study group."
      );
    }
  };

  const handleDeleteGroup = async (groupId) => {
    if (!user) {
      setTemporaryMessage("error", "Please log in to delete a study group.");
      return;
    }

    try {
      const token = localStorage.getItem("token");
      if (!token) {
        setTemporaryMessage(
          "error",
          "Authentication token missing. Please log in again."
        );
        return;
      }

      await axios.delete(
        `${process.env.REACT_APP_API_URL}/api/study-groups/${groupId}`,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setGroups(groups.filter((group) => group._id !== groupId));
    } catch (err) {
      console.error("Error deleting study group:", err);
      setTemporaryMessage(
        "error",
        err.response?.data?.message || "Failed to delete study group."
      );
    }
  };

  const handleKeyDown = (e) => {
    if (e.key === "Enter") {
      e.preventDefault();
      categoryRef.current.focus();
    }
  };

  const handleCategoryKeyDown = (e) => {
    if (e.key === "Enter") {
      e.preventDefault();
      descriptionRef.current.focus();
    }
  };

  const handleEditKeyDown = (e) => {
    if (e.key === "Enter") {
      e.preventDefault();
      editCategoryRef.current.focus();
    }
  };

  const handleEditCategoryKeyDown = (e) => {
    if (e.key === "Enter") {
      e.preventDefault();
      editDescriptionRef.current.focus();
    }
  };

  const toggleMembersVisibility = (groupId) => {
    setVisibleMembers((prev) => ({
      ...prev,
      [groupId]: !prev[groupId],
    }));
  };

  // Filter groups based on selected category
  const filteredGroups =
    selectedCategory === "All"
      ? groups
      : groups.filter((group) => group.category === selectedCategory);

  if (authLoading) {
    return (
      <div className="text-gray-600 dark:text-teal-400 py-10 animate-pulse">
        Loading auth...
      </div>
    );
  }

  return (
    <section
      className="min-h-screen bg-gradient-to-br from-gray-50 to-blue-50 dark:from-indigo-900 dark:to-indigo-800 py-10"
      aria-label="Study Groups"
    >
      <style>
        {`
          @keyframes fadeInUp {
            0% { opacity: 0; transform: translateY(20px); }
            100% { opacity: 1; transform: translateY(0); }
          }
          @keyframes pulse {
            0% { opacity: 0.6; }
            50% { opacity: 1; }
            100% { opacity: 0.6; }
          }
          @keyframes glow {
            0% { box-shadow: 0 0 5px rgba(45, 212, 191, 0.3); }
            50% { box-shadow: 0 0 15px rgba(45, 212, 191, 0.5); }
            100% { box-shadow: 0 0 5px rgba(45, 212, 191, 0.3); }
          }
          .animate-fade-in-up {
            animation: fadeInUp 0.5s ease-out forwards;
          }
          .animate-pulse-text {
            animation: pulse 1.5s infinite;
          }
          .dark-glow {
            animation: glow 2s infinite ease-in-out;
          }
          .hover-underline::after {
            content: '';
            position: absolute;
            width: 0;
            height: 2px;
            bottom: 0;
            left: 0;
            background: linear-gradient(to right, #2563eb, #4f46e5);
            transition: width 0.3s ease-in-out;
          }
          .dark .hover-underline::after {
            background: linear-gradient(to right, #2dd4bf, #5eead4);
          }
          .hover-underline:hover::after {
            width: 100%;
          }
          .tooltip {
            visibility: hidden;
            opacity: 0;
            transition: opacity 0.3s ease-in-out;
            position: absolute;
            bottom: 100%;
            left: 50%;
            transform: translateX(-50%);
            background-color: #1f2937;
            color: #fff;
            padding: 4px 8px;
            border-radius: 4px;
            font-size: 12px;
            white-space: nowrap;
            z-index: 10;
          }
          .avatar:hover .tooltip {
            visibility: visible;
            opacity: 1;
          }
          input:-webkit-autofill,
          input:-webkit-autofill:hover,
          input:-webkit-autofill:focus,
          input:-webkit-autofill:active,
          textarea:-webkit-autofill,
          textarea:-webkit-autofill:hover,
          textarea:-webkit-autofill:focus,
          textarea:-webkit-autofill:active {
            -webkit-box-shadow: 0 0 0 30px #f9fafb inset !important;
            -webkit-text-fill-color: #1f2937 !important;
            border-radius: 0.5rem !important;
            border: 1px solid #e5e7eb !important;
          }
          .dark input:-webkit-autofill,
          .dark input:-webkit-autofill:hover,
          .dark input:-webkit-autofill:focus,
          .dark input:-webkit-autofill:active,
          .dark textarea:-webkit-autofill,
          .dark textarea:-webkit-autofill:hover,
          .dark textarea:-webkit-autofill:focus,
          .dark textarea:-webkit-autofill:active {
            -webkit-box-shadow: 0 0 0 30px #4338ca inset !important;
            -webkit-text-fill-color: #f3f4f6 !important;
            border-radius: 0.5rem !important;
            border: 1px solid #14b8a6 !important;
          }
        `}
      </style>
      <div className="container mx-auto px-4 animate-fade-in-up">
        <h1 className="relative text-3xl font-bold bg-clip-text text-transparent bg-gradient-to-r from-blue-600 to-indigo-600 dark:from-amber-400 dark:to-amber-300 hover-underline mb-8">
          Study Groups
        </h1>

        {user ? (
          <div className="mb-8 bg-gradient-to-br from-blue-50 to-indigo-50 dark:from-indigo-800 dark:to-teal-800 p-6 rounded-xl shadow-xl border border-blue-100 dark:border-teal-600 hover:shadow-2xl dark:hover:shadow-[0_0_15px_rgba(45,212,191,0.3)] hover:-translate-y-2 transition-all duration-300 animate-fade-in-up">
            <h2 className="relative text-xl font-bold bg-clip-text text-transparent bg-gradient-to-r from-blue-600 to-indigo-600 dark:from-amber-400 dark:to-amber-300 mb-6 hover-underline">
              Create a New Study Group
            </h2>
            <form onSubmit={handleCreateGroup}>
              <div className="mb-5">
                <label
                  htmlFor="group-name"
                  className="block text-gray-700 dark:text-teal-400 mb-2 font-medium bg-clip-text text-transparent bg-gradient-to-r from-gray-700 to-gray-900 dark:from-teal-400 dark:to-teal-300"
                >
                  Group Name
                </label>
                <input
                  type="text"
                  id="group-name"
                  value={newGroup.name}
                  onChange={(e) =>
                    setNewGroup({ ...newGroup, name: e.target.value })
                  }
                  onKeyDown={handleKeyDown}
                  ref={groupNameRef}
                  placeholder="Enter group name"
                  className="w-full p-3 bg-gray-50 dark:bg-indigo-700 border border-gray-200 dark:border-teal-500 rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-gradient-to-r from-blue-500 to-indigo-500 dark:from-teal-500 dark:to-teal-400 focus:scale-[1.01] transition-all duration-300 text-gray-800 dark:text-gray-100 placeholder-gray-500 dark:placeholder-gray-400"
                  aria-label="Group Name"
                />
              </div>
              <div className="mb-5">
                <label
                  htmlFor="group-category"
                  className="block text-gray-700 dark:text-teal-400 mb-2 font-medium bg-clip-text text-transparent bg-gradient-to-r from-gray-700 to-gray-900 dark:from-teal-400 dark:to-teal-300"
                >
                  Category
                </label>
                <select
                  id="group-category"
                  value={newGroup.category}
                  onChange={(e) =>
                    setNewGroup({ ...newGroup, category: e.target.value })
                  }
                  onKeyDown={handleCategoryKeyDown}
                  ref={categoryRef}
                  className="w-full p-3 bg-gray-50 dark:bg-indigo-700 border border-gray-200 dark:border-teal-500 rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-gradient-to-r from-blue-500 to-indigo-500 dark:from-teal-500 dark:to-teal-400 focus:scale-[1.01] transition-all duration-300 text-gray-800 dark:text-gray-100"
                  aria-label="Group Category"
                >
                  <option value="">Select a category</option>
                  {categories.map((category) => (
                    <option key={category} value={category}>
                      {category}
                    </option>
                  ))}
                </select>
              </div>
              <div className="mb-6">
                <label
                  htmlFor="group-description"
                  className="block text-gray-700 dark:text-teal-400 mb-2 font-medium bg-clip-text text-transparent bg-gradient-to-r from-gray-700 to-gray-900 dark:from-teal-400 dark:to-teal-300"
                >
                  Description
                </label>
                <textarea
                  id="group-description"
                  value={newGroup.description}
                  onChange={(e) =>
                    setNewGroup({ ...newGroup, description: e.target.value })
                  }
                  ref={descriptionRef}
                  placeholder="Describe the study group"
                  className="w-full p-3 bg-gray-50 dark:bg-indigo-700 border border-gray-200 dark:border-teal-500 rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-gradient-to-r from-blue-500 to-indigo-500 dark:from-teal-500 dark:to-teal-400 focus:scale-[1.01] transition-all duration-300 text-gray-800 dark:text-gray-100 placeholder-gray-500 dark:placeholder-gray-400"
                  rows="4"
                  aria-label="Group Description"
                />
              </div>
              <button
                type="submit"
                className="bg-gradient-to-r from-blue-600 to-indigo-600 dark:from-teal-600 dark:to-teal-400 text-white dark:text-gray-100 px-5 py-3 rounded-lg hover:shadow-[0_0_10px_rgba(59,130,246,0.5)] dark:hover:shadow-[0_0_10px_rgba(45,212,191,0.5)] dark:hover:dark-glow hover:scale-105 transition-all duration-300 focus:outline-none focus:ring-2 focus:ring-blue-500 dark:focus:ring-teal-400 focus:ring-offset-2 dark:focus:ring-offset-indigo-800 disabled:opacity-50"
                ref={createGroupButtonRef}
                aria-label="Create Study Group"
              >
                Create Group
              </button>
            </form>
          </div>
        ) : (
          <p className="text-gray-600 dark:text-teal-400 mb-8 text-lg transition-transform duration-200 hover:scale-105 animate-fade-in-up">
            Please log in to create or join a study group.
          </p>
        )}

        {editGroup && (
          <div className="mb-8 bg-gradient-to-br from-blue-50 to-indigo-50 dark:from-indigo-800 dark:to-teal-800 p-6 rounded-xl shadow-xl border border-blue-100 dark:border-teal-600 hover:shadow-2xl dark:hover:shadow-[0_0_15px_rgba(45,212,191,0.3)] hover:-translate-y-2 transition-all duration-300 animate-fade-in-up">
            <h2 className="relative text-xl font-bold bg-clip-text text-transparent bg-gradient-to-r from-blue-600 to-indigo-600 dark:from-amber-400 dark:to-amber-300 mb-6 hover-underline">
              Edit Study Group
            </h2>
            <form onSubmit={handleUpdateGroup}>
              <div className="mb-5">
                <label
                  htmlFor="edit-group-name"
                  className="block text-gray-700 dark:text-teal-400 mb-2 font-medium bg-clip-text text-transparent bg-gradient-to-r from-gray-700 to-gray-900 dark:from-teal-400 dark:to-teal-300"
                >
                  Group Name
                </label>
                <input
                  type="text"
                  id="edit-group-name"
                  value={editGroup.name}
                  onChange={(e) =>
                    setEditGroup({ ...editGroup, name: e.target.value })
                  }
                  onKeyDown={handleEditKeyDown}
                  ref={editGroupNameRef}
                  placeholder="Enter group name"
                  className="w-full p-3 bg-gray-50 dark:bg-indigo-700 border border-gray-200 dark:border-teal-500 rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-gradient-to-r from-blue-500 to-indigo-500 dark:from-teal-500 dark:to-teal-400 focus:scale-[1.01] transition-all duration-300 text-gray-800 dark:text-gray-100 placeholder-gray-500 dark:placeholder-gray-400"
                  aria-label="Edit Group Name"
                />
              </div>
              <div className="mb-5">
                <label
                  htmlFor="edit-group-category"
                  className="block text-gray-700 dark:text-teal-400 mb-2 font-medium bg-clip-text text-transparent bg-gradient-to-r from-gray-700 to-gray-900 dark:from-teal-400 dark:to-teal-300"
                >
                  Category
                </label>
                <select
                  id="edit-group-category"
                  value={editGroup.category}
                  onChange={(e) =>
                    setEditGroup({ ...editGroup, category: e.target.value })
                  }
                  onKeyDown={handleEditCategoryKeyDown}
                  ref={editCategoryRef}
                  className="w-full p-3 bg-gray-50 dark:bg-indigo-700 border border-gray-200 dark:border-teal-500 rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-gradient-to-r from-blue-500 to-indigo-500 dark:from-teal-500 dark:to-teal-400 focus:scale-[1.01] transition-all duration-300 text-gray-800 dark:text-gray-100"
                  aria-label="Edit Group Category"
                >
                  <option value="">Select a category</option>
                  {categories.map((category) => (
                    <option key={category} value={category}>
                      {category}
                    </option>
                  ))}
                </select>
              </div>
              <div className="mb-6">
                <label
                  htmlFor="edit-group-description"
                  className="block text-gray-700 dark:text-teal-400 mb-2 font-medium bg-clip-text text-transparent bg-gradient-to-r from-gray-700 to-gray-900 dark:from-teal-400 dark:to-teal-300"
                >
                  Description
                </label>
                <textarea
                  id="edit-group-description"
                  value={editGroup.description}
                  onChange={(e) =>
                    setEditGroup({ ...editGroup, description: e.target.value })
                  }
                  ref={editDescriptionRef}
                  placeholder="Describe the study group"
                  className="w-full p-3 bg-gray-50 dark:bg-indigo-700 border border-gray-200 dark:border-teal-500 rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-gradient-to-r from-blue-500 to-indigo-500 dark:from-teal-500 dark:to-teal-400 focus:scale-[1.01] transition-all duration-300 text-gray-800 dark:text-gray-100 placeholder-gray-500 dark:placeholder-gray-400"
                  rows="4"
                  aria-label="Edit Group Description"
                />
              </div>
              <div className="flex space-x-3">
                <button
                  type="submit"
                  className="bg-gradient-to-r from-blue-600 to-indigo-600 dark:from-teal-600 dark:to-teal-400 text-white dark:text-gray-100 px-5 py-3 rounded-lg hover:shadow-[0_0_10px_rgba(59,130,246,0.5)] dark:hover:shadow-[0_0_10px_rgba(45,212,191,0.5)] dark:hover:dark-glow hover:scale-105 transition-all duration-300 focus:outline-none focus:ring-2 focus:ring-blue-500 dark:focus:ring-teal-400 focus:ring-offset-2 dark:focus:ring-offset-indigo-800"
                  aria-label="Update Study Group"
                >
                  Update Group
                </button>
                <button
                  type="button"
                  onClick={() => setEditGroup(null)}
                  className="bg-gradient-to-r from-gray-500 to-gray-600 dark:from-gray-600 dark:to-gray-500 text-white dark:text-gray-100 px-5 py-3 rounded-lg hover:shadow-[0_0_10px_rgba(107,114,128,0.5)] dark:hover:shadow-[0_0_10px_rgba(107,114,128,0.5)] hover:scale-105 transition-all duration-300 focus:outline-none focus:ring-2 focus:ring-gray-500 dark:focus:ring-gray-400 focus:ring-offset-2 dark:focus:ring-offset-indigo-800"
                  aria-label="Cancel Edit"
                >
                  Cancel
                </button>
              </div>
            </form>
          </div>
        )}

        {error && (
          <div className="bg-red-100 dark:bg-red-900/70 text-red-700 dark:text-red-300 p-4 mb-6 rounded-lg border border-gradient-to-r from-red-200 to-red-300 dark:from-red-800 dark:to-red-700 shadow-md hover:shadow-[0_0_10px_rgba(239,68,68,0.5)] dark:hover:shadow-[0_0_10px_rgba(45,212,191,0.5)] transition-all duration-300 animate-fade-in-up">
            {error}
          </div>
        )}
        {success && (
          <div className="bg-green-100 dark:bg-green-900/70 text-green-700 dark:text-green-300 p-4 mb-6 rounded-lg border border-gradient-to-r from-green-200 to-green-300 dark:from-green-800 dark:to-green-700 shadow-md hover:shadow-[0_0_10px_rgba(34,197,94,0.5)] dark:hover:shadow-[0_0_10px_rgba(45,212,191,0.5)] transition-all duration-300 animate-fade-in-up">
            {success}
          </div>
        )}
        {confirmation && (
          <div className="bg-yellow-100 dark:bg-yellow-900/70 text-yellow-700 dark:text-yellow-300 p-4 mb-6 rounded-lg border border-gradient-to-r from-yellow-200 to-yellow-300 dark:from-yellow-800 dark:to-yellow-700 shadow-md hover:shadow-[0_0_10px_rgba(234,179,8,0.5)] dark:hover:shadow-[0_0_10px_rgba(45,212,191,0.5)] transition-all duration-300 animate-fade-in-up flex justify-between items-center">
            <span>{confirmation.message}</span>
            <button
              onClick={() => {
                confirmation.undo();
                clearConfirmation();
              }}
              className="bg-gradient-to-r from-blue-600 to-indigo-600 dark:from-teal-600 dark:to-teal-400 text-white dark:text-gray-100 px-3 py-1 rounded-md hover:shadow-[0_0_10px_rgba(59,130,246,0.5)] dark:hover:shadow-[0_0_10px_rgba(45,212,191,0.5)] dark:hover:dark-glow hover:scale-105 transition-all duration-300 focus:outline-none focus:ring-2 focus:ring-blue-500 dark:focus:ring-teal-400 focus:ring-offset-2 dark:focus:ring-offset-indigo-800 text-sm"
              aria-label="Undo Action"
            >
              Undo
            </button>
          </div>
        )}

        <div className="mb-6">
          <label
            htmlFor="category-filter"
            className="block text-gray-700 dark:text-teal-400 mb-2 font-medium bg-clip-text text-transparent bg-gradient-to-r from-gray-700 to-gray-900 dark:from-teal-400 dark:to-teal-300"
          >
            Filter by Category
          </label>
          <select
            id="category-filter"
            value={selectedCategory}
            onChange={(e) => setSelectedCategory(e.target.value)}
            className="w-full sm:w-48 p-3 bg-gray-50 dark:bg-indigo-700 border border-gray-200 dark:border-teal-500 rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-gradient-to-r from-blue-500 to-indigo-500 dark:from-teal-500 dark:to-teal-400 focus:scale-[1.01] transition-all duration-300 text-gray-800 dark:text-gray-100"
            aria-label="Filter by Category"
          >
            <option value="All">All</option>
            {categories.map((category) => (
              <option key={category} value={category}>
                {category}
              </option>
            ))}
          </select>
        </div>

        {isLoading ? (
          <div className="bg-gradient-to-br from-blue-50 to-indigo-50 dark:from-indigo-800 dark:to-teal-800 p-6 rounded-xl shadow-xl border border-blue-100 dark:border-teal-600 hover:shadow-2xl dark:hover:shadow-[0_0_15px_rgba(45,212,191,0.3)] hover:-translate-y-2 transition-all duration-300 animate-fade-in-up flex flex-col items-center justify-center">
            <div className="inline-block animate-spin rounded-full h-10 w-10 border-t-4 border-b-4 border-gradient-to-r from-blue-600 to-indigo-600 dark:from-teal-500 dark:to-teal-400"></div>
            <p className="text-gray-600 dark:text-teal-400 mt-3 text-lg animate-pulse-text">
              Loading study groups...
            </p>
          </div>
        ) : filteredGroups.length > 0 ? (
          <div className="bg-gradient-to-br from-blue-50 to-indigo-50 dark:from-indigo-800 dark:to-teal-800 p-6 rounded-xl shadow-xl border border-blue-100 dark:border-teal-600 hover:shadow-2xl dark:hover:shadow-[0_0_15px_rgba(45,212,191,0.3)] hover:-translate-y-2 transition-all duration-300 animate-fade-in-up space-y-6">
            {filteredGroups.map((group, index) => (
              <div
                key={group._id}
                className="bg-gradient-to-r from-gray-50 to-blue-50 dark:from-indigo-700 dark:to-indigo-600 p-4 rounded-lg shadow-sm border border-gray-200 dark:border-teal-500 animate-fade-in-up"
                style={{ animationDelay: `${index * 0.1}s` }}
              >
                <div className="flex items-center justify-between mb-2">
                  <h3 className="relative text-lg font-semibold bg-clip-text text-transparent bg-gradient-to-r from-blue-600 to-indigo-600 dark:from-amber-400 dark:to-amber-300 hover-underline">
                    {group.name}
                  </h3>
                  {group.category && (
                    <span className="inline-block bg-gradient-to-r from-blue-200 to-indigo-200 dark:from-teal-600 dark:to-teal-500 text-gray-800 dark:text-gray-100 px-2 py-1 rounded-full text-xs font-medium">
                      {group.category}
                    </span>
                  )}
                </div>
                <p className="text-gray-600 dark:text-gray-200 mb-2 italic transition-all duration-200">
                  {group.description}
                </p>
                <div className="flex flex-wrap space-x-4 mb-3 text-sm">
                  <p className="bg-clip-text text-transparent bg-gradient-to-r from-gray-500 to-gray-700 dark:from-teal-400 dark:to-teal-300 transition-all duration-200">
                    Creator: <span>{group.creator?.username || "Unknown"}</span>
                  </p>
                  <p className="bg-clip-text text-transparent bg-gradient-to-r from-gray-500 to-gray-700 dark:from-teal-400 dark:to-teal-300 transition-all duration-200">
                    Members: <span>{group.members?.length || 0}</span>
                  </p>
                </div>
                {group.members?.length > 0 && (
                  <div className="mb-3">
                    <button
                      onClick={() => toggleMembersVisibility(group._id)}
                      className="text-sm bg-gradient-to-r from-blue-600 to-indigo-600 dark:from-teal-600 dark:to-teal-400 text-white dark:text-gray-100 px-3 py-1 rounded-md hover:shadow-[0_0_10px_rgba(59,130,246,0.5)] dark:hover:shadow-[0_0_10px_rgba(45,212,191,0.5)] dark:hover:dark-glow hover:scale-105 transition-all duration-300 focus:outline-none focus:ring-2 focus:ring-blue-500 dark:focus:ring-teal-400 focus:ring-offset-2 dark:focus:ring-offset-indigo-800 flex items-center space-x-1"
                      aria-label={
                        visibleMembers[group._id]
                          ? `Hide members of ${group.name}`
                          : `Show members of ${group.name}`
                      }
                    >
                      <i
                        className={
                          visibleMembers[group._id]
                            ? "fa-solid fa-chevron-up"
                            : "fa-solid fa-chevron-down"
                        }
                      ></i>
                      <span>
                        {visibleMembers[group._id]
                          ? "Hide Members"
                          : "Show Members"}
                      </span>
                    </button>
                    {visibleMembers[group._id] && (
                      <div className="mt-2 flex flex-wrap gap-2">
                        {group.members.map((member) => (
                          <div
                            key={member._id}
                            className="relative avatar flex items-center justify-center w-8 h-8 rounded-full bg-gradient-to-r from-blue-400 to-indigo-400 dark:from-teal-500 dark:to-teal-400 text-white text-xs font-medium cursor-pointer transition-all duration-300 hover:scale-110"
                          >
                            <span>
                              {member.username
                                ? member.username.charAt(0).toUpperCase()
                                : "U"}
                            </span>
                            <span className="tooltip">
                              {member.username || "Unknown"}
                            </span>
                          </div>
                        ))}
                      </div>
                    )}
                  </div>
                )}
                {user && (
                  <div className="flex items-center space-x-3">
                    {group.members?.some((member) => member._id === user.id) ? (
                      group.creator._id === user.id ? (
                        <>
                          <button
                            onClick={() => handleEditGroup(group)}
                            className="bg-gradient-to-r from-yellow-600 to-yellow-800 dark:from-yellow-700 dark:to-yellow-600 text-white dark:text-gray-100 px-3 py-1 rounded-md hover:shadow-[0_0_10px_rgba(234,179,8,0.5)] dark:hover:shadow-[0_0_10px_rgba(45,212,191,0.5)] dark:hover:dark-glow hover:scale-105 transition-all duration-300 focus:outline-none focus:ring-2 focus:ring-yellow-500 dark:focus:ring-yellow-400 focus:ring-offset-2 dark:focus:ring-offset-indigo-800 text-sm flex items-center space-x-1"
                            ref={(el) =>
                              (editGroupButtonRefs.current[group._id] = el)
                            }
                            aria-label={`Edit ${group.name}`}
                          >
                            <i className="fa-solid fa-edit"></i>
                            <span>Edit Group</span>
                          </button>
                          <button
                            onClick={() => handleDeleteGroup(group._id)}
                            className="bg-gradient-to-r from-red-600 to-red-800 dark:from-red-700 dark:to-red-600 text-white dark:text-gray-100 px-3 py-1 rounded-md hover:shadow-[0_0_10px_rgba(239,68,68,0.5)] dark:hover:shadow-[0_0_10px_rgba(45,212,191,0.5)] dark:hover:dark-glow hover:scale-105 transition-all duration-300 focus:outline-none focus:ring-2 focus:ring-red-500 dark:focus:ring-red-400 focus:ring-offset-2 dark:focus:ring-offset-indigo-800 text-sm flex items-center space-x-1"
                            ref={(el) =>
                              (deleteGroupButtonRefs.current[group._id] = el)
                            }
                            aria-label={`Delete ${group.name}`}
                          >
                            <i className="fa-solid fa-trash"></i>
                            <span>Delete Group</span>
                          </button>
                        </>
                      ) : (
                        <button
                          onClick={() => handleLeaveGroup(group._id)}
                          className="bg-gradient-to-r from-red-600 to-red-800 dark:from-red-700 dark:to-red-600 text-white dark:text-gray-100 px-3 py-1 rounded-md hover:shadow-[0_0_10px_rgba(239,68,68,0.5)] dark:hover:shadow-[0_0_10px_rgba(45,212,191,0.5)] dark:hover:dark-glow hover:scale-105 transition-all duration-300 focus:outline-none focus:ring-2 focus:ring-red-500 dark:focus:ring-red-400 focus:ring-offset-2 dark:focus:ring-offset-indigo-800 text-sm flex items-center space-x-1"
                          ref={(el) =>
                            (leaveGroupButtonRefs.current[group._id] = el)
                          }
                          aria-label={`Leave ${group.name}`}
                        >
                          <i className="fa-solid fa-right-from-bracket"></i>
                          <span>Leave Group</span>
                        </button>
                      )
                    ) : (
                      <button
                        onClick={() => handleJoinGroup(group._id)}
                        className="bg-gradient-to-r from-blue-600 to-indigo-600 dark:from-teal-600 dark:to-teal-400 text-white dark:text-gray-100 px-3 py-1 rounded-md hover grupos:shadow-[0_0_10px_rgba(59,130,246,0.5)] dark:hover:shadow-[0_0_10px_rgba(45,212,191,0.5)] dark:hover:dark-glow hover:scale-105 transition-all duration-300 focus:outline-none focus:ring-2 focus:ring-blue-500 dark:focus:ring-teal-400 focus:ring-offset-2 dark:focus:ring-offset-indigo-800 text-sm flex items-center space-x-1"
                        ref={(el) =>
                          (joinGroupButtonRefs.current[group._id] = el)
                        }
                        aria-label={`Join ${group.name}`}
                      >
                        <i className="fa-solid fa-right-to-bracket"></i>
                        <span>Join Group</span>
                      </button>
                    )}
                  </div>
                )}
              </div>
            ))}
          </div>
        ) : (
          <p className="text-center text-lg bg-clip-text text-transparent bg-gradient-to-r from-blue-600 to-indigo-600 dark:from-amber-400 dark:to-amber-300 transition-transform duration-200 hover:scale-105">
            No study groups available.
          </p>
        )}
      </div>
    </section>
  );
}

export default StudyGroups;
