import React, { useContext, useState } from "react";
import { NavLink, useNavigate } from "react-router-dom";
import { AuthContext } from "../context/AuthContext";

function Navbar() {
  const { user, logout } = useContext(AuthContext);
  const navigate = useNavigate();
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);

  const handleLogout = () => {
    logout();
    navigate("/");
    setIsMobileMenuOpen(false); // Close mobile menu on logout
  };

  const toggleMobileMenu = () => {
    setIsMobileMenuOpen((prev) => !prev);
  };

  // Base styles for NavLink (shared between desktop and mobile)
  const navLinkBaseStyles = `
    relative px-4 py-2 rounded-md text-white font-medium
    transition-all duration-200 ease-in-out
  `;

  const navLinkHoverStyles = `
    hover:bg-blue-600 hover:scale-105
  `;

  const navLinkActiveStyles = `
    bg-blue-400 scale-105 shadow-md
  `;

  return (
    <nav
      className="bg-gradient-to-r from-blue-600 to-blue-700 p-4 shadow-lg sticky top-0 z-50"
      role="navigation"
    >
      <div className="container mx-auto flex justify-between items-center">
        {/* Logo */}
        <NavLink
          to="/"
          className="text-white text-2xl font-bold transform hover:scale-105 transition-all duration-200"
          aria-label="NexusEd Home"
        >
          NexusEd
        </NavLink>

        {/* Hamburger Icon for Mobile */}
        <button
          className="md:hidden text-white focus:outline-none focus:ring-2 focus:ring-blue-200 rounded-md"
          onClick={toggleMobileMenu}
          aria-expanded={isMobileMenuOpen}
          aria-controls="mobile-menu"
          aria-label="Toggle navigation menu"
        >
          <svg
            className="w-6 h-6"
            fill="none"
            stroke="currentColor"
            viewBox="0 0 24 24"
            xmlns="http://www.w3.org/2000/svg"
          >
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth="2"
              d={
                isMobileMenuOpen
                  ? "M6 18L18 6M6 6l12 12"
                  : "M4 6h16M4 12h16M4 18h16"
              }
            />
          </svg>
        </button>

        {/* Desktop Menu */}
        <div className="hidden md:flex md:space-x-4">
          <NavLink
            to="/"
            className={({ isActive }) =>
              `${navLinkBaseStyles} ${
                isActive ? navLinkActiveStyles : navLinkHoverStyles
              }`
            }
            aria-label="Home"
          >
            Home
          </NavLink>
          {user ? (
            <>
              <NavLink
                to="/profile"
                className={({ isActive }) =>
                  `${navLinkBaseStyles} ${
                    isActive ? navLinkActiveStyles : navLinkHoverStyles
                  }`
                }
                aria-label="Profile"
              >
                Profile
              </NavLink>
              <button
                type="button"
                onClick={handleLogout}
                className="bg-red-600 text-white px-4 py-2 rounded-md hover:bg-red-700 hover:scale-105 transition-all duration-200 ease-in-out focus:outline-none focus:ring-2 focus:ring-red-500 focus:ring-offset-2"
                aria-label="Logout"
              >
                Logout
              </button>
            </>
          ) : (
            <>
              <NavLink
                to="/login"
                className={({ isActive }) =>
                  `${navLinkBaseStyles} ${
                    isActive ? navLinkActiveStyles : navLinkHoverStyles
                  }`
                }
                aria-label="Login"
              >
                Login
              </NavLink>
              <NavLink
                to="/register"
                className={({ isActive }) =>
                  `${navLinkBaseStyles} ${
                    isActive ? navLinkActiveStyles : navLinkHoverStyles
                  }`
                }
                aria-label="Register"
              >
                Register
              </NavLink>
            </>
          )}
        </div>
      </div>

      {/* Mobile Menu */}
      {isMobileMenuOpen && (
        <div id="mobile-menu" className="md:hidden mt-4 space-y-3">
          <NavLink
            to="/"
            className={({ isActive }) =>
              `${navLinkBaseStyles} block ${
                isActive ? navLinkActiveStyles : navLinkHoverStyles
              }`
            }
            onClick={() => setIsMobileMenuOpen(false)}
            aria-label="Home"
          >
            Home
          </NavLink>
          {user ? (
            <>
              <NavLink
                to="/profile"
                className={({ isActive }) =>
                  `${navLinkBaseStyles} block ${
                    isActive ? navLinkActiveStyles : navLinkHoverStyles
                  }`
                }
                onClick={() => setIsMobileMenuOpen(false)}
                aria-label="Profile"
              >
                Profile
              </NavLink>
              <button
                type="button"
                onClick={handleLogout}
                className="block w-full text-left text-white px-4 py-2 rounded-md bg-red-600 hover:bg-red-700 hover:scale-105 transition-all duration-200 ease-in-out focus:outline-none focus:ring-2 focus:ring-red-200 focus:ring-offset-2"
                aria-label="Logout"
              >
                Logout
              </button>
            </>
          ) : (
            <>
              <NavLink
                to="/login"
                className={({ isActive }) =>
                  `${navLinkBaseStyles} block ${
                    isActive ? navLinkActiveStyles : navLinkHoverStyles
                  }`
                }
                onClick={() => setIsMobileMenuOpen(false)}
                aria-label="Login"
              >
                Login
              </NavLink>
              <NavLink
                to="/register"
                className={({ isActive }) =>
                  `${navLinkBaseStyles} block ${
                    isActive ? navLinkActiveStyles : navLinkHoverStyles
                  }`
                }
                onClick={() => setIsMobileMenuOpen(false)}
                aria-label="Register"
              >
                Register
              </NavLink>
            </>
          )}
        </div>
      )}
    </nav>
  );
}

export default Navbar;
