import React, { memo } from "react";
import { Row, Col, Tab, Nav, Spinner, Alert } from "react-bootstrap";
import useProfile from "../hooks/useProfile";
import ProfileHeader from "./ProfileHeader";
import ProfileGroups from "./ProfileGroups";
import UserPosts from "./UserPosts";
import "../styles/profileStyles.css";

// Use memo to prevent unnecessary re-renders
const Profile = memo(function Profile() {
  const {
    user,
    authLoading,
    posts,
    setPosts,
    isPostsLoading,
    joinedGroups,
    isGroupsLoading,
    bio,
    setBio,
    username,
    setUsername,
    profilePicture,
    isEditing,
    setIsEditing,
    error,
    uploadError,
    success,
    isLoading,
    sortOption,
    setSortOption,
    imageLoadError,
    setImageLoadError,
    setTemporaryMessage,
    setTemporarySuccess,
    handleProfilePictureChange,
    handleRemoveProfilePicture,
    handleBioUpdate,
    BIO_MAX_LENGTH,
    USERNAME_MIN_LENGTH,
    USERNAME_MAX_LENGTH,
    DEFAULT_BIO,
  } = useProfile();

  if (authLoading) {
    return (
      <div className="text-center py-5">
        <Spinner animation="border" variant="primary" />
        <p className="mt-3 text-secondary">Loading profile...</p>
      </div>
    );
  }

  if (!user) {
    return (
      <Alert variant="info" className="text-center py-5">
        <Alert.Heading>Not Logged In</Alert.Heading>
        <p>Please log in to view your profile.</p>
      </Alert>
    );
  }

  return (
    <div className="animate-fade-in-up">
      {error && (
        <Alert variant="danger" className="mb-4">
          {error}
        </Alert>
      )}

      {success && (
        <Alert variant="success" className="mb-4">
          {success}
        </Alert>
      )}

      <ProfileHeader
        user={user}
        bio={bio}
        setBio={setBio}
        username={username}
        setUsername={setUsername}
        profilePicture={profilePicture}
        isEditing={isEditing}
        setIsEditing={setIsEditing}
        uploadError={uploadError}
        isLoading={isLoading}
        imageLoadError={imageLoadError}
        setImageLoadError={setImageLoadError}
        handleProfilePictureChange={handleProfilePictureChange}
        handleRemoveProfilePicture={handleRemoveProfilePicture}
        handleBioUpdate={handleBioUpdate}
        BIO_MAX_LENGTH={BIO_MAX_LENGTH}
        USERNAME_MIN_LENGTH={USERNAME_MIN_LENGTH}
        USERNAME_MAX_LENGTH={USERNAME_MAX_LENGTH}
        DEFAULT_BIO={DEFAULT_BIO}
      />

      <div className="profile-content-section">
        <Tab.Container id="profile-tabs" defaultActiveKey="posts">
          <div className="profile-tabs-wrapper mb-4">
            <Nav variant="pills" className="profile-nav-enhanced justify-content-center">
              <Nav.Item>
                <Nav.Link eventKey="posts" className="profile-nav-link px-4 py-3 me-2">
                  <i className="fas fa-newspaper me-2"></i>
                  <span className="nav-text">Posts</span>
                  <span className="nav-badge">{posts.length}</span>
                </Nav.Link>
              </Nav.Item>
              <Nav.Item>
                <Nav.Link eventKey="groups" className="profile-nav-link px-4 py-3 me-2">
                  <i className="fas fa-users me-2"></i>
                  <span className="nav-text">Groups</span>
                  <span className="nav-badge">{joinedGroups.length}</span>
                </Nav.Link>
              </Nav.Item>
              <Nav.Item>
                <Nav.Link eventKey="activity" className="profile-nav-link px-4 py-3">
                  <i className="fas fa-chart-line me-2"></i>
                  <span className="nav-text">Activity</span>
                  <span className="nav-badge">New</span>
                </Nav.Link>
              </Nav.Item>
            </Nav>
          </div>

          <Tab.Content className="tab-content-enhanced">
            <Tab.Pane eventKey="posts" className="fade-in-content">
              <div className="content-section">
                <UserPosts
                  posts={posts}
                  setPosts={setPosts}
                  isLoading={isPostsLoading}
                  userId={user.id}
                  sortOption={sortOption}
                  setSortOption={setSortOption}
                  setTemporaryMessage={setTemporaryMessage}
                  setTemporarySuccess={setTemporarySuccess}
                />
              </div>
            </Tab.Pane>
            <Tab.Pane eventKey="groups" className="fade-in-content">
              <div className="content-section">
                <ProfileGroups
                  groups={joinedGroups}
                  isLoading={isGroupsLoading}
                  setTemporaryMessage={setTemporaryMessage}
                  setTemporarySuccess={setTemporarySuccess}
                />
              </div>
            </Tab.Pane>
            <Tab.Pane eventKey="activity" className="fade-in-content">
              <div className="content-section">
                <div className="activity-dashboard">
                  <div className="row g-4">
                    <div className="col-md-6">
                      <div className="activity-card">
                        <div className="activity-icon-wrapper">
                          <i className="fas fa-fire text-danger"></i>
                        </div>
                        <h5>Activity Streak</h5>
                        <p className="h3 mb-1">7 days</p>
                        <small className="text-muted">Keep it up!</small>
                      </div>
                    </div>
                    <div className="col-md-6">
                      <div className="activity-card">
                        <div className="activity-icon-wrapper">
                          <i className="fas fa-trophy text-warning"></i>
                        </div>
                        <h5>Achievements</h5>
                        <p className="h3 mb-1">5</p>
                        <small className="text-muted">Badges earned</small>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </Tab.Pane>
          </Tab.Content>
        </Tab.Container>
      </div>
    </div>
  );
});

export default Profile;
