// 2. Fixed ProfileHeader.js
import React, { useRef, useCallback } from "react";
import {
  Card,
  Row,
  Col,
  Form,
  Button,
  InputGroup,
  Alert,
  Spinner,
} from "react-bootstrap";

function ProfileHeader({
  user,
  bio,
  setBio,
  username,
  setUsername,
  profilePicture,
  isEditing,
  setIsEditing,
  uploadError,
  isLoading,
  imageLoadError,
  setImageLoadError,
  handleProfilePictureChange,
  handleRemoveProfilePicture,
  handleBioUpdate,
  BIO_MAX_LENGTH,
  USERNAME_MIN_LENGTH,
  USERNAME_MAX_LENGTH,
  DEFAULT_BIO,
}) {
  const fileInputRef = useRef(null);
  const isCurrentUser = true; // In a real app, check if the profile belongs to the logged-in user

  // Use useCallback to memoize the onError handler to prevent infinite re-renders
  const handleImageError = useCallback(() => {
    if (!imageLoadError) {
      setImageLoadError(true);
    }
  }, [imageLoadError, setImageLoadError]);

  const handleFileInputClick = () => {
    fileInputRef.current.click();
  };

  const handleFileChange = (e) => {
    handleProfilePictureChange(e, fileInputRef);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    handleBioUpdate(e);
  };

  const handleCancel = () => {
    setBio(user.bio || DEFAULT_BIO);
    setUsername(user.username);
    setIsEditing(false);
  };

  const handleRemoveImage = () => {
    handleRemoveProfilePicture(fileInputRef);
  };

  return (
    <div className="profile-header-enhanced position-relative overflow-hidden mb-4">
      <div className="profile-background-gradient"></div>
      <div className="profile-background-pattern"></div>
      
      <Card className="border-0 bg-transparent">
        <Card.Body className="p-4 position-relative">
          <Row className="align-items-center">
            <Col md={4} className="text-center text-md-start mb-4 mb-md-0">
              <div className="profile-avatar-container position-relative d-inline-block">
                <div className="profile-avatar-wrapper">
                  <div
                    className="profile-avatar rounded-circle overflow-hidden position-relative"
                    style={{ width: "160px", height: "160px" }}
                  >
                    {profilePicture && !imageLoadError ? (
                      <img
                        src={`${process.env.REACT_APP_API_URL}${profilePicture}`}
                        alt={username}
                        className="w-100 h-100 object-fit-cover"
                        onError={handleImageError}
                      />
                    ) : (
                      <div className="w-100 h-100 d-flex align-items-center justify-content-center bg-light">
                        <i className="fas fa-user display-4 text-secondary"></i>
                      </div>
                    )}
                    <div className="profile-avatar-overlay"></div>
                  </div>

                  {isCurrentUser && (
                    <div className="profile-upload-btn position-absolute">
                      <input
                        type="file"
                        ref={fileInputRef}
                        onChange={handleFileChange}
                        className="d-none"
                        accept="image/*"
                      />
                      <Button
                        variant="light"
                        className="rounded-circle shadow-lg profile-camera-btn"
                        onClick={handleFileInputClick}
                        disabled={isLoading}
                      >
                        {isLoading ? (
                          <Spinner animation="border" size="sm" />
                        ) : (
                          <i className="fas fa-camera"></i>
                        )}
                      </Button>
                    </div>
                  )}
                </div>

                {uploadError && (
                  <Alert variant="danger" className="mt-3 text-start small">
                    <i className="fas fa-exclamation-circle me-2"></i>
                    {uploadError}
                  </Alert>
                )}
              </div>
            </Col>

          <Col md={8}>
            <div className="profile-info-content text-white">
              {isEditing ? (
                <Form onSubmit={handleSubmit} className="profile-edit-form">
                  <div className="mb-3">
                    <Form.Label htmlFor="username" className="text-white fw-semibold">
                      Username
                    </Form.Label>
                    <InputGroup>
                      <Form.Control
                        id="username"
                        type="text"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                        minLength={USERNAME_MIN_LENGTH}
                        maxLength={USERNAME_MAX_LENGTH}
                        required
                        className="form-control-enhanced"
                      />
                    </InputGroup>
                    <Form.Text className="text-light opacity-75">
                      {USERNAME_MIN_LENGTH}-{USERNAME_MAX_LENGTH} characters
                    </Form.Text>
                  </div>

                  <div className="mb-4">
                    <Form.Label htmlFor="bio" className="text-white fw-semibold">
                      Bio
                    </Form.Label>
                    <Form.Control
                      id="bio"
                      as="textarea"
                      rows={3}
                      value={bio}
                      onChange={(e) => setBio(e.target.value)}
                      maxLength={BIO_MAX_LENGTH}
                      placeholder={DEFAULT_BIO}
                      className="form-control-enhanced"
                    />
                    <div className="d-flex justify-content-between mt-1">
                      <Form.Text className="text-light opacity-75">
                        Tell others about yourself
                      </Form.Text>
                      <Form.Text className={`${bio.length > BIO_MAX_LENGTH * 0.9 ? 'text-warning' : 'text-light opacity-75'}`}>
                        {bio.length}/{BIO_MAX_LENGTH}
                      </Form.Text>
                    </div>
                  </div>

                  <div className="d-flex gap-3">
                    <Button
                      type="button"
                      variant="outline-light"
                      onClick={handleCancel}
                      disabled={isLoading}
                      className="px-4"
                    >
                      <i className="fas fa-times me-2"></i>
                      Cancel
                    </Button>
                    <Button 
                      type="submit" 
                      variant="light" 
                      disabled={isLoading}
                      className="px-4 btn-save-profile"
                    >
                      {isLoading ? (
                        <>
                          <Spinner animation="border" size="sm" className="me-2" />
                          Saving...
                        </>
                      ) : (
                        <>
                          <i className="fas fa-check me-2"></i>
                          Save Changes
                        </>
                      )}
                    </Button>
                  </div>
                </Form>
              ) : (
                <>
                  <div className="d-flex justify-content-between align-items-start mb-3">
                    <div className="profile-user-info">
                      <h1 className="profile-username display-6 fw-bold mb-2 text-white">
                        {user.username}
                        <i className="fas fa-certificate text-primary ms-2" title="Verified User"></i>
                      </h1>
                      <div className="profile-member-since d-flex align-items-center mb-3">
                        <i className="fas fa-calendar-alt me-2 text-light opacity-75"></i>
                        <span className="text-light opacity-90">
                          Member since {new Date(user.createdAt).toLocaleDateString('en-US', { 
                            year: 'numeric', 
                            month: 'long', 
                            day: 'numeric' 
                          })}
                        </span>
                      </div>
                    </div>

                    {isCurrentUser && (
                      <Button
                        variant="outline-light"
                        size="sm"
                        onClick={() => setIsEditing(true)}
                        className="btn-edit-profile px-3 py-2"
                      >
                        <i className="fas fa-edit me-2"></i>
                        Edit Profile
                      </Button>
                    )}
                  </div>

                  <div className="profile-bio mb-4">
                    <p className="fs-6 text-light mb-0 lh-relaxed">
                      {user.bio || DEFAULT_BIO}
                    </p>
                  </div>

                  <div className="profile-stats d-flex gap-4 mb-3">
                    <div className="stat-item text-center">
                      <div className="stat-value h4 mb-1 text-white fw-bold">24</div>
                      <div className="stat-label text-light opacity-75 small">Posts</div>
                    </div>
                    <div className="stat-item text-center">
                      <div className="stat-value h4 mb-1 text-white fw-bold">156</div>
                      <div className="stat-label text-light opacity-75 small">Likes</div>
                    </div>
                    <div className="stat-item text-center">
                      <div className="stat-value h4 mb-1 text-white fw-bold">3</div>
                      <div className="stat-label text-light opacity-75 small">Groups</div>
                    </div>
                  </div>

                  {profilePicture && isCurrentUser && (
                    <Button
                      variant="outline-danger"
                      size="sm"
                      onClick={handleRemoveImage}
                      disabled={isLoading}
                      className="btn-remove-picture"
                    >
                      <i className="fas fa-trash me-2"></i>
                      Remove Profile Picture
                    </Button>
                  )}
                </>
              )}
            </div>
          </Col>
        </Row>
      </Card.Body>
    </Card>
    </div>
  );
}

export default ProfileHeader;
