import React, { useState, useEffect, useRef } from "react";

function PostCard({
  title,
  content,
  author,
  id,
  setPosts,
  currentUser,
  comments,
  likes,
  post,
}) {
  const [commentContent, setCommentContent] = useState("");
  const [error, setError] = useState("");
  const timeoutRef = useRef(null);

  // Clear previous timeout when setting a new error
  const setTemporaryError = (message) => {
    if (timeoutRef.current) {
      clearTimeout(timeoutRef.current);
    }
    setError(message);
    timeoutRef.current = setTimeout(() => {
      setError("");
      timeoutRef.current = null;
    }, 3000);
  };

  // Cleanup timeout on component unmount
  useEffect(() => {
    return () => {
      if (timeoutRef.current) {
        clearTimeout(timeoutRef.current);
      }
    };
  }, []);

  const handleDeletePost = async () => {
    try {
      const token = localStorage.getItem("token");
      const response = await fetch(
        `${process.env.REACT_APP_API_URL}/api/posts/${id}`,
        {
          method: "DELETE",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`,
          },
        }
      );
      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || "Failed to delete post");
      }
      setPosts((prevPosts) => prevPosts.filter((post) => post._id !== id));
      setError("");
    } catch (err) {
      setTemporaryError(err.message || "Failed to delete post");
    }
  };

  const handleDeleteComment = async (commentId) => {
    try {
      const token = localStorage.getItem("token");
      const response = await fetch(
        `${process.env.REACT_APP_API_URL}/api/posts/${id}/comments/${commentId}`,
        {
          method: "DELETE",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`,
          },
        }
      );
      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || "Failed to delete comment");
      }
      const updatedPost = await response.json();
      setPosts((prevPosts) =>
        prevPosts.map((post) =>
          post._id === id
            ? {
                ...updatedPost,
                author: updatedPost.author?.username
                  ? updatedPost.author
                  : { username: author, _id: post.author?._id },
              }
            : post
        )
      );
      setError("");
    } catch (err) {
      setTemporaryError(err.message || "Failed to delete comment");
    }
  };

  const handleCommentSubmit = async (e) => {
    e.preventDefault();
    setError("");

    if (!commentContent.trim()) {
      setTemporaryError("Comment cannot be empty");
      return;
    }

    try {
      const token = localStorage.getItem("token");
      const response = await fetch(
        `${process.env.REACT_APP_API_URL}/api/posts/${id}/comments`,
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`,
          },
          body: JSON.stringify({ content: commentContent }),
        }
      );

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || "Failed to add comment");
      }

      const updatedPost = await response.json();
      setPosts((prevPosts) =>
        prevPosts.map((post) =>
          post._id === id
            ? {
                ...updatedPost,
                author: updatedPost.author?.username
                  ? updatedPost.author
                  : { username: author, _id: post.author?._id },
              }
            : post
        )
      );
      setCommentContent("");
      setError("");
    } catch (err) {
      setTemporaryError(err.message || "Failed to add comment");
    }
  };

  const handleLikePost = async () => {
    try {
      const token = localStorage.getItem("token");
      if (!token || !currentUser) {
        throw new Error("User must be logged in to like a post");
      }

      const response = await fetch(
        `${process.env.REACT_APP_API_URL}/api/posts/${id}/like`,
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`,
          },
        }
      );

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(
          errorData?.message || `Failed to like post (HTTP ${response.status})`
        );
      }

      const updatedPost = await response.json();
      if (!updatedPost._id) {
        throw new Error("Invalid response: Missing _id");
      }

      setPosts((prevPosts) => {
        const newPosts = prevPosts.map((post) => {
          if (post._id === id) {
            const newAuthor =
              typeof updatedPost.author === "object" &&
              updatedPost.author?.username
                ? updatedPost.author
                : {
                    username: author,
                    _id:
                      typeof updatedPost.author === "string"
                        ? updatedPost.author
                        : post.author?._id,
                  };
            const updated = {
              ...updatedPost,
              author: newAuthor,
              comments: Array.isArray(updatedPost.comments)
                ? updatedPost.comments
                : post.comments || [],
              likes: Array.isArray(updatedPost.likes)
                ? updatedPost.likes
                : post.likes || [],
            };
            return updated;
          }
          return post;
        });
        return newPosts;
      });
      setError("");
    } catch (err) {
      setTemporaryError(err.message || "Failed to like post");
    }
  };

  const handleLikeComment = async (commentId) => {
    try {
      const token = localStorage.getItem("token");
      if (!token || !currentUser) {
        throw new Error("User must be logged in to like a comment");
      }

      const response = await fetch(
        `${process.env.REACT_APP_API_URL}/api/posts/${id}/comments/${commentId}/like`,
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`,
          },
        }
      );

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(
          errorData.message ||
            `Failed to like comment (HTTP ${response.status})`
        );
      }

      const updatedPost = await response.json();
      setPosts((prevPosts) =>
        prevPosts.map((post) =>
          post._id === id
            ? {
                ...updatedPost,
                author:
                  typeof updatedPost.author === "object" &&
                  updatedPost.author?.username
                    ? updatedPost.author
                    : {
                        username: author,
                        _id:
                          typeof updatedPost.author === "string"
                            ? updatedPost.author
                            : post.author?._id,
                      },
              }
            : post
        )
      );
      setError("");
    } catch (err) {
      setTemporaryError(err.message || "Failed to like comment");
    }
  };

  return (
    <div className="bg-white p-6 rounded-lg shadow-md mb-4">
      <h2 className="text-xl font-semibold text-gray-800">{title}</h2>
      <p className="text-gray-600 mt-2">{content}</p>
      <p className="text-gray-500 text-sm mt-2">Posted by {author}</p>
      <div className="mt-2 flex items-center space-x-4">
        {currentUser && (
          <button
            onClick={handleLikePost}
            className="bg-blue-600 text-white px-3 py-1 rounded-md hover:bg-blue-700 disabled:opacity-50 text-sm"
            disabled={!currentUser}
          >
            {likes && currentUser && likes.includes(currentUser.id.toString())
              ? "Unlike"
              : "Like"}{" "}
            ({likes ? likes.length : 0})
          </button>
        )}
        {currentUser && post.author?._id?.toString() === currentUser.id && (
          <button
            onClick={handleDeletePost}
            className="bg-red-600 text-white px-3 py-1 rounded-md hover:bg-red-700 text-sm"
          >
            Delete
          </button>
        )}
      </div>
      {error && (
        <div className="bg-red-100 text-red-700 p-2 mt-2 rounded-md">
          {error}
        </div>
      )}
      <div className="mt-4">
        <h4 className="text-lg font-semibold text-gray-800">Comments</h4>
        {comments && comments.length > 0 ? (
          comments.map((comment) => (
            <div key={comment._id} className="bg-gray-100 p-2 mt-2 rounded-md">
              <p className="text-gray-600">{comment.content}</p>
              <p className="text-gray-500 text-sm">
                By {comment.author?.username || "Unknown"} on{" "}
                {new Date(comment.createdAt).toLocaleDateString()}
              </p>
              <div className="flex items-center space-x-4 mt-1">
                {currentUser && (
                  <button
                    onClick={() => handleLikeComment(comment._id)}
                    className="bg-blue-600 text-white px-3 py-1 rounded-md hover:bg-blue-700 disabled:opacity-50 text-sm"
                    disabled={!currentUser}
                  >
                    {comment.likes &&
                    currentUser &&
                    comment.likes.includes(currentUser.id.toString())
                      ? "Unlike"
                      : "Like"}{" "}
                    ({comment.likes ? comment.likes.length : 0})
                  </button>
                )}
                {currentUser &&
                  comment.author?._id?.toString() === currentUser.id && (
                    <button
                      onClick={() => handleDeleteComment(comment._id)}
                      className="bg-red-600 text-white px-3 py-1 rounded-md hover:bg-red-700 text-sm"
                    >
                      Delete
                    </button>
                  )}
              </div>
            </div>
          ))
        ) : (
          <p className="text-gray-600 text-sm">No comments yet.</p>
        )}
        {currentUser && (
          <form onSubmit={handleCommentSubmit} className="mt-4">
            <textarea
              value={commentContent}
              onChange={(e) => setCommentContent(e.target.value)}
              placeholder="Add a comment"
              className="w-full p-2 mb-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-blue-600"
              rows="2"
            />
            <button
              type="submit"
              className="bg-blue-600 text-white px-4 py-2 rounded-md hover:bg-blue-700"
            >
              Comment
            </button>
          </form>
        )}
      </div>
    </div>
  );
}

export default PostCard;
