import React, { useState, useEffect, useContext } from "react";
import PostCard from "./PostCard";
import { AuthContext } from "../context/AuthContext";
import axios from "axios";

function Profile() {
  const { user, setUser } = useContext(AuthContext);
  const [posts, setPosts] = useState([]);
  const [bio, setBio] = useState("");
  const [isEditing, setIsEditing] = useState(false);
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");

  useEffect(() => {
    if (user) {
      setBio(user.bio || "");
      const fetchUserPosts = async () => {
        try {
          const response = await fetch(
            `${process.env.REACT_APP_API_URL}/api/posts?author=${user.id}`
          );
          if (!response.ok) {
            throw new Error(`Failed to fetch user posts: ${response.status}`);
          }
          const data = await response.json();
          setPosts(data);
        } catch (err) {
          console.error("Error fetching user posts:", err);
        }
      };
      fetchUserPosts();
    }
  }, [user]);

  const handleBioUpdate = async (e) => {
    e.preventDefault();
    setError("");
    setSuccess("");

    if (!bio.trim()) {
      setError("Bio cannot be empty");
      return;
    }

    try {
      const token = localStorage.getItem("token");
      console.log("Bio update - Token:", token ? "Present" : "Missing");
      console.log("Bio update - User:", user);
      console.log("Bio update - setUser type:", typeof setUser);
      console.log("Bio update - Payload:", { bio });
      console.log(
        "Bio update - API URL:",
        `${process.env.REACT_APP_API_URL}/api/users/profile`
      );

      if (!token) {
        throw new Error("No authentication token found. Please log in again.");
      }
      if (typeof setUser !== "function") {
        throw new Error(
          "setUser is not a function. Ensure AuthProvider wraps the app."
        );
      }

      const response = await axios.put(
        `${process.env.REACT_APP_API_URL}/api/users/profile`,
        { bio },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      console.log("Bio update - Response:", response.data);
      const updatedUser = {
        id: response.data.id,
        username: response.data.username,
        email: response.data.email,
        bio: response.data.bio,
      };
      setUser(updatedUser);
      setIsEditing(false);
      setSuccess("Bio updated successfully");
    } catch (err) {
      const errorMessage =
        err.response?.data?.message || err.message || "Failed to update bio";
      console.error("Bio update - Error:", {
        message: errorMessage,
        status: err.response?.status,
        data: err.response?.data,
      });
      setError(errorMessage);
    }
  };

  if (!user) {
    return (
      <div className="container mx-auto p-4">
        <p className="text-gray-600">Please log in to view your profile.</p>
      </div>
    );
  }

  return (
    <div className="container mx-auto p-4">
      <div className="bg-white p-6 rounded-lg shadow-md mb-6">
        <h2 className="text-2xl font-semibold text-gray-800 mb-2">
          {user.username}
        </h2>
        {isEditing ? (
          <form onSubmit={handleBioUpdate} className="mt-4">
            <textarea
              value={bio}
              onChange={(e) => setBio(e.target.value)}
              placeholder="Update your bio"
              className="w-full p-2 mb-4 border rounded-md focus:outline-none focus:ring-2 focus:ring-blue-600"
              rows="4"
            />
            {error && (
              <div className="bg-red-100 text-red-700 p-2 mb-4 rounded-md">
                {error}
              </div>
            )}
            <button
              type="submit"
              className="bg-blue-600 text-white px-4 py-2 rounded-md hover:bg-blue-700 mr-2"
            >
              Save
            </button>
            <button
              type="button"
              onClick={() => {
                setIsEditing(false);
                setBio(user.bio || "");
                setError("");
              }}
              className="bg-gray-300 text-gray-800 px-4 py-2 rounded-md hover:bg-gray-400"
            >
              Cancel
            </button>
          </form>
        ) : (
          <>
            <p className="text-gray-600">{user.bio || "No bio set"}</p>
            <button
              onClick={() => setIsEditing(true)}
              className="mt-4 bg-blue-600 text-white px-4 py-2 rounded-md hover:bg-blue-700"
            >
              Edit Bio
            </button>
            {success && (
              <div className="bg-green-100 text-green-700 p-2 mt-4 rounded-md">
                {success}
              </div>
            )}
          </>
        )}
      </div>
      <h3 className="text-xl font-semibold text-gray-800 mb-4">My Posts</h3>
      <div>
        {posts.length > 0 ? (
          posts.map((post) => (
            <PostCard
              key={post._id}
              id={post._id}
              title={post.title}
              content={post.content}
              author={post.author?.username || "Unknown"}
              comments={post.comments}
              likes={post.likes}
              post={post}
              setPosts={setPosts}
              currentUser={user}
            />
          ))
        ) : (
          <p className="text-gray-600">No posts yet.</p>
        )}
      </div>
    </div>
  );
}

export default Profile;
