import React, { useState, useContext, useEffect } from "react";
import { Link, NavLink as RouterNavLink, useNavigate } from "react-router-dom";
import { Navbar as BsNavbar, Nav, Container, Button } from "react-bootstrap";
import { AuthContext } from "../context/AuthContext";

function Navbar() {
  const { user, logout } = useContext(AuthContext);
  const navigate = useNavigate();
  const [darkMode, setDarkMode] = useState(false);
  const [expanded, setExpanded] = useState(false);
  const [isLoggingOut, setIsLoggingOut] = useState(false);

  // Check preferred color scheme and localStorage on component mount
  useEffect(() => {
    const storedDarkMode = localStorage.getItem("darkMode");
    if (storedDarkMode) {
      setDarkMode(storedDarkMode === "true");
    } else {
      const prefersDarkMode = window.matchMedia(
        "(prefers-color-scheme: dark)"
      ).matches;
      setDarkMode(prefersDarkMode);
    }
  }, []);

  // Update body data-bs-theme and localStorage when darkMode changes
  useEffect(() => {
    document.documentElement.setAttribute(
      "data-bs-theme",
      darkMode ? "dark" : "light"
    );
    localStorage.setItem("darkMode", darkMode);
  }, [darkMode]);

  const toggleDarkMode = () => {
    setDarkMode((prevMode) => !prevMode);
  };

  const handleLogout = async () => {
    setIsLoggingOut(true);
    setExpanded(false);

    // Add fade-out effect to the main content
    const mainContent = document.querySelector("main");
    const footer = document.querySelector("footer");

    if (mainContent) {
      mainContent.style.transition =
        "opacity 0.5s ease-out, transform 0.5s ease-out";
      mainContent.style.opacity = "0";
      mainContent.style.transform = "translateY(-20px)";
    }

    if (footer) {
      footer.style.transition = "opacity 0.5s ease-out";
      footer.style.opacity = "0";
    }

    // Show logout success message
    const logoutMessage = document.createElement("div");
    logoutMessage.innerHTML = `
      <div class="position-fixed top-50 start-50 translate-middle" style="z-index: 9999;">
        <div class="card shadow-lg border-0" style="min-width: 300px;">
          <div class="card-body text-center py-4">
            <div class="mb-3">
              <i class="fas fa-check-circle text-success" style="font-size: 3rem;"></i>
            </div>
            <h5 class="card-title text-success mb-2">Logged Out Successfully!</h5>
            <p class="card-text text-muted mb-0">Redirecting to homepage...</p>
          </div>
        </div>
      </div>
    `;

    // Add backdrop
    const backdrop = document.createElement("div");
    backdrop.className = "position-fixed top-0 start-0 w-100 h-100";
    backdrop.style.backgroundColor = "rgba(0, 0, 0, 0.5)";
    backdrop.style.zIndex = "9998";
    backdrop.style.opacity = "0";
    backdrop.style.transition = "opacity 0.3s ease-in-out";

    document.body.appendChild(backdrop);
    document.body.appendChild(logoutMessage);

    // Fade in the backdrop and message
    setTimeout(() => {
      backdrop.style.opacity = "1";
    }, 100);

    // Animate the message card
    const messageCard = logoutMessage.querySelector(".card");
    messageCard.style.transform = "scale(0.8)";
    messageCard.style.opacity = "0";
    messageCard.style.transition = "all 0.4s cubic-bezier(0.34, 1.56, 0.64, 1)";

    setTimeout(() => {
      messageCard.style.transform = "scale(1)";
      messageCard.style.opacity = "1";
    }, 200);

    // Wait for animation, then logout and navigate
    setTimeout(() => {
      // Perform actual logout
      logout();

      // Fade out message
      messageCard.style.transform = "scale(0.9)";
      messageCard.style.opacity = "0";
      backdrop.style.opacity = "0";

      // Navigate to home and clean up
      setTimeout(() => {
        navigate("/");

        // Clean up elements
        if (document.body.contains(logoutMessage)) {
          document.body.removeChild(logoutMessage);
        }
        if (document.body.contains(backdrop)) {
          document.body.removeChild(backdrop);
        }

        // Reset main content styles
        if (mainContent) {
          mainContent.style.opacity = "1";
          mainContent.style.transform = "translateY(0)";
        }

        if (footer) {
          footer.style.opacity = "1";
        }

        setIsLoggingOut(false);
      }, 300);
    }, 1500); // Show success message for 1.5 seconds
  };

  return (
    <BsNavbar
      expand="lg"
      className={`navbar bg-gradient-primary text-white shadow-sm mb-4`}
      expanded={expanded}
      onToggle={setExpanded}
    >
      <Container>
        <BsNavbar.Brand
          as={Link}
          to="/"
          className="text-white fw-bold d-flex align-items-center"
        >
          <i className="fas fa-graduation-cap me-2 fs-4"></i>
          NexusEd
        </BsNavbar.Brand>

        <Button
          variant="link"
          className="text-white ms-2 text-decoration-none d-flex d-lg-none position-relative"
          onClick={toggleDarkMode}
          aria-label={darkMode ? "Switch to Light Mode" : "Switch to Dark Mode"}
          disabled={isLoggingOut}
        >
          <i className={`fas ${darkMode ? "fa-sun" : "fa-moon"}`}></i>
        </Button>

        <BsNavbar.Toggle
          aria-controls="basic-navbar-nav"
          className="border-0"
          disabled={isLoggingOut}
        >
          <i className="fas fa-bars text-white"></i>
        </BsNavbar.Toggle>

        <BsNavbar.Collapse id="basic-navbar-nav">
          <Nav className="ms-auto d-flex align-items-center">
            <Nav.Link
              as={RouterNavLink}
              to="/"
              className={({ isActive }) =>
                `text-white mx-1 ${isActive ? "active fw-bold" : ""} ${
                  isLoggingOut ? "opacity-50" : ""
                }`
              }
              onClick={() => setExpanded(false)}
              style={{ pointerEvents: isLoggingOut ? "none" : "auto" }}
            >
              Home
            </Nav.Link>

            {user && (
              <>
                <Nav.Link
                  as={RouterNavLink}
                  to="/profile"
                  className={({ isActive }) =>
                    `text-white mx-1 ${isActive ? "active fw-bold" : ""} ${
                      isLoggingOut ? "opacity-50" : ""
                    }`
                  }
                  onClick={() => setExpanded(false)}
                  style={{ pointerEvents: isLoggingOut ? "none" : "auto" }}
                >
                  Profile
                </Nav.Link>

                <Nav.Link
                  as={RouterNavLink}
                  to="/calendar"
                  className={({ isActive }) =>
                    `text-white mx-1 ${isActive ? "active fw-bold" : ""} ${
                      isLoggingOut ? "opacity-50" : ""
                    }`
                  }
                  onClick={() => setExpanded(false)}
                  style={{ pointerEvents: isLoggingOut ? "none" : "auto" }}
                >
                  Calendar
                </Nav.Link>
              </>
            )}

            <Nav.Link
              as={RouterNavLink}
              to="/study-groups"
              className={({ isActive }) =>
                `text-white mx-1 ${isActive ? "active fw-bold" : ""} ${
                  isLoggingOut ? "opacity-50" : ""
                }`
              }
              onClick={() => setExpanded(false)}
              style={{ pointerEvents: isLoggingOut ? "none" : "auto" }}
            >
              Study Groups
            </Nav.Link>

            <div className="ms-2 d-none d-lg-block">
              <Button
                variant="link"
                className="text-white text-decoration-none"
                onClick={toggleDarkMode}
                disabled={isLoggingOut}
                aria-label={
                  darkMode ? "Switch to Light Mode" : "Switch to Dark Mode"
                }
              >
                <i className={`fas ${darkMode ? "fa-sun" : "fa-moon"}`}></i>
              </Button>
            </div>

            {user ? (
              <Button
                variant="outline-light"
                className={`ms-2 btn-sm logout-btn ${
                  isLoggingOut ? "logging-out" : ""
                }`}
                onClick={handleLogout}
                disabled={isLoggingOut}
              >
                {isLoggingOut ? (
                  <>
                    <span
                      className="spinner-border spinner-border-sm me-2"
                      role="status"
                      aria-hidden="true"
                    ></span>
                    Logging out...
                  </>
                ) : (
                  <>
                    <i className="fas fa-sign-out-alt me-2"></i>
                    Logout
                  </>
                )}
              </Button>
            ) : (
              <>
                <Nav.Link
                  as={RouterNavLink}
                  to="/login"
                  className={({ isActive }) =>
                    `text-white mx-1 ${isActive ? "active fw-bold" : ""}`
                  }
                  onClick={() => setExpanded(false)}
                >
                  Login
                </Nav.Link>

                <Nav.Link
                  as={RouterNavLink}
                  to="/register"
                  className={({ isActive }) =>
                    `text-white mx-1 ${isActive ? "active fw-bold" : ""}`
                  }
                  onClick={() => setExpanded(false)}
                >
                  Register
                </Nav.Link>
              </>
            )}
          </Nav>
        </BsNavbar.Collapse>
      </Container>
    </BsNavbar>
  );
}

export default Navbar;
