import React, { useContext } from "react";
import { Routes, Route } from "react-router-dom";
import Navbar from "./components/Navbar";
import Home from "./components/Home";
import Profile from "./components/Profile";
import Login from "./components/Login";
import Register from "./components/Register";
import { ThemeContext, ThemeProvider } from "./context/ThemeContext";

function App() {
  return (
    <ThemeProvider>
      <AppContent />
    </ThemeProvider>
  );
}

function AppContent() {
  const { theme, toggleTheme } = useContext(ThemeContext);

  return (
    <div className="flex flex-col min-h-screen">
      <link
        rel="stylesheet"
        href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/6.5.1/css/all.min.css"
        integrity="sha512-DTOQO9RWCH3ppGqcWaEA1BIZOC6xxalwEsw9c2QQeAIftl+Vegovlnee1c9QX4TctnWMn13TZye+giMm8e2w=="
        crossOrigin="anonymous"
        referrerPolicy="no-referrer"
      />
      <style>
        {`
          @keyframes fadeInUp {
            0% { opacity: 0; transform: translateY(20px); }
            100% { opacity: 1; transform: translateY(0); }
          }
          @keyframes spin {
            0% { transform: rotate(0deg); }
            100% { transform: rotate(360deg); }
          }
          .animate-fade-in-up {
            animation: fadeInUp 0.5s ease-out forwards;
          }
          .hover-scale {
            transition: transform 0.2s ease-in-out;
          }
          .hover-scale:hover {
            transform: scale(1.05);
          }
          .social-icon {
            transition: all 0.3s ease-in-out;
          }
          .social-icon:hover {
            transform: scale(1.2);
            filter: brightness(1.2);
          }
          .theme-toggle {
            transition: all 0.3s ease-in-out;
          }
          .theme-toggle:hover {
            transform: rotate(360deg);
            filter: brightness(1.2);
          }
          [data-theme="light"] .theme-toggle {
            color: #2563eb;
          }
          [data-theme="dark"] .theme-toggle {
            color: #60a5fa;
          }
        `}
      </style>
      <Navbar />
      <main className="container mx-auto px-4 py-8 flex-grow">
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/profile" element={<Profile />} />
          <Route path="/login" element={<Login />} />
          <Route path="/register" element={<Register />} />
        </Routes>
      </main>
      <button
        onClick={toggleTheme}
        className="fixed bottom-4 right-4 z-50 p-3 bg-gradient-to-r from-blue-500 to-indigo-500 text-white rounded-full shadow-lg hover:shadow-xl theme-toggle"
        aria-label="Toggle dark mode"
      >
        <i
          className={theme === "light" ? "fa-solid fa-moon" : "fa-solid fa-sun"}
        ></i>
      </button>
      <footer className="bg-gradient-to-r from-blue-600 to-indigo-600 text-white p-6 shadow-lg border-t border-blue-200 dark:bg-gradient-to-r dark:from-gray-800 dark:to-gray-900 dark:border-gray-700">
        <div className="container mx-auto flex flex-col md:flex-row items-center justify-between animate-fade-in-up">
          <p className="bg-clip-text bg-gradient-to-r from-blue-200 to-indigo-200 dark:from-gray-300 dark:to-gray-400">
            Â© 2025 NexusEd. All rights reserved.
          </p>
          <div className="md:-ml-40">
            <p className="text-sm mt-2 md:mt-0 bg-clip-text text-transparent bg-gradient-to-r from-blue-100 to-indigo-100 dark:from-gray-400 dark:to-gray-500">
              Powered by NexusEd
            </p>
          </div>
          <div className="flex space-x-4 mt-4 md:mt-0">
            <a href="#" className="social-icon" aria-label="Facebook">
              <svg
                className="w-6 h-6 fill-current text-white dark:text-gray-300"
                viewBox="0 0 24 24"
              >
                <path d="M12 2.04c-5.5 0-9.96 4.46-9.96 9.96 0 4.99 3.66 9.12 8.44 9.88v-6.98h-2.54v-2.9h2.54v-2.2c0-2.5 1.5-3.88 3.78-3.88 1.1 0 2.24.2 2.24.2v2.46h-1.26c-1.24 0-1.62.77-1.62 1.56v1.86h2.84l-.46 2.9h-2.38v6.98c4.78-.76 8.44-4.89 8.44-9.88 0-5.5-4.46-9.96-9.96-9.96z" />
              </svg>
            </a>
            <a href="#" className="social-icon" aria-label="Twitter">
              <svg
                className="w-6 h-6 fill-current text-white dark:text-gray-300"
                viewBox="0 0 24 24"
              >
                <path d="M23.95 4.57a9.97 9.97 0 0 1-2.83 1.3 4.93 4.93 0 0 0-8.38 4.5 13.98 13.98 0 0 1-10.16-5.15 4.93 4.93 0 0 0 1.52 6.58 4.9 4.9 0 0 1-2.23-.62v.06a4.93 4.93 0 0 0 3.95 4.84 4.9 4.9 0 0 1-2.22.08 4.93 4.93 0 0 0 4.6 3.42 9.87 9.87 0 0 1-6.1 2.1c-.4 0-.8-.02-1.18-.07a13.94 13.94 0 0 0 7.55 2.2c9.05 0 14-7.5 14-14v-.64a9.96 9.96 0 0 0 2.48-2.54 9.87 9.87 0 0 1-2.8.77z" />
              </svg>
            </a>
            <a href="#" className="social-icon" aria-label="Instagram">
              <svg
                className="w-6 h-6 fill-current text-white dark:text-gray-300"
                viewBox="0 0 24 24"
              >
                <path d="M12 2.16c3.21 0 3.58.01 4.85.07 1.17.06 1.81.25 2.23.41.56.22.96.48 1.38.9.42.42.68.82.9 1.38.16.42.35 1.06.41 2.23.06 1.27.07 1.64.07 4.85s-.01 3.58-.07 4.85c-.06 1.17-.25 1.81-.41 2.23-.22.56-.48.96-.9 1.38-.42.42-.82.68-1.38.9-.42.16-1.06.35-2.23.41-1.27.06-1.64.07-4.85.07s-3.58-.01-4.85-.07c-1.17-.06-1.81-.25-2.23-.41-.56-.22-.96-.48-1.38-.9-.42-.42-.68-.82-.9-1.38-.16-.42-.35-1.06-.41-2.23-.06-1.27-.07-1.64-.07-4.85s.01-3.58.07-4.85c.06-1.17.25-1.81.41-2.23.22-.56.48-.96.9-1.38.42-.42.82-.68 1.38-.9.42-.16 1.06-.35 2.23-.41 1.27-.06 1.64-.07 4.85-.07zm0-2.16c-3.25 0-3.66.01-4.94.07-1.29.06-2.17.26-2.94.56-.8.31-1.48.73-2.16 1.41-.68.68-1.1 1.36-1.41 2.16-.3.77-.5 1.65-.56 2.94-.06 1.28-.07 1.69-.07 4.94s.01 3.66.07 4.94c.06 1.29.26 2.17.56 2.94.31.8.73 1.48 1.41 2.16.68.68 1.36 1.1 2.16 1.41.77.3 1.65.5 2.94.56 1.28.06 1.69.07 4.94.07s3.66-.01 4.94-.07c1.29-.06 2.17-.26 2.94-.56.8-.31 1.48-.73 2.16-1.41.68-.68 1.1-1.36 1.41-2.16.3-.77.5-1.65.56-2.94.06-1.28.07-1.69.07-4.94s-.01-3.66-.07-4.94c-.06-1.29-.26-2.17-.56-2.94-.31-.8-.73-1.48-1.41-2.16-.68-.68-1.36-1.1-2.16-1.41-.77-.3-1.65-.5-2.94-.56-1.28-.06-1.69-.07-4.94-.07zM12 5.84c-3.38 0-6.16 2.78-6.16 6.16s2.78 6.16 6.16 6.16 6.16-2.78 6.16-6.16-2.78-6.16-6.16-6.16zm0 10.16c-2.21 0-4-1.79-4-4s1.79-4 4-4 4 1.79 4 4-1.79 4-4 4zm6.4-11.2c0 .8-.65 1.44-1.44 1.44s-1.44-.65-1.44-1.44.65-1.44 1.44-1.44 1.44.65 1.44 1.44z" />
              </svg>
            </a>
          </div>
        </div>
      </footer>
    </div>
  );
}

export default App;
