import React, { useState, useEffect, useRef } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faHeart as faHeartRegular } from "@fortawesome/free-regular-svg-icons";
import {
  faHeart as faHeartSolid,
  faTrash,
} from "@fortawesome/free-solid-svg-icons";

function PostCard({
  title,
  content,
  author,
  id,
  setPosts,
  currentUser,
  comments,
  likes,
  post,
}) {
  const [commentContent, setCommentContent] = useState("");
  const [error, setError] = useState("");
  const [isCommenting, setIsCommenting] = useState(false);
  const timeoutRef = useRef(null);

  const setTemporaryError = (message) => {
    if (timeoutRef.current) {
      clearTimeout(timeoutRef.current);
    }
    setError(message);
    timeoutRef.current = setTimeout(() => {
      setError("");
      timeoutRef.current = null;
    }, 3000);
  };

  useEffect(() => {
    return () => {
      if (timeoutRef.current) {
        clearTimeout(timeoutRef.current);
      }
    };
  }, []);

  const handleDeletePost = async () => {
    try {
      const token = localStorage.getItem("token");
      const response = await fetch(
        `${process.env.REACT_APP_API_URL}/api/posts/${id}`,
        {
          method: "DELETE",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`,
          },
        }
      );
      if (!response.ok) {
        const contentType = response.headers.get("content-type");
        let errorMessage = `Failed to delete post (HTTP ${response.status})`;
        if (contentType && contentType.includes("application/json")) {
          const errorData = await response.json();
          errorMessage = errorData.message || errorMessage;
        } else {
          const errorText = await response.text();
          errorMessage = errorText || errorMessage;
        }
        throw new Error(errorMessage);
      }
      setPosts((prevPosts) => prevPosts.filter((post) => post._id !== id));
      setError("");
    } catch (err) {
      setTemporaryError(err.message || "Failed to delete post");
    }
  };

  const handleDeleteComment = async (commentId) => {
    try {
      const token = localStorage.getItem("token");
      const response = await fetch(
        `${process.env.REACT_APP_API_URL}/api/posts/${id}/comments/${commentId}`,
        {
          method: "DELETE",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`,
          },
        }
      );
      if (!response.ok) {
        const contentType = response.headers.get("content-type");
        let errorMessage = `Failed to delete comment (HTTP ${response.status})`;
        if (contentType && contentType.includes("application/json")) {
          const errorData = await response.json();
          errorMessage = errorData.message || errorMessage;
        } else {
          const errorText = await response.text();
          errorMessage = errorText || errorMessage;
        }
        throw new Error(errorMessage);
      }
      const updatedPost = await response.json();
      setPosts((prevPosts) =>
        prevPosts.map((post) =>
          post._id === id
            ? {
                ...updatedPost,
                author: updatedPost.author?.username
                  ? updatedPost.author
                  : { username: author, _id: post.author?._id },
              }
            : post
        )
      );
      setError("");
    } catch (err) {
      setTemporaryError(err.message || "Failed to delete comment");
    }
  };

  const handleCommentSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setIsCommenting(true);

    if (!commentContent.trim()) {
      setTemporaryError("Comment cannot be empty");
      setIsCommenting(false);
      return;
    }

    try {
      const token = localStorage.getItem("token");
      const response = await fetch(
        `${process.env.REACT_APP_API_URL}/api/posts/${id}/comments`,
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`,
          },
          body: JSON.stringify({ content: commentContent }),
        }
      );

      if (!response.ok) {
        const contentType = response.headers.get("content-type");
        let errorMessage = `Failed to add comment (HTTP ${response.status})`;
        if (contentType && contentType.includes("application/json")) {
          const errorData = await response.json();
          errorMessage = errorData.message || errorMessage;
        } else {
          const errorText = await response.text();
          errorMessage = errorText || errorMessage;
        }
        throw new Error(errorMessage);
      }

      const updatedPost = await response.json();
      setPosts((prevPosts) =>
        prevPosts.map((post) =>
          post._id === id
            ? {
                ...updatedPost,
                author: updatedPost.author?.username
                  ? updatedPost.author
                  : { username: author, _id: post.author?._id },
              }
            : post
        )
      );
      setCommentContent("");
      setError("");
    } catch (err) {
      setTemporaryError(err.message || "Failed to add comment");
    } finally {
      setIsCommenting(false);
    }
  };

  const handleLikePost = async () => {
    try {
      const token = localStorage.getItem("token");
      if (!token || !currentUser) {
        throw new Error("User must be logged in to like a post");
      }

      const response = await fetch(
        `${process.env.REACT_APP_API_URL}/api/posts/${id}/like`,
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`,
          },
        }
      );

      if (!response.ok) {
        const contentType = response.headers.get("content-type");
        let errorMessage = `Failed to like post (HTTP ${response.status})`;
        if (contentType && contentType.includes("application/json")) {
          const errorData = await response.json();
          errorMessage = errorData?.message || errorMessage;
        } else {
          const errorText = await response.text();
          errorMessage = errorText || errorMessage;
        }
        throw new Error(errorMessage);
      }

      const updatedPost = await response.json();
      if (!updatedPost._id) {
        throw new Error("Invalid response: Missing _id");
      }

      setPosts((prevPosts) => {
        const newPosts = prevPosts.map((post) => {
          if (post._id === id) {
            const newAuthor =
              typeof updatedPost.author === "object" &&
              updatedPost.author?.username
                ? updatedPost.author
                : {
                    username: author,
                    _id:
                      typeof updatedPost.author === "string"
                        ? updatedPost.author
                        : post.author?._id,
                  };
            const updated = {
              ...updatedPost,
              author: newAuthor,
              comments: Array.isArray(updatedPost.comments)
                ? updatedPost.comments
                : post.comments || [],
              likes: Array.isArray(updatedPost.likes)
                ? updatedPost.likes
                : post.likes || [],
            };
            return updated;
          }
          return post;
        });
        return newPosts;
      });
      setError("");
    } catch (err) {
      setTemporaryError(err.message || "Failed to like post");
    }
  };

  const handleLikeComment = async (commentId) => {
    try {
      const token = localStorage.getItem("token");
      if (!token || !currentUser) {
        throw new Error("User must be logged in to like a comment");
      }

      const response = await fetch(
        `${process.env.REACT_APP_API_URL}/api/posts/${id}/comments/${commentId}/like`,
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`,
          },
        }
      );

      if (!response.ok) {
        const contentType = response.headers.get("content-type");
        let errorMessage = `Failed to like comment (HTTP ${response.status})`;
        if (contentType && contentType.includes("application/json")) {
          const errorData = await response.json();
          errorMessage = errorData.message || errorMessage;
        } else {
          const errorText = await response.text();
          errorMessage =
            errorText ||
            `Server responded with non-JSON content (HTTP ${response.status})`;
        }
        throw new Error(errorMessage);
      }

      const updatedPost = await response.json();
      setPosts((prevPosts) =>
        prevPosts.map((post) =>
          post._id === id
            ? {
                ...updatedPost,
                author:
                  typeof updatedPost.author === "object" &&
                  updatedPost.author?.username
                    ? updatedPost.author
                    : {
                        username: author,
                        _id:
                          typeof updatedPost.author === "string"
                            ? updatedPost.author
                            : post.author?._id,
                      },
              }
            : post
        )
      );
      setError("");
    } catch (err) {
      setTemporaryError(err.message || "Failed to like comment");
    }
  };

  return (
    <div className="bg-white p-6 rounded-lg shadow-md hover:shadow-lg hover:-translate-y-1 transition duration-200">
      <h2 className="text-xl font-semibold text-gray-800 mb-2">{title}</h2>
      <p className="text-gray-600 mb-3">{content}</p>
      <p className="text-gray-500 text-sm mb-4">Posted by {author}</p>
      <div className="flex items-center space-x-3 mb-6">
        {currentUser && (
          <button
            onClick={handleLikePost}
            className="bg-blue-600 text-white px-3 py-1 rounded-md hover:bg-blue-700 transition duration-200 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2 disabled:opacity-50 text-sm flex items-center space-x-1"
            disabled={!currentUser}
            aria-label={
              likes && currentUser && likes.includes(currentUser.id.toString())
                ? "Unlike Post"
                : "Like Post"
            }
          >
            {likes &&
            currentUser &&
            likes.includes(currentUser.id.toString()) ? (
              <FontAwesomeIcon icon={faHeartSolid} className="text-base" />
            ) : (
              <FontAwesomeIcon icon={faHeartRegular} className="text-base" />
            )}
            <span>({likes ? likes.length : 0})</span>
          </button>
        )}
        {currentUser && post.author?._id?.toString() === currentUser.id && (
          <button
            onClick={handleDeletePost}
            className="bg-red-600 text-white px-3 py-1 rounded-md hover:bg-red-700 transition duration-200 focus:outline-none focus:ring-2 focus:ring-red-500 focus:ring-offset-2 text-sm flex items-center space-x-1"
            aria-label="Delete Post"
          >
            <FontAwesomeIcon icon={faTrash} className="text-base" />
          </button>
        )}
      </div>
      {error && (
        <div className="bg-red-100 text-red-700 p-3 mb-6 rounded-md message">
          {error}
        </div>
      )}
      <div>
        <h4 className="text-lg font-semibold text-gray-800 mb-4">Comments</h4>
        {comments && comments.length > 0 ? (
          comments.map((comment) => (
            <div key={comment._id} className="bg-gray-100 p-4 mb-3 rounded-md">
              <p className="text-gray-600 mb-2">{comment.content}</p>
              <p className="text-gray-500 text-sm mb-2">
                By {comment.author?.username || "Unknown"} on{" "}
                {new Date(comment.createdAt).toLocaleDateString()}
              </p>
              <div className="flex items-center space-x-3">
                {currentUser && (
                  <button
                    onClick={() => handleLikeComment(comment._id)}
                    className="bg-blue-600 text-white px-3 py-1 rounded-md hover:bg-blue-700 transition duration-200 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2 disabled:opacity-50 text-sm flex items-center space-x-1"
                    disabled={!currentUser}
                    aria-label={
                      comment.likes &&
                      currentUser &&
                      comment.likes.includes(currentUser.id.toString())
                        ? "Unlike Comment"
                        : "Like Comment"
                    }
                  >
                    {comment.likes &&
                    currentUser &&
                    comment.likes.includes(currentUser.id.toString()) ? (
                      <FontAwesomeIcon
                        icon={faHeartSolid}
                        className="text-base"
                      />
                    ) : (
                      <FontAwesomeIcon
                        icon={faHeartRegular}
                        className="text-base"
                      />
                    )}
                    <span>({comment.likes ? comment.likes.length : 0})</span>
                  </button>
                )}
                {currentUser &&
                  comment.author?._id?.toString() === currentUser.id && (
                    <button
                      onClick={() => handleLikeComment(comment._id)}
                      className="bg-blue-600 text-white px-3 py-1 rounded-md hover:bg-blue-700 transition duration-200 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2 disabled:opacity-50 text-sm flex items-center space-x-1"
                      disabled={!currentUser}
                      aria-label={
                        comment.likes &&
                        currentUser &&
                        comment.likes.includes(currentUser.id.toString())
                          ? "Unlike Comment"
                          : "Like Comment"
                      }
                    >
                      {comment.likes &&
                      currentUser &&
                      comment.likes.includes(currentUser.id.toString()) ? (
                        <FontAwesomeIcon
                          icon={faHeartSolid}
                          className="text-base"
                        />
                      ) : (
                        <FontAwesomeIcon
                          icon={faHeartRegular}
                          className="text-base"
                        />
                      )}
                      <span>({comment.likes ? comment.likes.length : 0})</span>
                    </button>
                  )}
                {currentUser &&
                  comment.author?._id?.toString() === currentUser.id && (
                    <button
                      onClick={() => handleDeleteComment(comment._id)}
                      className="bg-red-600 text-white px-3 py-1 rounded-md hover:bg-red-700 transition duration-200 focus:outline-none focus:ring-2 focus:ring-red-500 focus:ring-offset-2 text-sm flex items-center space-x-1"
                      aria-label="Delete Comment"
                    >
                      <FontAwesomeIcon icon={faTrash} className="text-base" />
                    </button>
                  )}
              </div>
            </div>
          ))
        ) : (
          <p className="text-gray-600 text-sm mb-4">No comments yet.</p>
        )}
        {currentUser && (
          <form onSubmit={handleCommentSubmit}>
            <textarea
              value={commentContent}
              onChange={(e) => setCommentContent(e.target.value)}
              placeholder="Add a comment"
              className="w-full p-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent transition duration-150 mb-4"
              rows="2"
              disabled={isCommenting}
              aria-label="Comment"
            />
            <button
              type="submit"
              className="bg-blue-600 text-white px-4 py-2 rounded-md hover:bg-blue-700 transition duration-200 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2 disabled:opacity-50"
              disabled={isCommenting}
              aria-label="Submit Comment"
            >
              {isCommenting ? "Commenting..." : "Comment"}
            </button>
          </form>
        )}
      </div>
    </div>
  );
}

export default PostCard;
