import React, { useState, useEffect, useContext } from "react";
import { Calendar, dateFnsLocalizer } from "react-big-calendar";
import { format, parse, startOfWeek, getDay } from "date-fns";
import { AuthContext } from "../context/AuthContext";
import { ToastContainer, toast } from "react-toastify";
import { Navigate } from "react-router-dom"; // Import Navigate for redirection
import "react-big-calendar/lib/css/react-big-calendar.css";

const locales = {
  "en-US": require("date-fns/locale/en-US"),
};
const localizer = dateFnsLocalizer({
  format,
  parse,
  startOfWeek,
  getDay,
  locales,
});

function CalendarPage() {
  const { user, isLoading: authLoading } = useContext(AuthContext); // Get isLoading from AuthContext
  const [events, setEvents] = useState([]);
  const [groups, setGroups] = useState([]);
  const [selectedGroup, setSelectedGroup] = useState("");
  const [showEventForm, setShowEventForm] = useState(false);
  const [newEvent, setNewEvent] = useState({
    title: "",
    start: "",
    end: "",
    description: "",
    groupId: "",
  });
  const [error, setError] = useState("");
  const [isLoading, setIsLoading] = useState(true);

  // Fetch user's groups and events on mount
  useEffect(() => {
    if (authLoading || !user) return; // Wait until authLoading is false and user exists

    const fetchData = async () => {
      try {
        // Fetch user's groups
        const groupsResponse = await fetch(
          `${process.env.REACT_APP_API_URL}/api/study-groups`,
          {
            headers: {
              Authorization: `Bearer ${localStorage.getItem("token")}`,
            },
          }
        );
        if (!groupsResponse.ok) throw new Error("Failed to fetch groups");
        const groupsData = await groupsResponse.json();
        const userGroups = groupsData.filter((group) =>
          group.members.some((member) => member._id === user.id)
        );
        setGroups(userGroups);

        // Fetch events
        const eventsResponse = await fetch(
          `${process.env.REACT_APP_API_URL}/api/events`,
          {
            headers: {
              Authorization: `Bearer ${localStorage.getItem("token")}`,
            },
          }
        );
        if (!eventsResponse.ok) throw new Error("Failed to fetch events");
        const eventsData = await eventsResponse.json();
        const formattedEvents = eventsData.map((event) => ({
          ...event,
          start: new Date(event.start),
          end: new Date(event.end),
        }));
        setEvents(formattedEvents);

        // Show reminders for upcoming events (within the next 24 hours)
        const now = new Date();
        const upcomingEvents = formattedEvents.filter((event) => {
          const timeDiff = event.start - now;
          return timeDiff > 0 && timeDiff <= 24 * 60 * 60 * 1000; // Within 24 hours
        });
        upcomingEvents.forEach((event) => {
          toast.info(
            `Reminder: "${event.title}" starts at ${format(
              event.start,
              "h:mm a"
            )} on ${format(event.start, "MMM d, yyyy")} for group ${
              groups.find((g) => g._id === event.groupId)?.name
            }!`,
            {
              position: "top-right",
              autoClose: 5000,
            }
          );
        });
      } catch (err) {
        setError(err.message || "Failed to load data");
      } finally {
        setIsLoading(false);
      }
    };
    fetchData();
  }, [user, authLoading]); // Depend on user and authLoading

  // Handle event creation
  const handleCreateEvent = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem("token");
      if (!token) throw new Error("Authentication token missing");

      const response = await fetch(
        `${process.env.REACT_APP_API_URL}/api/events`,
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`,
          },
          body: JSON.stringify({
            ...newEvent,
            start: new Date(newEvent.start),
            end: new Date(newEvent.end),
          }),
        }
      );
      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || "Failed to create event");
      }
      const createdEvent = await response.json();
      setEvents([
        ...events,
        {
          ...createdEvent,
          start: new Date(createdEvent.start),
          end: new Date(createdEvent.end),
        },
      ]);
      setNewEvent({
        title: "",
        start: "",
        end: "",
        description: "",
        groupId: "",
      });
      setShowEventForm(false);
      toast.success("Event created successfully!", {
        position: "top-right",
        autoClose: 3000,
      });
    } catch (err) {
      setError(err.message || "Failed to create event");
    }
  };

  // Filter events by selected group
  const filteredEvents = selectedGroup
    ? events.filter((event) => event.groupId === selectedGroup)
    : events;

  // Show loading while AuthContext is loading
  if (authLoading) {
    return (
      <div className="bg-gradient-to-br from-blue-50 to-indigo-50 dark:from-gray-800 dark:to-gray-900 p-6 rounded-xl shadow-xl border border-blue-100 dark:border-gray-600 hover:shadow-2xl dark:hover:shadow-[0_0_15px_rgba(209,213,219,0.3)] hover:-translate-y-2 transition-all duration-300 animate-fade-in-up flex flex-col items-center justify-center">
        <div className="inline-block animate-spin rounded-full h-10 w-10 border-t-4 border-b-4 border-gradient-to-r from-blue-600 to-indigo-600 dark:from-gray-500 dark:to-gray-400"></div>
        <p className="text-gray-600 dark:text-gray-400 mt-3 text-lg animate-pulse">
          Loading user data...
        </p>
      </div>
    );
  }

  // Redirect to login if user is not logged in
  if (!user) {
    return <Navigate to="/login" replace />;
  }

  // Show loading while fetching groups and events
  if (isLoading) {
    return (
      <div className="bg-gradient-to-br from-blue-50 to-indigo-50 dark:from-gray-800 dark:to-gray-900 p-6 rounded-xl shadow-xl border border-blue-100 dark:border-gray-600 hover:shadow-2xl dark:hover:shadow-[0_0_15px_rgba(209,213,219,0.3)] hover:-translate-y-2 transition-all duration-300 animate-fade-in-up flex flex-col items-center justify-center">
        <div className="inline-block animate-spin rounded-full h-10 w-10 border-t-4 border-b-4 border-gradient-to-r from-blue-600 to-indigo-600 dark:from-gray-500 dark:to-gray-400"></div>
        <p className="text-gray-600 dark:text-gray-400 mt-3 text-lg animate-pulse">
          Loading calendar...
        </p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="bg-red-100 dark:bg-red-900/70 text-red-700 dark:text-red-300 p-4 mt-10 rounded-lg border border-gradient-to-r from-red-200 to-red-300 dark:from-red-800 dark:to-red-700 shadow-md hover:shadow-[0_0_10px_rgba(239,68,68,0.5)] dark:hover:shadow-[0_0_10px_rgba(209,213,219,0.5)] transition-all duration-300 animate-fade-in-up">
        {error}
      </div>
    );
  }

  return (
    <section className="min-h-screen bg-gradient-to-br from-gray-50 to-blue-50 dark:from-gray-800 dark:to-gray-900 py-10">
      <ToastContainer />
      <div className="container mx-auto px-4">
        <div className="max-w-5xl mx-auto">
          <h1 className="relative text-3xl font-bold bg-clip-text text-transparent bg-gradient-to-r from-blue-600 to-indigo-600 dark:from-amber-200 dark:to-amber-100 mb-6 hover-underline">
            Calendar & Events
          </h1>

          {/* Event Creation Button */}
          <div className="mb-6 flex justify-between items-center">
            <button
              onClick={() => setShowEventForm(true)}
              className="bg-gradient-to-r from-blue-600 to-indigo-600 dark:from-cyan-600 dark:to-teal-600 text-white dark:text-gray-200 px-5 py-3 rounded-lg hover:shadow-[0_0_10px_rgba(59,130,246,0.5)] dark:hover:shadow-[0_0_10px_rgba(21,94,117,0.5)] dark:hover:dark-glow hover:scale-105 transition-all duration-300 focus:outline-none focus:ring-2 focus:ring-blue-500 dark:focus:ring-cyan-500 focus:ring-offset-2 dark:focus:ring-offset-gray-800 flex items-center space-x-2"
              aria-label="Create New Event"
            >
              <i className="fa-solid fa-plus"></i>
              <span>Create Event</span>
            </button>

            {/* Group Filter */}
            <div className="flex items-center space-x-3">
              <label
                htmlFor="group-filter"
                className="text-gray-700 dark:text-gray-200 font-medium"
              >
                Filter by Group:
              </label>
              <select
                id="group-filter"
                value={selectedGroup}
                onChange={(e) => setSelectedGroup(e.target.value)}
                className="p-2 bg-gray-50 dark:bg-gray-700 border border-gray-200 dark:border-gray-600 rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-gradient-to-r from-blue-500 to-indigo-500 dark:from-gray-500 dark:to-gray-400 focus:scale-[1.01] transition-all duration-300 text-gray-800 dark:text-gray-200"
                aria-label="Filter events by group"
              >
                <option value="">All Groups</option>
                {groups.map((group) => (
                  <option key={group._id} value={group._id}>
                    {group.name}
                  </option>
                ))}
              </select>
            </div>
          </div>

          {/* Event Creation Form */}
          {showEventForm && (
            <div className="mb-6 bg-gradient-to-br from-blue-50 to-indigo-50 dark:from-gray-800 dark:to-gray-900 p-6 rounded-xl shadow-xl border border-blue-100 dark:border-gray-600 hover:shadow-2xl dark:hover:shadow-[0_0_15px_rgba(209,213,219,0.3)] transition-all duration-300 animate-fade-in-up">
              <h2 className="relative text-xl font-bold bg-clip-text text-transparent bg-gradient-to-r from-blue-600 to-indigo-600 dark:from-amber-200 dark:to-amber-100 mb-4 hover-underline">
                Create New Event
              </h2>
              <form onSubmit={handleCreateEvent}>
                <div className="mb-4">
                  <label
                    htmlFor="event-title"
                    className="block text-gray-700 dark:text-gray-200 mb-2 font-medium"
                  >
                    Event Title
                  </label>
                  <input
                    type="text"
                    id="event-title"
                    value={newEvent.title}
                    onChange={(e) =>
                      setNewEvent({ ...newEvent, title: e.target.value })
                    }
                    required
                    className="w-full p-3 bg-gray-50 dark:bg-gray-700 border border-gray-200 dark:border-gray-600 rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-gradient-to-r from-blue-500 to-indigo-500 dark:from-gray-500 dark:to-gray-400 focus:scale-[1.01] transition-all duration-300 text-gray-800 dark:text-gray-200 placeholder-gray-500 dark:placeholder-gray-400"
                    placeholder="Enter event title"
                    aria-label="Event Title"
                  />
                </div>
                <div className="mb-4">
                  <label
                    htmlFor="event-start"
                    className="block text-gray-700 dark:text-gray-200 mb-2 font-medium"
                  >
                    Start Date & Time
                  </label>
                  <input
                    type="datetime-local"
                    id="event-start"
                    value={newEvent.start}
                    onChange={(e) =>
                      setNewEvent({ ...newEvent, start: e.target.value })
                    }
                    required
                    className="w-full p-3 bg-gray-50 dark:bg-gray-700 border border-gray-200 dark:border-gray-600 rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-gradient-to-r from-blue-500 to-indigo-500 dark:from-gray-500 dark:to-gray-400 focus:scale-[1.01] transition-all duration-300 text-gray-800 dark:text-gray-200"
                    aria-label="Event Start Date and Time"
                  />
                </div>
                <div className="mb-4">
                  <label
                    htmlFor="event-end"
                    className="block text-gray-700 dark:text-gray-200 mb-2 font-medium"
                  >
                    End Date & Time
                  </label>
                  <input
                    type="datetime-local"
                    id="event-end"
                    value={newEvent.end}
                    onChange={(e) =>
                      setNewEvent({ ...newEvent, end: e.target.value })
                    }
                    required
                    className="w-full p-3 bg-gray-50 dark:bg-gray-700 border border-gray-200 dark:border-gray-600 rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-gradient-to-r from-blue-500 to-indigo-500 dark:from-gray-500 dark:to-gray-400 focus:scale-[1.01] transition-all duration-300 text-gray-800 dark:text-gray-200"
                    aria-label="Event End Date and Time"
                  />
                </div>
                <div className="mb-4">
                  <label
                    htmlFor="event-description"
                    className="block text-gray-700 dark:text-gray-200 mb-2 font-medium"
                  >
                    Description
                  </label>
                  <textarea
                    id="event-description"
                    value={newEvent.description}
                    onChange={(e) =>
                      setNewEvent({ ...newEvent, description: e.target.value })
                    }
                    className="w-full p-3 bg-gray-50 dark:bg-gray-700 border border-gray-200 dark:border-gray-600 rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-gradient-to-r from-blue-500 to-indigo-500 dark:from-gray-500 dark:to-gray-400 focus:scale-[1.01] transition-all duration-300 text-gray-800 dark:text-gray-200 placeholder-gray-500 dark:placeholder-gray-400"
                    rows="3"
                    placeholder="Describe the event"
                    aria-label="Event Description"
                  />
                </div>
                <div className="mb-4">
                  <label
                    htmlFor="event-group"
                    className="block text-gray-700 dark:text-gray-200 mb-2 font-medium"
                  >
                    Associated Group
                  </label>
                  <select
                    id="event-group"
                    value={newEvent.groupId}
                    onChange={(e) =>
                      setNewEvent({ ...newEvent, groupId: e.target.value })
                    }
                    required
                    className="w-full p-3 bg-gray-50 dark:bg-gray-700 border border-gray-200 dark:border-gray-600 rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-gradient-to-r from-blue-500 to-indigo-500 dark:from-gray-500 dark:to-gray-400 focus:scale-[1.01] transition-all duration-300 text-gray-800 dark:text-gray-200"
                    aria-label="Select Associated Group"
                  >
                    <option value="">Select a group</option>
                    {groups.map((group) => (
                      <option key={group._id} value={group._id}>
                        {group.name}
                      </option>
                    ))}
                  </select>
                </div>
                <div className="flex space-x-3">
                  <button
                    type="submit"
                    className="bg-gradient-to-r from-blue-600 to-indigo-600 dark:from-cyan-600 dark:to-teal-600 text-white dark:text-gray-200 px-5 py-3 rounded-lg hover:shadow-[0_0_10px_rgba(59,130,246,0.5)] dark:hover:shadow-[0_0_10px_rgba(21,94,117,0.5)] dark:hover:dark-glow hover:scale-105 transition-all duration-300 focus:outline-none focus:ring-2 focus:ring-blue-500 dark:focus:ring-cyan-500 focus:ring-offset-2 dark:focus:ring-offset-gray-800"
                    aria-label="Save Event"
                  >
                    Save Event
                  </button>
                  <button
                    type="button"
                    onClick={() => setShowEventForm(false)}
                    className="bg-gradient-to-r from-red-500 to-red-600 dark:from-red-600 dark:to-red-700 text-white dark:text-gray-200 px-5 py-3 rounded-lg hover:shadow-[0_0_10px_rgba(239,68,68,0.5)] dark:hover:shadow-[0_0_10px_rgba(220,38,38,0.5)] dark:hover:dark-glow hover:scale-105 transition-all duration-300 focus:outline-none focus:ring-2 focus:ring-red-500 dark:focus:ring-red-600 focus:ring-offset-2 dark:focus:ring-offset-gray-800"
                    aria-label="Cancel Event Creation"
                  >
                    Cancel
                  </button>
                </div>
              </form>
            </div>
          )}

          {/* Calendar View */}
          <div className="bg-gradient-to-br from-blue-50 to-indigo-50 dark:from-gray-800 dark:to-gray-900 p-6 rounded-xl shadow-xl border border-blue-100 dark:border-gray-600 hover:shadow-2xl dark:hover:shadow-[0_0_15px_rgba(209,213,219,0.3)] transition-all duration-300 animate-fade-in-up">
            <Calendar
              localizer={localizer}
              events={filteredEvents}
              startAccessor="start"
              endAccessor="end"
              style={{ height: 500 }}
              className="bg-transparent text-gray-800 dark:text-gray-200"
              eventPropGetter={(event) => ({
                style: {
                  backgroundColor: "#3b82f6", // Tailwind's blue-500
                  color: "white",
                  borderRadius: "5px",
                  border: "none",
                },
              })}
              onSelectEvent={(event) =>
                alert(
                  `Event: ${event.title}\nDescription: ${
                    event.description
                  }\nGroup: ${
                    groups.find((g) => g._id === event.groupId)?.name
                  }\nTime: ${format(event.start, "h:mm a")} - ${format(
                    event.end,
                    "h:mm a"
                  )} on ${format(event.start, "MMM d, yyyy")}`
                )
              }
            />
          </div>
        </div>
      </div>
    </section>
  );
}

export default CalendarPage;
