import React, { useState, useRef } from "react";

function GroupChat({ groupId, messages, setGroup, currentUser }) {
  const [messageContent, setMessageContent] = useState("");
  const [error, setError] = useState("");
  const [isPosting, setIsPosting] = useState(false);
  const timeoutRef = useRef(null);
  const messageInputRef = useRef(null);

  const setTemporaryError = (message) => {
    if (timeoutRef.current) {
      clearTimeout(timeoutRef.current);
    }
    setError(message);
    timeoutRef.current = setTimeout(() => {
      setError("");
      timeoutRef.current = null;
    }, 3000);
  };

  const handleMessageSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setIsPosting(true);

    if (!messageContent.trim()) {
      setTemporaryError("Message cannot be empty");
      setIsPosting(false);
      return;
    }

    try {
      const token = localStorage.getItem("token");
      const response = await fetch(
        `${process.env.REACT_APP_API_URL}/api/study-groups/${groupId}/messages`,
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`,
          },
          body: JSON.stringify({ content: messageContent }),
        }
      );

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || "Failed to post message");
      }

      const updatedGroup = await response.json();
      setGroup(updatedGroup);
      setMessageContent("");
    } catch (err) {
      setTemporaryError(err.message || "Failed to post message");
    } finally {
      setIsPosting(false);
    }
  };

  const handleDeleteMessage = async (messageId) => {
    try {
      const token = localStorage.getItem("token");
      const response = await fetch(
        `${process.env.REACT_APP_API_URL}/api/study-groups/${groupId}/messages/${messageId}`,
        {
          method: "DELETE",
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || "Failed to delete message");
      }

      const updatedGroup = await response.json();
      setGroup(updatedGroup);
    } catch (err) {
      setTemporaryError(err.message || "Failed to delete message");
    }
  };

  const handleMessageKeyDown = (e) => {
    if (e.key === "Enter") {
      if (e.shiftKey) {
        e.preventDefault();
        setMessageContent(messageContent + "\n");
      } else {
        e.preventDefault();
        handleMessageSubmit(e);
      }
    }
  };

  const formatDateTime = (date) => {
    const formatter = new Intl.DateTimeFormat("en-US", {
      month: "numeric",
      day: "numeric",
      year: "numeric",
      hour: "numeric",
      minute: "numeric",
      hour12: true,
      timeZone: "Asia/Kolkata",
    });
    return formatter.format(new Date(date)).replace(" at", ",");
  };

  return (
    <div className="mt-6">
      <h4 className="relative text-lg font-semibold bg-clip-text text-transparent bg-gradient-to-r from-blue-600 to-indigo-600 dark:from-amber-400 dark:to-amber-300 mb-4 hover-underline">
        Discussion Board
      </h4>
      {error && (
        <div className="bg-red-100 dark:bg-red-900/70 text-red-700 dark:text-red-300 p-4 mb-6 rounded-lg border border-gradient-to-r from-red-200 to-red-300 dark:from-red-800 dark:to-red-700 shadow-md animate-fade-in-up hover:shadow-[0_0_10px_rgba(239,68,68,0.5)] dark:hover:shadow-[0_0_10px_rgba(45,212,191,0.5)] transition-all duration-300">
          {error}
        </div>
      )}
      {messages && messages.length > 0 ? (
        <div className="space-y-4 mb-6">
          {messages.map((message, index) => (
            <div
              key={message._id}
              className="bg-gradient-to-r from-gray-50 to-blue-50 dark:from-indigo-700 dark:to-indigo-600 p-4 rounded-lg shadow-sm border border-gray-200 dark:border-teal-500 animate-fade-in-up"
              style={{ animationDelay: `${index * 0.1}s` }}
            >
              <p className="text-gray-600 dark:text-gray-200 mb-2 transition-all duration-200">
                {message.content}
              </p>
              <p className="text-sm mb-2 bg-clip-text text-transparent bg-gradient-to-r from-gray-500 to-gray-700 dark:from-teal-400 dark:to-teal-300 transition-all duration-200">
                By {message.author?.username || "Unknown"} on{" "}
                {formatDateTime(message.createdAt)}
              </p>
              {currentUser &&
                message.author?._id?.toString() === currentUser.id && (
                  <button
                    onClick={() => handleDeleteMessage(message._id)}
                    className="bg-gradient-to-r from-red-600 to-red-800 dark:from-red-700 dark:to-red-600 text-white dark:text-gray-100 px-3 py-1 rounded-md hover:shadow-[0_0_10px_rgba(239,68,68,0.5)] dark:hover:shadow-[0_0_10px_rgba(45,212,191,0.5)] dark:hover:dark-glow hover:scale-105 transition-all duration-300 focus:outline-none focus:ring-2 focus:ring-red-500 dark:focus:ring-red-400 focus:ring-offset-2 dark:focus:ring-offset-indigo-800 text-sm flex items-center space-x-1"
                    aria-label="Delete Message"
                  >
                    <i className="fa-solid fa-trash"></i>
                  </button>
                )}
            </div>
          ))}
        </div>
      ) : (
        <p className="text-sm mb-4 bg-clip-text text-transparent bg-gradient-to-r from-blue-600 to-indigo-600 dark:from-amber-400 dark:to-amber-300 transition-transform duration-200">
          No messages yet.
        </p>
      )}
      {currentUser && (
        <form onSubmit={handleMessageSubmit}>
          <textarea
            value={messageContent}
            onChange={(e) => setMessageContent(e.target.value)}
            onKeyDown={handleMessageKeyDown}
            placeholder="Post a message..."
            className="w-full p-3 bg-gray-50 dark:bg-indigo-700 border border-gray-200 dark:border-teal-500 rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-gradient-to-r from-blue-500 to-indigo-500 dark:from-teal-500 dark:to-teal-400 focus:scale-[1.01] transition-all duration-200 mb-4 text-gray-900 dark:text-gray-100 placeholder-gray-500 dark:placeholder-gray-400"
            rows="2"
            ref={messageInputRef}
            disabled={isPosting}
            aria-label="Post a Message"
          />
          <button
            type="submit"
            className="bg-gradient-to-r from-blue-600 to-indigo-600 dark:from-teal-600 dark:to-teal-400 text-white dark:text-gray-100 px-5 py-3 rounded-lg hover:shadow-[0_0_10px_rgba(59,130,246,0.5)] dark:hover:shadow-[0_0_10px_rgba(45,212,191,0.5)] dark:hover:dark-glow hover:scale-105 transition-all duration-300 focus:outline-none focus:ring-2 focus:ring-blue-500 dark:focus:ring-teal-400 focus:ring-offset-2 dark:focus:ring-offset-indigo-800 disabled:opacity-50"
            disabled={isPosting}
            aria-label="Post Message"
          >
            {isPosting ? "Posting..." : "Post Message"}
          </button>
        </form>
      )}
    </div>
  );
}

export default GroupChat;
