import React, { useState, useEffect, useContext, useRef } from "react";
import PostForm from "./PostForm";
import PostCard from "./PostCard";
import { AuthContext } from "../context/AuthContext";

function Home() {
  const { user } = useContext(AuthContext);
  const [posts, setPosts] = useState([]);
  const [studyGroups, setStudyGroups] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [isGroupsLoading, setIsGroupsLoading] = useState(true);
  const [error, setError] = useState("");
  const [modalError, setModalError] = useState("");
  const [fadeIn, setFadeIn] = useState(false);
  const [showModal, setShowModal] = useState(false);
  const [groupName, setGroupName] = useState("");
  const [groupDescription, setGroupDescription] = useState("");
  const timeoutRef = useRef(null);
  const prevUserRef = useRef(user);

  const setTemporaryError = (message, isModal = false) => {
    if (timeoutRef.current) {
      clearTimeout(timeoutRef.current);
    }
    if (isModal) {
      setModalError(message);
    } else {
      setError(message);
    }
    timeoutRef.current = setTimeout(() => {
      if (isModal) {
        setModalError("");
      } else {
        setError("");
      }
      timeoutRef.current = null;
    }, 3000);
  };

  // Fetch posts
  useEffect(() => {
    const fetchPosts = async () => {
      try {
        setIsLoading(true);
        const response = await fetch(
          `${process.env.REACT_APP_API_URL}/api/posts`
        );
        if (!response.ok) {
          throw new Error(`Failed to fetch posts: ${response.status}`);
        }
        const data = await response.json();
        setPosts(data);
      } catch (err) {
        setTemporaryError("Failed to load posts. Please try again.");
      } finally {
        setIsLoading(false);
      }
    };
    fetchPosts();

    return () => {
      if (timeoutRef.current) {
        clearTimeout(timeoutRef.current);
      }
    };
  }, []);

  // Fetch study groups
  const fetchStudyGroups = async () => {
    try {
      setIsGroupsLoading(true);
      const response = await fetch(
        `${process.env.REACT_APP_API_URL}/api/study-groups`
      );
      if (!response.ok) {
        throw new Error(`Failed to fetch study groups: ${response.status}`);
      }
      const data = await response.json();
      setStudyGroups(data);
    } catch (err) {
      setTemporaryError("Failed to load study groups. Please try again.");
    } finally {
      setIsGroupsLoading(false);
    }
  };

  useEffect(() => {
    fetchStudyGroups();
  }, []);

  // Detect user state change for fade-in on logout
  useEffect(() => {
    if (prevUserRef.current && !user) {
      setFadeIn(true);
    }
    prevUserRef.current = user;
  }, [user]);

  // Create a study group
  const handleCreateGroup = async (e) => {
    e.preventDefault();
    if (!groupName || !groupDescription) {
      setTemporaryError("Group name and description are required.", true);
      return;
    }

    try {
      const response = await fetch(
        `${process.env.REACT_APP_API_URL}/api/study-groups/create`,
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${localStorage.getItem("token")}`,
          },
          body: JSON.stringify({
            name: groupName,
            description: groupDescription,
          }),
        }
      );

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(
          errorData.message ||
            `Failed to create study group: ${response.status}`
        );
      }

      await fetchStudyGroups(); // Refresh study groups
      setShowModal(false);
      setGroupName("");
      setGroupDescription("");
    } catch (err) {
      setTemporaryError(err.message || "Failed to create study group.", true);
    }
  };

  // Join a study group
  const handleJoinGroup = async (groupId) => {
    if (!user) {
      setTemporaryError("Please log in to join a study group.");
      return;
    }

    try {
      const response = await fetch(
        `${process.env.REACT_APP_API_URL}/api/study-groups/join/${groupId}`,
        {
          method: "POST",
          headers: {
            Authorization: `Bearer ${localStorage.getItem("token")}`,
          },
        }
      );

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(
          errorData.message || `Failed to join study group: ${response.status}`
        );
      }

      await fetchStudyGroups(); // Refresh study groups to update members
    } catch (err) {
      setTemporaryError(err.message || "Failed to join study group.");
    }
  };

  return (
    <section
      className="min-h-screen bg-gradient-to-br from-gray-50 to-blue-50 dark:from-indigo-900 dark:to-indigo-800 py-10"
      aria-label="Home Feed"
    >
      <style>
        {`
          @keyframes fadeInUp {
            0% { opacity: 0; transform: translateY(20px); }
            100% { opacity: 1; transform: translateY(0); }
          }
          @keyframes fadeIn {
            0% { opacity: 0; }
            100% { opacity: 1; }
          }
          @keyframes pulse {
            0% { opacity: 0.6; }
            50% { opacity: 1; }
            100% { opacity: 0.6; }
          }
          @keyframes glow {
            0% { box-shadow: 0 0 5px rgba(45, 212, 191, 0.3); }
            50% { box-shadow: 0 0 15px rgba(45, 212, 191, 0.5); }
            100% { box-shadow: 0 0 5px rgba(45, 212, 191, 0.3); }
          }
          .animate-fade-in-up {
            animation: fadeInUp 0.5s ease-out forwards;
          }
          .animate-fade-in {
            animation: fadeIn 0.5s ease-in forwards;
          }
          .animate-pulse-text {
            animation: pulse 1.5s infinite;
          }
          .dark-glow {
            animation: glow 2s infinite ease-in-out;
          }
        `}
      </style>
      <div
        key={user ? "logged-in" : "logged-out"}
        className={`container px-4 ${
          fadeIn ? "animate-fade-in" : "animate-fade-in-up"
        }`}
      >
        {/* Study Groups Section */}
        <div className="mb-8">
          <div className="flex justify-between items-center mb-4">
            <h2 className="text-2xl font-bold text-gray-800 dark:text-teal-300">
              Study Groups
            </h2>
            {user && (
              <button
                onClick={() => setShowModal(true)}
                className="bg-teal-500 text-white px-4 py-2 rounded-lg hover:bg-teal-600 transition-all duration-300"
              >
                Create Study Group
              </button>
            )}
          </div>
          {isGroupsLoading ? (
            <div className="bg-white dark:bg-indigo-800 p-6 rounded-xl shadow-lg flex flex-col items-center justify-center border border-gray-200 dark:border-teal-600 hover:shadow-2xl dark:hover:shadow-[0_0_15px_rgba(45,212,191,0.3)] transition-all duration-300 animate-fade-in-up">
              <div className="inline-block animate-spin rounded-full h-10 w-10 border-t-4 border-b-4 border-gradient-to-r from-blue-600 to-indigo-600 dark:from-teal-500 dark:to-teal-400"></div>
              <p className="text-gray-600 dark:text-teal-400 mt-3 text-lg animate-pulse-text">
                Loading study groups...
              </p>
            </div>
          ) : studyGroups.length > 0 ? (
            <div className="grid gap-4">
              {studyGroups.map((group) => (
                <div
                  key={group._id}
                  className="bg-white dark:bg-indigo-800 p-4 rounded-xl shadow-lg border border-gray-200 dark:border-teal-600 hover:shadow-2xl dark:hover:shadow-[0_0_15px_rgba(45,212,191,0.3)] transition-all duration-300 animate-fade-in-up"
                >
                  <h3 className="text-xl font-semibold text-gray-800 dark:text-teal-300">
                    {group.name}
                  </h3>
                  <p className="text-gray-600 dark:text-gray-300 mt-1">
                    {group.description}
                  </p>
                  <p className="text-sm text-gray-500 dark:text-gray-400 mt-2">
                    Created by: {group.creator?.username || "Unknown"}
                  </p>
                  <p className="text-sm text-gray-500 dark:text-gray-400">
                    Members: {group.members.length}
                  </p>
                  <button
                    onClick={() => handleJoinGroup(group._id)}
                    className="mt-3 bg-blue-500 text-white px-3 py-1 rounded-lg hover:bg-blue-600 transition-all duration-300 disabled:bg-gray-500 disabled:cursor-not-allowed"
                    disabled={group.members.some(
                      (member) => member._id === user?._id
                    )}
                  >
                    {group.members.some((member) => member._id === user?._id)
                      ? "Joined"
                      : "Join Group"}
                  </button>
                </div>
              ))}
            </div>
          ) : (
            <p className="text-center text-lg text-gray-600 dark:text-teal-400 animate-fade-in-up">
              No study groups available. Create one to get started!
            </p>
          )}
        </div>

        {/* Create Study Group Modal */}
        {showModal && (
          <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
            <div className="bg-white dark:bg-indigo-800 p-6 rounded-xl shadow-lg w-full max-w-md border border-gray-200 dark:border-teal-600 animate-fade-in-up">
              <h3 className="text-xl font-bold text-gray-800 dark:text-teal-300 mb-4">
                Create a Study Group
              </h3>
              <form onSubmit={handleCreateGroup}>
                <div className="mb-4">
                  <label className="block text-gray-700 dark:text-gray-300 mb-1">
                    Group Name
                  </label>
                  <input
                    type="text"
                    value={groupName}
                    onChange={(e) => setGroupName(e.target.value)}
                    className="w-full p-2 rounded-lg border border-gray-300 dark:border-teal-600 bg-gray-100 dark:bg-indigo-900 text-gray-800 dark:text-gray-200 focus:outline-none focus:ring-2 focus:ring-teal-500"
                    placeholder="Enter group name"
                    required
                  />
                </div>
                <div className="mb-4">
                  <label className="block text-gray-700 dark:text-gray-300 mb-1">
                    Description
                  </label>
                  <textarea
                    value={groupDescription}
                    onChange={(e) => setGroupDescription(e.target.value)}
                    className="w-full p-2 rounded-lg border border-gray-300 dark:border-teal-600 bg-gray-100 dark:bg-indigo-900 text-gray-800 dark:text-gray-200 focus:outline-none focus:ring-2 focus:ring-teal-500"
                    placeholder="Describe the study group"
                    rows="4"
                    required
                  />
                </div>
                {modalError && (
                  <div className="bg-red-100 dark:bg-red-900/70 text-red-700 dark:text-red-300 p-3 mb-4 rounded-md flex items-center space-x-2 shadow-md">
                    <i className="fa-solid fa-exclamation-circle"></i>
                    <span>{modalError}</span>
                  </div>
                )}
                <div className="flex justify-end space-x-3">
                  <button
                    type="button"
                    onClick={() => {
                      setShowModal(false);
                      setModalError("");
                    }}
                    className="bg-gray-500 text-white px-4 py-2 rounded-lg hover:bg-gray-600 transition-all duration-300"
                  >
                    Cancel
                  </button>
                  <button
                    type="submit"
                    className="bg-teal-500 text-white px-4 py-2 rounded-lg hover:bg-teal-600 transition-all duration-300"
                  >
                    Create
                  </button>
                </div>
              </form>
            </div>
          </div>
        )}

        {/* Existing Posts Section */}
        {user && (
          <div className="mb-8 bg-gradient-to-br from-blue-50 to-indigo-50 dark:from-indigo-800 dark:to-teal-800 p-6 rounded-xl shadow-xl border border-blue-100 dark:border-teal-600 hover:shadow-2xl dark:hover:shadow-[0_0_15px_rgba(45,212,191,0.3)] transition-all duration-300">
            <PostForm setPosts={setPosts} />
          </div>
        )}
        {error && (
          <div className="bg-red-100 dark:bg-red-900/70 text-red-700 dark:text-red-300 p-4 mb-8 rounded-lg border border-gradient-to-r from-red-200 to-red-300 dark:from-red-800 dark:to-red-700 shadow-md hover:shadow-2xl dark:hover:shadow-[0_0_15px_rgba(45,212,191,0.3)] transition-all duration-300 animate-fade-in-up">
            {error}
          </div>
        )}
        {isLoading ? (
          <div className="bg-white dark:bg-indigo-800 p-6 rounded-xl shadow-lg flex flex-col items-center justify-center border border-gray-200 dark:border-teal-600 hover:shadow-2xl dark:hover:shadow-[0_0_15px_rgba(45,212,191,0.3)] transition-all duration-300 animate-fade-in-up">
            <div className="inline-block animate-spin rounded-full h-10 w-10 border-t-4 border-b-4 border-gradient-to-r from-blue-600 to-indigo-600 dark:from-teal-500 dark:to-teal-400"></div>
            <p className="text-gray-600 dark:text-teal-400 mt-3 text-lg animate-pulse-text">
              Loading posts...
            </p>
          </div>
        ) : posts.length > 0 ? (
          <div className="bg-white dark:bg-indigo-800 p-6 rounded-xl shadow-lg space-y-8 border border-gray-200 dark:border-teal-600 hover:shadow-2xl dark:hover:shadow-[0_0_15px_rgba(45,212,191,0.3)] transition-all duration-300 animate-fade-in-up">
            {posts.map((post, index) => (
              <div
                key={post._id}
                className="animate-fade-in-up"
                style={{ animationDelay: `${index * 0.1}s` }}
              >
                <PostCard
                  id={post._id}
                  title={post.title}
                  content={post.content}
                  author={post.author?.username || "Unknown"}
                  comments={post.comments}
                  likes={post.likes}
                  post={post}
                  setPosts={setPosts}
                  currentUser={user}
                />
              </div>
            ))}
          </div>
        ) : (
          <p className="text-center text-lg bg-clip-text text-transparent bg-gradient-to-r from-blue-600 to-indigo-600 dark:from-amber-400 dark:to-amber-300 transition-transform duration-300 hover:scale-105 animate-fade-in-up">
            No posts available.
          </p>
        )}
      </div>
    </section>
  );
}

export default Home;
